public static void jumpRefMethod(str _menuItemName, Common _stagingBuffer, Object _callerObj, DataAreaId _dataAreaId)
{
    changeCompany(_dataAreaId)
    {
        PurchPurchaseAgreementConfirmationHeaderStaging stagingRecord = _stagingBuffer; 
        var record = PurchAgreementHeaderHistory::findAgreementId(stagingRecord.PurchaseAgreementId, stagingRecord.ConfirmationNumber);

        Args args = new Args();
        args.record(record);
        args.lookupRecord(record);

        MenuFunction::runCalled(_menuItemName, MenuItemType::Display, false, args);
    }
}

/// <summary>
/// Determines the <c>RecId</c> of the party which will hold non agreement-specific ad-hoc delivery addresses.
/// </summary>
/// <returns>The <c>RecId</c> of the party.</returns>
// This method implements the interface defined by <c>TradeEntitySpecificDeliveryAddress</c>
public RecId deliveryAddressPartyRecId()
{
    return CompanyInfo::current();
}

private static str isDeliveryAddressAgreementSpecificComputedColumnDefinition()
{
    // The <c>IsDeliveryAddressAgreementSpecific</c> field is only designed for import scenarios
    // so we will always return 'No' for export scenarios to save performance.
    return SysComputedColumn::returnLiteral(NoYes::No);
}

private LogisticsPostalAddressBaseEntity findLogisticsPostalAddressBaseEntityByRecId(RecId _recId)
{
    LogisticsPostalAddressBaseEntity logisticsPostalAddressBaseEntity;

    select firstonly logisticsPostalAddressBaseEntity
        where logisticsPostalAddressBaseEntity.AddressRecId == _recId;

    return logisticsPostalAddressBaseEntity;
}

private static str purchaseAgreementConfirmationNumberComputedColumnDefinition()
{
    str purchAgreementIdField = PurchPurchaseAgreementConfirmationHeaderEntity::purchaseAgreementIdComputedColumnDefinition();

    DictField confirmationNumberDictField = new DictField(tableNum(PurchAgreementHeaderHistory), fieldNum(PurchAgreementHeaderHistory, ConfirmationNumber));

    return strFmt('%1+\'-\'+%2.%3', purchAgreementIdField, PurchPurchaseAgreementConfirmationHeaderEntity::getViewSQLTableName(purchAgreementIdField), confirmationNumberDictField.name(DbBackend::Sql));
}

private static str purchaseAgreementIdComputedColumnDefinition()
{
    return SysComputedColumn::returnField(
                tablestr(PurchPurchaseAgreementConfirmationHeaderEntity),
                dataEntityDataSourceStr(PurchPurchaseAgreementConfirmationHeaderEntity, PurchAgreementHeaderHistory),
                fieldStr(PurchAgreementHeaderHistory, PurchNumberSequence));
}

/// <summary>
/// Returns the table name used in the SQL view definition.
/// </summary>
/// <param name = "_field">A computed column definition from which to get the table name.</param>
/// <returns>The table name used in the SQL view definition.</returns>
/// <remarks>The SQL view definition will contain a string wit the value T1.FieldName. This API will return T1.</remarks>
private static str getViewSQLTableName(str _field)
{
    var periodPosition = strScan(_field, '.', 1, strlen(_field));

    return strDel(_field, periodPosition, strLen(_field));
}

private static str confirmationDateComputedColumnDefinition()
{
    str purchAgreementIdField = PurchPurchaseAgreementConfirmationHeaderEntity::purchaseAgreementIdComputedColumnDefinition();

    DictField confirmationNumberDictField = new DictField(tableNum(PurchAgreementHeaderHistory), fieldNum(PurchAgreementHeaderHistory, CreatedDateTime));

    return strFmt('%1.%2', PurchPurchaseAgreementConfirmationHeaderEntity::getViewSQLTableName(purchAgreementIdField), confirmationNumberDictField.name(DbBackend::Sql));
}

public static Query defaultCTQuery()
{
    Query purchaseAgreementHeaderHistoryQuery = new Query();

    var purchaseAgreementHeaderHistoryDS = purchaseAgreementHeaderHistoryQuery.addDataSource(tableNum(PurchAgreementHeaderHistory));

    var agreementClassificationDS = purchaseAgreementHeaderHistoryDS.addDataSource(tableNum(AgreementClassification));
    agreementClassificationDS.joinMode(JoinMode::InnerJoin);
    agreementClassificationDS.relations(true);

    var dirPersonDS = purchaseAgreementHeaderHistoryDS.addDataSource(tableNum(DirPerson));
    dirPersonDS.joinMode(JoinMode::OuterJoin);
    dirPersonDS.relations(true);

    var companyInfoDS = purchaseAgreementHeaderHistoryDS.addDataSource(tableNum(CompanyInfo));
    companyInfoDS.joinMode(JoinMode::OuterJoin);
    companyInfoDS.addLink(fieldNum(PurchAgreementHeaderHistory, BuyingLegalEntity), fieldNum(CompanyInfo, RecId));

    var dimensionSetEntityDS = purchaseAgreementHeaderHistoryDS.addDataSource(tableNum(DimensionAttributeValueSet));
    dimensionSetEntityDS.joinMode(JoinMode::OuterJoin);
    dimensionSetEntityDS.addLink(fieldNum(PurchAgreementHeaderHistory, DefaultDimension), fieldNum(DimensionAttributeValueSet, RecId));

    var purchaseAgreementHeaderDefaultHistoryDS = purchaseAgreementHeaderHistoryDS.addDataSource(tableNum(PurchAgreementHeaderDefaultHistory));
    purchaseAgreementHeaderDefaultHistoryDS.joinMode(JoinMode::OuterJoin);
    purchaseAgreementHeaderDefaultHistoryDS.addLink(fieldNum(PurchAgreementHeaderHistory, RecId), fieldNum(PurchAgreementHeaderDefaultHistory, PurchaseAgreementHeaderHistory));

    var agreementHeaderDefaultHistoryDS = purchaseAgreementHeaderHistoryDS.addDataSource(tableNum(AgreementHeaderDefaultHistory));
    agreementHeaderDefaultHistoryDS.joinMode(JoinMode::OuterJoin);
    agreementHeaderDefaultHistoryDS.addLink(fieldNum(PurchAgreementHeaderHistory, RecId), fieldNum(AgreementHeaderDefaultHistory, AgreementHeaderHistory));

    LogisticsPostalAddressBaseEntityChangeTrackingHelper::addPostalAddressBasedOnAddressRecIdLink(agreementHeaderDefaultHistoryDS, fieldNum(AgreementHeaderDefaultHistory, DeliveryPostalAddress));

    return purchaseAgreementHeaderHistoryQuery;
}
