// Call this method when defaulting must happen prior to data source validation
private void defaultRowOnDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (!_dataSourceCtx.skipDefaultRow())
    {
        _dataSourceCtx.getBuffer().defaultRow();
        _dataSourceCtx.skipDefaultRow(true);
    }
}

private static str isDeliveryAddressAgreementSpecificComputedColumnDefinition()
{
    // The <c>IsDeliveryAddressAgreementSpecific</c> field is only designed for import scenarios
    // so we will always return 'No' for export scnearios to save performance.
    return SysComputedColumn::returnLiteral(NoYes::No);
}

private LogisticsPostalAddressBaseEntity findLogisticsPostalAddressBaseEntityByRecId(RecId _recId)
{
    LogisticsPostalAddressBaseEntity logisticsPostalAddressBaseEntity;

    select firstonly logisticsPostalAddressBaseEntity
        where logisticsPostalAddressBaseEntity.AddressRecId == _recId;

    return logisticsPostalAddressBaseEntity;
}

private void validateWriteAgreementHeader(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean isValid = true;

    PurchAgreementHeader purchAgreementHeader = _dataSourceCtx.getBuffer();

    var dataProvider = PurchAgreementHeaderFieldEditabilityDataProvider::newFromAgreementHeader(purchAgreementHeader);

    if (this.isFieldModified(fieldNum(PurchPurchaseAgreementHeaderEntity, AgreementVendorAccountNumber)) && !dataProvider.canEditVendor())
    {
        isValid = this.allowEditCheckFailed(fieldPName(PurchPurchaseAgreementHeaderEntity, AgreementVendorAccountNumber));
    }

    if (this.isFieldModified(fieldNum(PurchPurchaseAgreementHeaderEntity, AgreementStatus)) && !dataProvider.canEditAgreementState())
    {
        isValid = this.allowEditCheckFailed(fieldPName(PurchPurchaseAgreementHeaderEntity, AgreementStatus));
    }

    if (this.isFieldModified(fieldNum(PurchPurchaseAgreementHeaderEntity, DefaultCommitmentType)) && !dataProvider.canEditDefaultAgreementLineType())
    {
        isValid = this.allowEditCheckFailed(fieldPName(PurchPurchaseAgreementHeaderEntity, DefaultCommitmentType));
    }

    if (this.isFieldModified(fieldNum(PurchPurchaseAgreementHeaderEntity, DefaultEffectiveDate)) && !dataProvider.canEditValidityPeriod())
    {
        isValid = this.allowEditCheckFailed(fieldPName(PurchPurchaseAgreementHeaderEntity, DefaultEffectiveDate));
    }

    if (this.isFieldModified(fieldNum(PurchPurchaseAgreementHeaderEntity, DefaultExpirationDate)) && !dataProvider.canEditValidityPeriod())
    {
        isValid = this.allowEditCheckFailed(fieldPName(PurchPurchaseAgreementHeaderEntity, DefaultExpirationDate));
    }

    if (this.isFieldModified(fieldNum(PurchPurchaseAgreementHeaderEntity, CurrencyCode)) && !dataProvider.canEditCurrency())
    {
        isValid = this.allowEditCheckFailed(fieldPName(PurchPurchaseAgreementHeaderEntity, CurrencyCode));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }
}

private void validateWriteAgreementHeaderDefault(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean isValid = true;

    PurchAgreementHeader purchAgreementHeader = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(PurchPurchaseAgreementHeaderEntity, PurchAgreementHeader)).getBuffer();
    var dataProvider = PurchAgreementHeaderFieldEditabilityDataProvider::newFromAgreementHeader(purchAgreementHeader);

    if (this.isFieldModified(fieldNum(PurchPurchaseAgreementHeaderEntity, ProjectId)) && !dataProvider.canEditProjectId())
    {
        isValid = this.allowEditCheckFailed(fieldPName(PurchPurchaseAgreementHeaderEntity, ProjectId));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }
}

private boolean allowEditCheckFailed(FieldLabel _fieldLabel)
{
    return checkFailed(strFmt("@ApplicationFoundation:DataEntitiesNoAllowEdit", _fieldLabel));
}

private boolean isFieldModified(FieldId _fieldId)
{
    return DataEntityDataSourceRuntimeContextAppSuite_Extension::isFieldModified(this, _fieldId);
}

