public void defaultField(FieldId _fieldId)
{
    super (_fieldId);

    switch (_fieldId)
    {
        case fieldNum(SalesAgreementHeaderEntity, DeliveryModeCode):
            if (this.isFieldSet(fieldNum(SalesAgreementHeaderEntity, ShippingCarrierId))
                && this.isFieldSet(fieldNum(SalesAgreementHeaderEntity, ShippingCarrierServiceId)))
            {
                this.DeliveryModeCode = TMSCarrierService::findDlvModeId(this.ShippingCarrierId, this.ShippingCarrierServiceId);
            }
            break;
    }
}

public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):
            ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, TMSSalesAgreementHeader):
            ret = TMSSalesAgreementHeader::find(SalesAgreementHeader::findAgreementId(this.SalesAgreementId).RecId);
            break;

        default:
            ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    AgreementHeaderDefault agreementHeaderDefault;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeader):

            SalesAgreementHeader salesAgreementHeader = _dataSourceCtx.getBuffer();

            if (_dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
                && this.isFieldSet(fieldNum(SalesAgreementHeaderEntity, AgreementCustomerAccountNumber)))
            {
                salesAgreementHeader.initFromCustTable(CustTable::find(this.AgreementCustomerAccountNumber));
            }

            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeaderDefault):

            SalesAgreementHeaderDefault salesAgreementHeaderDefault = _dataSourceCtx.getBuffer();

            if (_dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
                && this.isFieldSet(fieldNum(SalesAgreementHeaderEntity, AgreementCustomerAccountNumber)))
            {
                salesAgreementHeaderDefault.initFromCustTable(CustTable::find(this.AgreementCustomerAccountNumber));
            }

            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, AgreementHeaderDefault):

            agreementHeaderDefault = _dataSourceCtx.getBuffer();

            if (_dataSourceCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
                && this.isFieldSet(fieldNum(SalesAgreementHeaderEntity, AgreementCustomerAccountNumber)))
            {
                agreementHeaderDefault.initFromCustTable(CustTable::find(this.AgreementCustomerAccountNumber));
            }

            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, TMSSalesAgreementHeader):

            agreementHeaderDefault = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesAgreementHeaderEntity, AgreementHeaderDefault)).getBuffer();

            TMSSalesAgreementHeader tmsSalesAgreementHeader = _dataSourceCtx.getBuffer();

            if (agreementHeaderDefault.isFieldSet(fieldNum(AgreementHeaderDefault, DeliveryMode)))
            {
                tmsSalesAgreementHeader.setCarrierFieldsFromDlvMode(agreementHeaderDefault.DeliveryMode);
            }

            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, AgreementClassification):

            if (this.isFieldSet(fieldNum(SalesAgreementHeaderEntity, SalesAgreementClassificationName)))
            {
                AgreementClassification agreementClassification = _dataSourceCtx.getBuffer();
                agreementClassification.AgreementRelationType = tablenum(SalesAgreementHeader);
            }

            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):

            if (_dataSourceCtx.isAnyEntityFieldSet(this))
            {
                this.TradeEntitySpecificDeliveryAddress::mapLogisticsPostalAddressBaseEntityDataSourceToEntity(_dataSourceCtx);
            }
            else
            {
                agreementHeaderDefault = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesAgreementHeaderEntity, AgreementHeaderDefault)).getBuffer();
                if (agreementHeaderDefault.DeliveryPostalAddress)
                {
                    _dataSourceCtx.setBuffer(this.findLogisticsPostalAddressBaseEntityByRecId(agreementHeaderDefault.DeliveryPostalAddress));
                    _dataSourceCtx.setFieldStateForAllFields(FieldState::Set);
                    _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
                    this.mapDataSourceToEntity(_entityCtx, _dataSourceCtx);
                }
            }

            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeaderExt_RU):

            SalesAgreementHeaderExt_RU salesAgreementHeaderExt = _dataSourceCtx.getBuffer();

            if (!salesAgreementHeaderExt.AgreementId)
            {
                SalesAgreementHeader salesAgreementHeaderLoc = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeader)).getBuffer();
                salesAgreementHeaderExt.initFromAgreementHeader(salesAgreementHeaderLoc);
            }

            break;
    }

    super(_entityCtx, _dataSourceCtx);
}

public void mapDataSourceToEntity(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, AgreementHeaderDefault):
            AgreementHeaderDefault agreementHeaderDefault = _dataSourceCtx.getBuffer();
            this.TradeEntitySpecificDeliveryAddress::mapSavedPostalAddressToEntity(_entityCtx, agreementHeaderDefault.DeliveryPostalAddress);
            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeader):
            SalesAgreementHeader salesAgreementHeader = _dataSourceCtx.getBuffer();
            if (this.DefaultLedgerDimension != salesAgreementHeader.DefaultDimension)
            {
                this.DefaultLedgerDimensionDisplayValue = DimensionSetEntity::find(salesAgreementHeader.DefaultDimension).DisplayValue;
                this.DefaultLedgerDimension = salesAgreementHeader.DefaultDimension;
            }

            if (this.PreparerRecId != salesAgreementHeader.Originator)
            {
                this.PreparerRecId = salesAgreementHeader.Originator;
                this.PreparerPersonPartyNumber = DirPerson::find(salesAgreementHeader.Originator).PartyNumber;
            }

            break;
    }

    super(_entityCtx, _dataSourceCtx);
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    this.defaultRowOnDataSource(_dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeader):
            this.validateWriteAgreementHeader(_dataSourceCtx);
            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, TMSSalesAgreementHeader):
            _entityCtx.detectConflictForDatasource(_dataSourceCtx);
            break;
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    if (ret)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(SalesAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):
                this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
                break;

            case dataEntityDataSourceStr(SalesAgreementHeaderEntity, AgreementHeaderDefault):
                this.validateWriteAgreementHeaderDefault(_entityCtx, _dataSourceCtx);
                break;
        }
    }

    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    this.defaultRowOnDataSource(_dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeader):
            this.validateWriteAgreementHeader(_dataSourceCtx);
            break;

        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, AgreementHeaderDefault):
            this.validateWriteAgreementHeaderDefault(_entityCtx, _dataSourceCtx);
            break;
    }

    return super(_entityCtx, _dataSourceCtx);
}

public boolean deleteEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesAgreementHeaderEntity, LogisticsPostalAddressBaseEntity):
            _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            break;
    }

    return super(_entityCtx, _dataSourceCtx);
}

public static void jumpRefMethod(str _menuItemName, Common _stagingBuffer, Object _callerObj, DataAreaId _dataAreaId)
{
    changeCompany(_dataAreaId)
    {
        SalesAgreementHeaderStaging stagingRecord = _stagingBuffer;
        var record = SalesAgreementHeader::findAgreementId(stagingRecord.SalesAgreementId);

        Args args = new Args();
        args.record(record);
        args.lookupRecord(record);

        MenuFunction::runCalled(_menuItemName, MenuItemType::Display, false, args);
    }
}

