// Call this method when defaulting must happen prior to data source validation
private void defaultRowOnDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (!_dataSourceCtx.skipDefaultRow())
    {
        _dataSourceCtx.getBuffer().defaultRow();
        _dataSourceCtx.skipDefaultRow(true);
    }
}

private static str isDeliveryAddressAgreementSpecificComputedColumnDefinition()
{
    // The <c>IsDeliveryAddressAgreementSpecific</c> field is only designed for import scenarios
    // so we will always return 'No' for export scenarios to save performance.
    return SysComputedColumn::returnLiteral(NoYes::No);
}

private LogisticsPostalAddressBaseEntity findLogisticsPostalAddressBaseEntityByRecId(RecId _recId)
{
    LogisticsPostalAddressBaseEntity logisticsPostalAddressBaseEntity;

    select firstonly logisticsPostalAddressBaseEntity
        where logisticsPostalAddressBaseEntity.AddressRecId == _recId;

    return logisticsPostalAddressBaseEntity;
}

private void validateWriteAgreementHeader(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean isValid = true;

    SalesAgreementHeader salesAgreementHeader = _dataSourceCtx.getBuffer();

    var dataProvider = SalesAgreementHeaderFieldEditabilityDataProvider::newFromAgreementHeader(salesAgreementHeader);

    if (this.isFieldModified(fieldNum(SalesAgreementHeaderEntity, AgreementStatus)) && !dataProvider.canEditAgreementState())
    {
        isValid = this.allowEditCheckFailed(fieldPName(SalesAgreementHeaderEntity, AgreementStatus));
    }

    if (this.isFieldModified(fieldNum(SalesAgreementHeaderEntity, DefaultCommitmentType)) && !dataProvider.canEditDefaultAgreementLineType())
    {
        isValid = this.allowEditCheckFailed(fieldPName(SalesAgreementHeaderEntity, DefaultCommitmentType));
    }

    if (this.isFieldModified(fieldNum(SalesAgreementHeaderEntity, DefaultEffectiveDate)) && !dataProvider.canEditValidityPeriod())
    {
        isValid = this.allowEditCheckFailed(fieldPName(SalesAgreementHeaderEntity, DefaultEffectiveDate));
    }

    if (this.isFieldModified(fieldNum(SalesAgreementHeaderEntity, DefaultExpirationDate)) && !dataProvider.canEditValidityPeriod())
    {
        isValid = this.allowEditCheckFailed(fieldPName(SalesAgreementHeaderEntity, DefaultExpirationDate));
    }

    if (this.isFieldModified(fieldNum(SalesAgreementHeaderEntity, CurrencyCode)) && !dataProvider.canEditCurrency())
    {
        isValid = this.allowEditCheckFailed(fieldPName(SalesAgreementHeaderEntity, CurrencyCode));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }
}

private void validateWriteAgreementHeaderDefault(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean isValid = true;

    SalesAgreementHeader salesAgreementHeader = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(SalesAgreementHeaderEntity, SalesAgreementHeader)).getBuffer();
    var dataProvider = SalesAgreementHeaderFieldEditabilityDataProvider::newFromAgreementHeader(salesAgreementHeader);

    if (this.isFieldModified(fieldNum(SalesAgreementHeaderEntity, ProjectId)) && !dataProvider.canEditProjectId())
    {
        isValid = this.allowEditCheckFailed(fieldPName(SalesAgreementHeaderEntity, ProjectId));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }
}

private boolean allowEditCheckFailed(FieldLabel _fieldLabel)
{
    return checkFailed(strFmt("@ApplicationFoundation:DataEntitiesNoAllowEdit", _fieldLabel));
}

private boolean isFieldModified(FieldId _fieldId)
{
    return DataEntityDataSourceRuntimeContextAppSuite_Extension::isFieldModified(this, _fieldId);
}

public static Query defaultCTQuery()
{
    Query salesAgreementHeaderQuery = new Query();

    var salesAgreementHeaderDS = salesAgreementHeaderQuery.addDataSource(tableNum(SalesAgreementHeader));

    var agreementClassificationDS = salesAgreementHeaderDS.addDataSource(tableNum(AgreementClassification));
    agreementClassificationDS.joinMode(JoinMode::InnerJoin);
    agreementClassificationDS.relations(true);

    var dirPersonDS = salesAgreementHeaderDS.addDataSource(tableNum(DirPerson));
    dirPersonDS.joinMode(JoinMode::OuterJoin);
    dirPersonDS.relations(true);

    var companyInfoDS = salesAgreementHeaderDS.addDataSource(tableNum(CompanyInfo));
    companyInfoDS.joinMode(JoinMode::OuterJoin);
    companyInfoDS.addLink(fieldNum(SalesAgreementHeader, SellingLegalEntity), fieldNum(CompanyInfo, RecId));

    var dimensionSetEntityDS = salesAgreementHeaderDS.addDataSource(tableNum(DimensionAttributeValueSet));
    dimensionSetEntityDS.joinMode(JoinMode::OuterJoin);
    dimensionSetEntityDS.addLink(fieldNum(SalesAgreementHeader, DefaultDimension), fieldNum(DimensionAttributeValueSet, RecId));

    var salesAgreementHeaderDefaultDS = salesAgreementHeaderDS.addDataSource(tableNum(SalesAgreementHeaderDefault));
    salesAgreementHeaderDefaultDS.joinMode(JoinMode::OuterJoin);
    salesAgreementHeaderDefaultDS.addLink(fieldNum(SalesAgreementHeader, RecId), fieldNum(SalesAgreementHeaderDefault, SalesAgreementHeader));

    var orderResponsibleWorkerDS = salesAgreementHeaderDefaultDS.addDataSource(tableNum(HcmWorker));
    orderResponsibleWorkerDS.joinMode(JoinMode::OuterJoin);
    orderResponsibleWorkerDS.addLink(fieldNum(SalesAgreementHeaderDefault, WorkerSalesResponsible), fieldNum(HcmWorker, RecId));

    var agreementHeaderDefaultDS = salesAgreementHeaderDS.addDataSource(tableNum(AgreementHeaderDefault));
    agreementHeaderDefaultDS.joinMode(JoinMode::OuterJoin);
    agreementHeaderDefaultDS.addLink(fieldNum(SalesAgreementHeader, RecId), fieldNum(AgreementHeaderDefault, AgreementHeader));

    LogisticsPostalAddressBaseEntityChangeTrackingHelper::addPostalAddressBasedOnAddressRecIdLink(agreementHeaderDefaultDS, fieldNum(AgreementHeaderDefault, DeliveryPostalAddress));

    var tmsSalesAgreementHeaderDS = salesAgreementHeaderDS.addDataSource(tableNum(TMSSalesAgreementHeader));
    tmsSalesAgreementHeaderDS.joinMode(JoinMode::OuterJoin);
    tmsSalesAgreementHeaderDS.addLink(fieldNum(SalesAgreementHeader, RecId), fieldNum(TMSSalesAgreementHeader, RefRecId));

    return salesAgreementHeaderQuery;
}

