public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderHeaderCDSEntity, SalesOrderHeaderV2Entity):
            if (this.SalesOrderNumber)
            {
                _dataSourceCtx.getBuffer().skipNumberSequenceCheck(true);
            }
            break;
    }
    super(_entityCtx, _dataSourceCtx);
}

// This method implements the interface defined by <c>TradeEntitySpecificDeliveryAddress</c>
public RecId deliveryAddressPartyRecId()
{
    return CustTable::find(this.OrderingCustomerAccountNumber).Party;
}

/// <summary>
/// Updates the discount and totals for a given entity record.
/// </summary>
/// <param name = "_salesOrderHeaderEntityRecord">The sales order header entity record.</param>
/// <returns>The list of related entities that need to be resynced.</returns>
/// <remarks>
/// This method is aimed to be used by Dual Write to trigger price and discount calculation updates.
/// See <c>RunDocumentAction</c> OData action on the <c>DualWriteProjectConfigurationEntity</c> entity.
/// </remarks>
public static List updatePrices(Common _salesOrderHeaderEntityRecord)
{
    SalesOrderHeaderCDSEntity salesOrderHeaderEntityRecord = _salesOrderHeaderEntityRecord;
    SalesCDSPrePricingAmountsTmp prePricingAmounts = SalesCDSPrePricingAmountsTmp::initPrePricingAmountsFromSalesLines(salesOrderHeaderEntityRecord.SalesOrderNumber);
    List relatedRecords = new List(Types::Container);

    if (salesOrderHeaderEntityRecord != null)
    {
        salesOrderHeaderEntityRecord.calculatePrices();
        relatedRecords = salesOrderHeaderEntityRecord.getRecordsToSync(prePricingAmounts);
    }

    return relatedRecords;
}

private void calculatePrices()
{
    SalesTable salesTable = SalesTable::find(this.SalesOrderNumber, true);

    this.updateLinesPriceDisc(salesTable); /// Updates the line discount for all lines within the given order.
    salesTable.reread(); /// Needs reread() because updating SalesLine can change SalesTable.
    salesTable.updateFinalDisc();  /// Calculates the total discount for sales orders, purchase orders.
    salesTable.updateMultiLineDisc(); /// Updates the multiline discount for all lines within the given order.
    SalesOrderTotalsCalculate::calculateTotalsForOrder(this.SalesOrderNumber); /// Updates the SalesOrderTotals and SalesOrderLineTotals tables, which should trigger the DW on the SalesOrderHeaderCDSEntity and SalesOrderLineCDSEntity
}

private void updateLinesPriceDisc(SalesTable _salesTable)
{
    // Do not recalculate price for call center returns.
    if (!(_salesTable.mcrIsCallCenter() && _salesTable.SalesType != SalesType::ReturnItem))
    {
        SalesLine salesLine;

        while select forupdate salesLine
            where salesLine.SalesId == this.SalesOrderNumber
        {
            salesLine.setPriceDisc(salesLine.inventDim());
            salesLine.update();
        }
    }
}

private List getRecordsToSync(SalesCDSPrePricingAmountsTmp _prePricingAmounts)
{
    List recordsToSync = new List(Types::Container);
    recordsToSync.addEnd([this.TableId, this.RecId]);

    TableId salesOrderLineEntityRecordTableId;
    SalesOrderLineCDSEntity salesOrderLineEntityRecord;
    Set salesOrderLineRecords = new Set(Types::Int64);
    boolean dualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight =
        DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled();

    while select salesOrderLineEntityRecord
        where salesOrderLineEntityRecord.SalesOrderNumber == this.SalesOrderNumber
        exists join _prePricingAmounts // Only amount fields changed lines to be synced.
            where _prePricingAmounts.ReferenceNumber == salesOrderLineEntityRecord.SalesOrderNumber
                && _prePricingAmounts.LineCreationSequenceNumber == salesOrderLineEntityRecord.LineCreationSequenceNumber
                && (_prePricingAmounts.LineAmount != salesOrderLineEntityRecord.LineAmount
                    || _prePricingAmounts.LineDisc != salesOrderLineEntityRecord.LineDiscountAmount
                    || _prePricingAmounts.LinePercent != salesOrderLineEntityRecord.LineDiscountPercentage
                    || _prePricingAmounts.MultiLnDisc != salesOrderLineEntityRecord.MultilineDiscountAmount
                    || _prePricingAmounts.MultiLnPercent != salesOrderLineEntityRecord.MultilineDiscountPercentage
                    || _prePricingAmounts.SalesMarkup != salesOrderLineEntityRecord.FixedPriceCharges
                    || _prePricingAmounts.SalesPrice != salesOrderLineEntityRecord.SalesPrice
                    || _prePricingAmounts.TotalTaxAmount != salesOrderLineEntityRecord.TotalTaxAmount
                    || _prePricingAmounts.TotalChargesAmount != salesOrderLineEntityRecord.TotalChargesAmount
                    || _prePricingAmounts.TotalDiscountAmount != salesOrderLineEntityRecord.TotalDiscountAmount)
    {
        if (!salesOrderLineEntityRecordTableId)
        {
            salesOrderLineEntityRecordTableId = this.getSalesOrderLineEntityRecordTableId();
        }

        recordsToSync.addEnd([salesOrderLineEntityRecordTableId, salesOrderLineEntityRecord.RecId]);

        if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled()
            || dualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight)
        {
            salesOrderLineRecords.add(salesOrderLineEntityRecord.RecId);
        }
    }

    if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled()
        || dualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight)
    {           
        if (!salesOrderLineEntityRecordTableId)
        {
            salesOrderLineEntityRecordTableId = this.getSalesOrderLineEntityRecordTableId();
        }

        recordsToSync.appendList(this.getSalesPriceOrLineAmountOrLineDiscPercentageOrLineDiscAmountNotSyncedRecords(salesOrderLineEntityRecordTableId, salesOrderLineRecords));
    }

    return recordsToSync;
}

private TableId getSalesOrderLineEntityRecordTableId()
{
    const str SalesOrderLineCDSEntityTableName = tableStr(SalesOrderLineCDSEntity);

    if (SalesOrderLineCDSV2EntitySupportPriceOrderFlight::instance().isEnabled())
    {
        const str SalesOrderLineCDSV2EntityTableName = tableStr(SalesOrderLineCDSV2Entity);
        const str SalesOrderDetailsExternalEntityName = 'salesorderdetails';

        DualWriteProjectConfiguration projectConfiguration;
        DMFEntity dmfEntity;

        select firstonly RecId from dmfEntity
            where dmfEntity.TargetEntity == SalesOrderLineCDSV2EntityTableName
        exists join projectConfiguration
            where projectConfiguration.InternalEntityName == dmfEntity.EntityName
                && projectConfiguration.ExternalEntityName == SalesOrderDetailsExternalEntityName
                && projectConfiguration.IsDelete == NoYes::No;

        if (dmfEntity.RecId)
        {
            return tableName2Id(SalesOrderLineCDSV2EntityTableName);
        }
    }

    return tableName2Id(SalesOrderLineCDSEntityTableName);
}

private List getSalesPriceOrLineAmountOrLineDiscPercentageOrLineDiscAmountNotSyncedRecords(TableId _salesOrderLineEntityTableId, Set _recordsToSync)
{
    List records = new List(Types::Container);
    SalesOrderLineNotSyncedWithCDSRecord salesOrderLineNotSyncedWithCDSRecord;

    ttsbegin;

    while select forupdate salesOrderLineNotSyncedWithCDSRecord
        where salesOrderLineNotSyncedWithCDSRecord.SalesId == this.SalesOrderNumber
            && (salesOrderLineNotSyncedWithCDSRecord.FieldsType == SalesNotSyncedWithCDSFieldsType::SalesPriceOrLineAmount
                    && DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
                || (salesOrderLineNotSyncedWithCDSRecord.FieldsType == SalesNotSyncedWithCDSFieldsType::SalesLineDiscountPercentageOrLineDiscountAmount
                    && DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
    {
        if (!_recordsToSync.in(salesOrderLineNotSyncedWithCDSRecord.SalesLineRefRecId))
        {
            records.addEnd([_salesOrderLineEntityTableId, salesOrderLineNotSyncedWithCDSRecord.SalesLineRefRecId]);
        }

        salesOrderLineNotSyncedWithCDSRecord.delete();
    }

    ttscommit;

    return records;
}

public static container getEntityDataSourceToFieldMapping(container mapping)
{
    mapping += [[dataEntityDataSourceStr(SalesOrderHeaderCDSEntity, SalesOrderHeaderV2Entity), tablenum(LogisticsPostalAddress), fieldstr(SalesOrderHeaderCDSEntity, DeliveryPostalAddressRecId)]];

    return mapping;
}

public static Query defaultCTQuery()
{
    Query salesOrderHeaderQuery = new Query();

    var salesTableDS = salesOrderHeaderQuery.addDataSource(tableNum(SalesTable));

    SalesOrderHeaderV2Entity::addChangeTrackingDataSourcesForSalesTableChildren(salesTableDS);

    return salesOrderHeaderQuery;
}

/// <summary>
/// Calculate the are totals updated flag.
/// </summary>
/// <returns>The value of <c>AreTotalsUpdated</c> field.</returns>
public static str areTotalsUpdatedComputedColumn()
{
    List conditionList = new List(Types::String);

    conditionList.addEnd(SalesOrderHeaderCDSEntity::isOrderTotalsUpdatedLaterThan(SysComputedColumn::comparisonField(dataentityviewstr(SalesOrderHeaderCDSEntity), viewstr(SalesOrderLastChangeDateV2), fieldStr(SalesOrderLastChangeDateV2, HeaderLastModifiedDateTime))));

    conditionList.addEnd(
        SysComputedColumn::or2(
            SalesOrderHeaderCDSEntity::isOrderTotalsUpdatedLaterThan(SysComputedColumn::comparisonField(dataentityviewstr(SalesOrderHeaderCDSEntity), viewstr(SalesOrderLastChangeDateV2), fieldStr(SalesOrderLastChangeDateV2, LineLastModifiedDateTime))),
            SysComputedColumn::equalExpression(SysComputedColumn::comparisonField(tableStr(SalesOrderHeaderCDSEntity), viewstr(SalesOrderLastChangeDateV2), fieldStr(SalesOrderLastChangeDateV2, LineCount)), SysComputedColumn::returnLiteral(0))));

    SalesOrderHeaderCDSEntity::addChargeConditions(conditionList);

    return SysComputedColumn::if(SysComputedColumn::isNullExpression(SysComputedColumn::comparisonField(dataentityviewstr(SalesOrderHeaderCDSEntity), tableStr(SalesOrderTotals), fieldStr(SalesOrderTotals, RecId))),
        SysComputedColumn::returnLiteral(NoYes::No),
        SysComputedColumn::if(
            SysComputedColumn::and(conditionList),
            SysComputedColumn::returnLiteral(NoYes::Yes),
            SysComputedColumn::returnLiteral(NoYes::No))
        );
}

private static void addChargeConditions(List _conditionList)
{
    if (isConfigurationkeyEnabled(configurationKeyNum(Markup)))
    {
        _conditionList.addEnd(
        SysComputedColumn::or2(
            SalesOrderHeaderCDSEntity::isOrderTotalsUpdatedLaterThan(SysComputedColumn::comparisonField(dataentityviewstr(SalesOrderHeaderCDSEntity), viewstr(SalesOrderChargeLastChangeDate), fieldStr(SalesOrderChargeLastChangeDate, HeaderChargeLastModifiedDateTime))),
            SysComputedColumn::equalExpression(SysComputedColumn::comparisonField(dataentityviewstr(SalesOrderHeaderCDSEntity), viewstr(SalesOrderChargeLastChangeDate), fieldStr(SalesOrderChargeLastChangeDate, HeaderChargeCount)), SysComputedColumn::returnLiteral(0))));

        _conditionList.addEnd(
        SysComputedColumn::or2(
            SalesOrderHeaderCDSEntity::isOrderTotalsUpdatedLaterThan(SysComputedColumn::comparisonField(dataentityviewstr(SalesOrderHeaderCDSEntity), viewstr(SalesOrderChargeLastChangeDate), fieldStr(SalesOrderChargeLastChangeDate, LineChargeLastModifiedDateTime))),
            SysComputedColumn::equalExpression(SysComputedColumn::comparisonField(dataentityviewstr(SalesOrderHeaderCDSEntity), viewstr(SalesOrderChargeLastChangeDate), fieldStr(SalesOrderChargeLastChangeDate, LineChargeCount)), SysComputedColumn::returnLiteral(0))));
    }
}

private static str isOrderTotalsUpdatedLaterThan(str _fieldToCompare)
{
    return SysComputedColumn::compareExpressions(
        SysComputedColumn::comparisonField(tableStr(SalesOrderHeaderCDSEntity), identifierStr(SalesOrderTotals), fieldStr(SalesOrderTotals, LastUpdatedDateTime)),
        '>',
        _fieldToCompare);
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderHeaderCDSEntity, SalesOrderHeaderV2Entity):
            // Enable insert of records with blank values for mandatory field RequestedShippingDate
            if (_entityCtx.isDatabaseOperationInsert())
            {
                this.setBlankFieldStateToNotSet(fieldNum(SalesOrderHeaderCDSEntity, RequestedShippingDate));
            }

            // Find existing logistic address location to avoid duplicate.
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                if (this.IsDeliveryAddressOrderSpecific)
                {
                    this.TradeEntitySpecificDeliveryAddress::resolveOrderSpecificLocationId(this.SalesOrderNumber);
                }
                else
                {
                    this.TradeEntitySpecificDeliveryAddress::resolvePartyAddressLocationId();
                }
            }

            break;
    }

    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderHeaderCDSEntity, SalesOrderHeaderV2Entity):
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                SalesOrderHeaderV2Entity entityBuf = _dataSourceCtx.getBuffer();

                entityBuf.IsCDSIntegration = NoYes::Yes;

                if (_entityCtx.isDatabaseOperationInsert())
                {
                    // Enable insert of logistics address when location id is blank but has a delivery address description
                    if (!entityBuf.DeliveryAddressLocationId && entityBuf.DeliveryAddressDescription)
                    {
                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesOrderHeaderV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }
                }

                if (_entityCtx.isDatabaseOperationUpdate())
                {
                    // Enable insert of logistics address when location id is blank.
                    if (!entityBuf.DeliveryAddressLocationId
                        && (!SalesSkipAddressCreationIfAddressDescriptionBlankFlight::instance().isEnabled() || entityBuf.DeliveryAddressDescription))
                    {
                        // New location Id will be generated in TradeEntitySpecificDeliveryAddress.mapLogisticsPostalAddressBaseEntityDataSourceToEntity().
                        entityBuf.DeliveryAddressLocationId = entityBuf.orig().DeliveryAddressLocationId;

                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesOrderHeaderV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }
                }

                entityBuf.fieldState(fieldNum(SalesOrderHeaderV2Entity, IsDeliveryAddressOrderSpecific), FieldState::Set); // let new logistics location is associated with party.
            }
            break;
    }
}

private void allowAddressCreation(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesOrderHeaderV2Entity entityBuf = _dataSourceCtx.getBuffer();

    // Update address fields state to Set. Otherwise, those fields value cannot be assigned to the new address.
    _dataSourceCtx.setFieldStateForSubDataSourceFields(dataEntityDataSourceStr(SalesOrderHeaderV2Entity, LogisticsPostalAddressBaseEntity), FieldState::Set);
    entityBuf.fieldState(fieldNum(SalesOrderHeaderV2Entity, DeliveryAddressLocationId), FieldState::NotSet); // Create new record.
    entityBuf.fieldState(fieldNum(SalesOrderHeaderV2Entity, FormattedDelveryAddress), FieldState::NotSet); // Not allow for write.
}

public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common entityDataSource = null;
    switch(_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderHeaderCDSEntity, SalesOrderOriginEntity):
            entityDataSource = this.findSalesOrderOriginEntity();
            break;
        default :
            entityDataSource = super(_entityCtx, _dataSourceCtx);
            break;
    }
    return entityDataSource;
}

private SalesOrderOriginEntity findSalesOrderOriginEntity()
{
    SalesOrderOriginEntity salesOrderOriginEntity;

    select firstonly salesOrderOriginEntity
        where salesOrderOriginEntity.IsOriginTypeAssigned == NoYes::Yes
            && salesOrderOriginEntity.OriginType == this.SalesOrderOriginType;

    return salesOrderOriginEntity;
}
