/// <summary>
/// Returns the default change tracking query.
/// </summary>
/// <returns>The default change tracking query.</returns>
public static Query defaultCTQuery()
{
    Query salesOrderHeaderV3ExistAddrReadOnlyQuery = new Query();

    var salesTableRootDS = SalesOrderHeaderV3ExistAddrReadOnlyQuery.addDataSource(tableNum(SalesTable));

    SalesOrderHeaderV2Entity::addChangeTrackingDataSourcesForSalesOrderHeaderV2Entity(salesTableRootDS, fieldNum(SalesTable, SalesId), JoinMode::InnerJoin);
    LogisticsPostalAddressBaseEntityChangeTrackingHelper::addPostalAddressBasedOnAddressRecIdLink(salesTableRootDS, fieldNum(SalesTable, DeliveryPostalAddress));

    return SalesOrderHeaderV3ExistAddrReadOnlyQuery;
}
