    dimensionHierarchyDS.joinMode(JoinMode::OuterJoin);
    dimensionHierarchyDS.relations(true);

    var foreignTradeCategoryDS = salesLineDS.addDataSource(tableNum(SalesLineForeignTradeCategory));
