public static str productNameComputedColumnDefinition()
{
    return SysComputedColumn::if(
        SysComputedColumn::notEqualExpression(SysComputedColumn::comparisonField(tableStr(SalesOrderLineCDSEntity), dataEntityDataSourceStr(SalesOrderLineCDSEntity, EcoResProductTranslationToSystemLanguage), fieldStr(EcoResProductTranslation, Name)),
                                          SysComputedColumn::comparisonLiteral('')),
        SysComputedColumn::returnField(tableStr(SalesOrderLineCDSEntity), dataEntityDataSourceStr(SalesOrderLineCDSEntity, EcoResProductTranslationToSystemLanguage), fieldStr(EcoResProductTranslation, Name)),
        SysComputedColumn::returnField(tableStr(SalesOrderLineCDSEntity), dataEntityDataSourceStr(SalesOrderLineCDSEntity, EcoResProductMasterTranslationToSystemLanguage), fieldStr(EcoResProductTranslation, Name)));
}

