public static str areTotalsComputedColumnDefinition()
{
    return SysComputedColumn::if(SysComputedColumn::isNullExpression(SysComputedColumn::comparisonField(tableStr(SalesOrderLineCDSEntity), dataEntityDataSourceStr(SalesOrderLineCDSEntity, SalesOrderLineTotals), fieldStr(SalesOrderLineTotals, RecId))),
        SysComputedColumn::returnLiteral(NoYes::No),
