private InventQty getQuantityToPick()
{
    InventQty quantityToPick;

    if (this.isFieldSet(fieldNum(SalesOrderLineCDSEntity, PickedQuantity)))
    {
        quantityToPick = this.PickedQuantity;

        if (this.RecId)
        {
            SalesLine salesLine = SalesLine::findLineCreationSequence(this.SalesOrderNumber, this.LineCreationSequenceNumber);

            if (salesLine)
            {
                InventQty quantityAlreadyPicked = salesLine.mcrPickedLineQuantity();
                quantityToPick -= quantityAlreadyPicked;
            }
        }
    }

    return quantityToPick;
}

private void pickLineQuantity(InventQty _quantityToPick)
{
    if (_quantityToPick)
    {
        SalesLine salesLine = SalesLine::findLineCreationSequence(this.SalesOrderNumber, this.LineCreationSequenceNumber);

        if (salesLine)
        {
            InventMovement inventMovement = InventMov_Sales::newFromSalesLine(salesLine);
            InventDim inventDim = inventMovement.inventdim();
            InventUpd_Picked::newParameters(inventMovement, inventDim, inventDim.toDimParm(), null, null, -_quantityToPick).updateNow();
        }
    }
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret, isLineDiscountSet;
    InventQty quantityToPick;
    SalesPrice salesPrice;
    LineAmount lineAmount;
    real lineDiscountPercentage;
    LineAmount lineDiscountAmount;
    SalesOrderLineV2Entity orderLineEntity;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderLineCDSEntity, SalesOrderLineEntity):
            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                orderLineEntity = _dataSourceCtx.getBuffer();
                salesPrice = orderLineEntity.SalesPrice;
                lineAmount = orderLineEntity.LineAmount;
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                orderLineEntity = _dataSourceCtx.getBuffer();
                lineDiscountPercentage = orderLineEntity.LineDiscountPercentage;
                lineDiscountAmount = orderLineEntity.LineDiscountAmount;
            }

            isLineDiscountSet = this.insertLineDiscount(_dataSourceCtx);
            quantityToPick = this.getQuantityToPick();
            break;
    }

    ret = super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderLineCDSEntity, SalesOrderLineEntity):
            if (isLineDiscountSet)
            {
                this.updateSalesMarkupWithRoundingDifference(_dataSourceCtx);
            }
            this.pickLineQuantity(quantityToPick);

            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesPriceOrLineAmountOutOfSync(orderLineEntity, salesPrice, lineAmount);
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesLineDiscountPercentageOrLineDiscountAmountOutOfSync(orderLineEntity, lineDiscountPercentage, lineDiscountAmount);
            }

            break;
    }
    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret, isLineDiscountSet;
    InventQty quantityToPick;
    SalesPrice salesPrice;
    LineAmount lineAmount;
    real lineDiscountPercentage;
    LineAmount lineDiscountAmount;
    SalesOrderLineV2Entity orderLineEntity;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderLineCDSEntity, SalesOrderLineEntity):
            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                orderLineEntity = _dataSourceCtx.getBuffer();
                salesPrice = orderLineEntity.SalesPrice;
                lineAmount = orderLineEntity.LineAmount;
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                orderLineEntity = _dataSourceCtx.getBuffer();
                lineDiscountPercentage = orderLineEntity.LineDiscountPercentage;
                lineDiscountAmount = orderLineEntity.LineDiscountAmount;
            }

            isLineDiscountSet = this.updateLineDiscount(_dataSourceCtx);
            quantityToPick = this.getQuantityToPick();

            if (quantityToPick < 0)
            {
                this.pickLineQuantity(quantityToPick);
            }

            break;
    }

    ret = super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderLineCDSEntity, SalesOrderLineEntity):
            if (isLineDiscountSet)
            {
                this.updateSalesMarkupWithRoundingDifference(_dataSourceCtx);
            }
            if (quantityToPick > 0)
            {
                this.pickLineQuantity(quantityToPick);
            }

            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesPriceOrLineAmountOutOfSync(orderLineEntity, salesPrice, lineAmount);
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesLineDiscountPercentageOrLineDiscountAmountOutOfSync(orderLineEntity, lineDiscountPercentage, lineDiscountAmount);
            }

            break;
    }
    return ret;
}

private void checkSalesPriceOrLineAmountOutOfSync(SalesOrderLineV2Entity _orderLineEntity, SalesPrice _originalSalesPrice, LineAmount _originalLineAmount)
{
    if (_orderLineEntity.SalesPrice != _originalSalesPrice || _orderLineEntity.LineAmount != _originalLineAmount)
    {
        SalesOrderLineNotSyncedWithCDSRecord::insertRecord(_orderLineEntity.SalesOrderNumber, _orderLineEntity.RecId, SalesNotSyncedWithCDSFieldsType::SalesPriceOrLineAmount);
    }
}

private void checkSalesLineDiscountPercentageOrLineDiscountAmountOutOfSync(SalesOrderLineV2Entity _orderLineEntity, real _originalLineDiscountPercentage, LineAmount _originalLineDiscountAmount)
{
    if (_orderLineEntity.LineDiscountPercentage != _originalLineDiscountPercentage || _orderLineEntity.LineDiscountAmount != _originalLineDiscountAmount)
    {
        SalesOrderLineNotSyncedWithCDSRecord::insertRecord(_orderLineEntity.SalesOrderNumber, _orderLineEntity.RecId, SalesNotSyncedWithCDSFieldsType::SalesLineDiscountPercentageOrLineDiscountAmount);
    }
}

public void postLoad()
{
    super();

    if (this.RecId)
    {
        SalesLine salesLine = SalesLine::findLineCreationSequence(this.SalesOrderNumber, this.LineCreationSequenceNumber);
        this.PickedQuantity = salesLine.mcrPickedLineQuantity();
    }
}

private boolean insertLineDiscount(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (this.TotalDiscountAmount != 0 && this.OrderedSalesQuantity != 0)
    {
        this.setLineDiscount(_dataSourceCtx, this.OrderedSalesQuantity);
        return true;
    }
    return false;
}

private boolean updateLineDiscount(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (SalesCDSUpdateLineDiscountOnlyWhenTotalDiscountAmountModifiedFlight::instance().isEnabled()
        && !this.isFieldModified(fieldNum(SalesOrderLineCDSEntity, TotalDiscountAmount)))
    {
        return false;
    }

    boolean isQuantitySet = this.isFieldSet(fieldNum(SalesOrderLineCDSEntity, OrderedSalesQuantity));
    if (this.TotalDiscountAmount != 0)
    {
        if (isQuantitySet && this.OrderedSalesQuantity != 0)
        {
            this.setLineDiscount(_dataSourceCtx, this.OrderedSalesQuantity);
            return true;
        }
        else
        {
            SalesLine line = SalesLine::findLineCreationSequence(this.SalesOrderNumber, this.LineCreationSequenceNumber);
            if (line.SalesQty != 0)
            {
                this.setLineDiscount(_dataSourceCtx, line.SalesQty);
                return true;
            }
        }
    }
    return false;
}

private void updateSalesMarkupWithRoundingDifference(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesOrderLineV2Entity orderLineEntity = _dataSourceCtx.getBuffer();
    SalesLine line = SalesLine::findInventTransId(orderLineEntity.InventoryLotId, true);
    var calculatedDiscount = CurrencyExchange::round(line.SalesQty * line.LineDisc, line.CurrencyCode);
    if (this.TotalDiscountAmount != calculatedDiscount)
    {
        line.SalesMarkup = calculatedDiscount - this.TotalDiscountAmount;
        line.LineAmount = line.calcLineAmountForced();
        line.update();
    }
}

private void setLineDiscount(DataEntityDataSourceRuntimeContext _dataSourceCtx, SalesOrderedQty _qty)
{
    if (_qty != 0)
    {
        SalesOrderLineV2Entity orderLineEntity = _dataSourceCtx.getBuffer();
        orderLineEntity.LineDiscountAmount = CurrencyExchange::round((this.TotalDiscountAmount / _qty), Ledger::accountingCurrency());
        orderLineEntity.FixedPriceCharges = 0;
        _dataSourceCtx.setBuffer(orderLineEntity);
    }
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesOrderLineCDSEntity, SalesOrderLineEntity):
            // Enable insert of records with blank values for mandatory field RequestedShippingDate
            if (_entityCtx.isDatabaseOperationInsert())
            {
                this.setBlankFieldStateToNotSet(fieldNum(SalesOrderLineCDSEntity, RequestedShippingDate));
            }

            // Find existing logistic address location to avoid duplicate.
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                if (this.IsDeliveryAddressOrderSpecific)
                {
                    this.TradeEntitySpecificDeliveryAddress::resolveOrderSpecificLocationId(this.SalesOrderNumber);
                }
                else
                {
                    this.TradeEntitySpecificDeliveryAddress::resolvePartyAddressLocationId();
                }
            }

            break;
    }

    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {     
        case dataEntityDataSourceStr(SalesOrderLineCDSEntity, SalesOrderLineEntity):
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                SalesOrderLineV2Entity entityBuf = _dataSourceCtx.getBuffer();

                entityBuf.IsCDSIntegration = NoYes::Yes;

                if (this.SalesPrice == 0 && this.shouldDefaultToAgreementPrice())
                {
                    entityBuf.fieldState(fieldNum(SalesOrderLineV2Entity, SalesPrice), FieldState::NotSet);
                }

                if (_entityCtx.isDatabaseOperationInsert())
                {
                    // Enable insert of logistics address when location id is blank but has a delivery address description
                    if (!entityBuf.DeliveryAddressLocationId && entityBuf.DeliveryAddressDescription)
                    {
                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesOrderLineV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }
                }
                else if (_entityCtx.isDatabaseOperationUpdate())
                {
                    // Enable insert of logistics address when location id is blank.
                    if (!entityBuf.DeliveryAddressLocationId
                        && (!SalesSkipAddressCreationIfAddressDescriptionBlankFlight::instance().isEnabled() || entityBuf.DeliveryAddressDescription))
                    {
                        // New location Id will be generated in TradeEntitySpecificDeliveryAddress.mapLogisticsPostalAddressBaseEntityDataSourceToEntity().
                        entityBuf.DeliveryAddressLocationId = entityBuf.orig().DeliveryAddressLocationId;

                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesOrderLineV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }
                }

                entityBuf.fieldState(fieldNum(SalesOrderLineV2Entity, IsDeliveryAddressOrderSpecific), FieldState::Set); // let new logistics location is associated with party.
            }
            break;
    }
}

private void allowAddressCreation(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesOrderLineV2Entity entityBuf = _dataSourceCtx.getBuffer();
    // Update address fields state to Set. Otherwise, those fields value cannot be assigned to the new address.
    _dataSourceCtx.setFieldStateForSubDataSourceFields(dataEntityDataSourceStr(SalesOrderLineV2Entity, LogisticsPostalAddressBaseEntity), FieldState::Set);
    entityBuf.fieldState(fieldNum(SalesOrderLineV2Entity, DeliveryAddressLocationId), FieldState::NotSet); // Create new record.
    entityBuf.fieldState(fieldNum(SalesOrderLineV2Entity, FormattedDelveryAddress), FieldState::NotSet); // Not allow for write.
}

protected boolean shouldDefaultToAgreementPrice()
{
    // Indicates if default pricing (i.e. Trade Agreement) should be used or not if sales price is set to zero from the entity.
    return FeatureStateProvider::isFeatureEnabled(SalesZeroPriceAsABlankOnSalesQuoteLineToggle::instance());
}

