public static Query defaultCTQuery()
{
    Query salesQuotationHeaderQuery = new Query();

    var salesQuotationTableDS = salesQuotationHeaderQuery.addDataSource(tableNum(SalesQuotationTable));
    salesQuotationTableDS.addRange(fieldNum(SalesQuotationTable, QuotationType)).value(queryValue(QuotationType::Sales));

    SalesQuotationHeaderV2Entity::addChangeTrackingDataSourcesForSalesQuotationTableChildren(salesQuotationTableDS);

    return salesQuotationHeaderQuery;
}

/// <summary>
/// Updates the discount and totals for a given entity record.
/// </summary>
/// <param name = "_salesQuotationHeaderEntityRecord">The sales quotation header entity record.</param>
/// <returns>The list of related entities that need to be resynced.</returns>
/// <remarks>
/// This method is aimed to be used by Dual Write to trigger price and discount calculation updates.
/// See <c>RunDocumentAction</c> OData action on the <c>DualWriteProjectConfigurationEntity</c> entity.
/// </remarks>
public static List updatePrices(Common _salesQuotationHeaderEntityRecord)
{
    SalesQuotationHeaderCDSEntity salesQuotationHeaderEntityRecord = _salesQuotationHeaderEntityRecord;
    SalesCDSPrePricingAmountsTmp prePricingAmounts = SalesCDSPrePricingAmountsTmp::initPrePricingAmountsFromQuotationLines(salesQuotationHeaderEntityRecord.SalesQuotationNumber);
    List relatedRecords = new List(Types::Container);

    if (salesQuotationHeaderEntityRecord != null)
    {
        salesQuotationHeaderEntityRecord.calculatePrices();
        relatedRecords = salesQuotationHeaderEntityRecord.getRecordsToSync(prePricingAmounts);
    }

    return relatedRecords;
}

private void calculatePrices()
{
    this.updateLinesPriceDisc();

    SalesQuotationTable salesQuotationTable = SalesQuotationTable::find(this.SalesQuotationNumber, true);
    salesQuotationTable.updateFinalDisc();
    salesQuotationTable.updateMultiLineDisc();

    SalesQuotationTotalsCalculate::calculateTotalsForQuotation(this.SalesQuotationNumber);
}

private void updateLinesPriceDisc()
{
    SalesQuotationLine salesQuotationLine;

    while select forupdate salesQuotationLine
        where salesQuotationLine.QuotationId == this.SalesQuotationNumber
    {
        salesQuotationLine.setPriceDisc(salesQuotationLine.inventDim());
        salesQuotationLine.update();
    }
}

private List getRecordsToSync(SalesCDSPrePricingAmountsTmp _prePricingAmounts)
{
    List recordsToSync = new List(Types::Container);
    recordsToSync.addEnd([this.TableId, this.RecId]);

    SalesQuotationLineCDSEntity salesQuotationLineEntityRecord;
    Set salesQuotationLineRecords = new Set(Types::Int64);
    boolean dualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight =
        DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled();

    while select salesQuotationLineEntityRecord
            where salesQuotationLineEntityRecord.SalesQuotationNumber == this.SalesQuotationNumber
        exists join _prePricingAmounts // Only amount fields changed lines to be synced.
            where _prePricingAmounts.ReferenceNumber == salesQuotationLineEntityRecord.SalesQuotationNumber
                && _prePricingAmounts.LineCreationSequenceNumber == salesQuotationLineEntityRecord.LineCreationSequenceNumber
                && (_prePricingAmounts.LineAmount != salesQuotationLineEntityRecord.LineAmount
                    || _prePricingAmounts.LineDisc != salesQuotationLineEntityRecord.LineDiscountAmount
                    || _prePricingAmounts.LinePercent != salesQuotationLineEntityRecord.LineDiscountPercentage
                    || _prePricingAmounts.MultiLnDisc != salesQuotationLineEntityRecord.MultilineDiscountAmount
                    || _prePricingAmounts.MultiLnPercent != salesQuotationLineEntityRecord.MultilineDiscountPercentage
                    || _prePricingAmounts.SalesMarkup != salesQuotationLineEntityRecord.FixedPriceCharges
                    || _prePricingAmounts.SalesPrice != salesQuotationLineEntityRecord.SalesPrice
                    || _prePricingAmounts.TotalTaxAmount != salesQuotationLineEntityRecord.TotalTaxAmount
                    || _prePricingAmounts.TotalChargesAmount != salesQuotationLineEntityRecord.TotalChargesAmount
                    || _prePricingAmounts.TotalDiscountAmount != salesQuotationLineEntityRecord.TotalDiscountAmount)
    {
        recordsToSync.addEnd([salesQuotationLineEntityRecord.TableId, salesQuotationLineEntityRecord.RecId]);
        if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled()
            || dualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight)
        {
            salesQuotationLineRecords.add(salesQuotationLineEntityRecord.RecId);
        }
    }

    if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled()
        || dualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight)
    {
        recordsToSync.appendList(this.getSalesPriceOrLineAmountOrLineDiscPercentageOrLineDiscAmountNotSyncedRecords(salesQuotationLineEntityRecord.TableId, salesQuotationLineRecords));
    }

    return recordsToSync;
}

private List getSalesPriceOrLineAmountOrLineDiscPercentageOrLineDiscAmountNotSyncedRecords(TableId _salesQuotationLineEntityTableId, Set _recordsToSync)
{
    List records = new List(Types::Container);
    SalesQuotationLineNotSyncedWithCDSRecord salesQuotationLineNotSyncedWithCDSRecord;

    ttsbegin;

    while select forupdate salesQuotationLineNotSyncedWithCDSRecord
        where salesQuotationLineNotSyncedWithCDSRecord.QuotationId == this.SalesQuotationNumber
            && (salesQuotationLineNotSyncedWithCDSRecord.FieldsType == SalesNotSyncedWithCDSFieldsType::SalesPriceOrLineAmount
                    && DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
                || (salesQuotationLineNotSyncedWithCDSRecord.FieldsType == SalesNotSyncedWithCDSFieldsType::SalesLineDiscountPercentageOrLineDiscountAmount
                    && DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())

    {
        if (!_recordsToSync.in(salesQuotationLineNotSyncedWithCDSRecord.QuotationLineRefRecId))
        {
            records.addEnd([_salesQuotationLineEntityTableId, salesQuotationLineNotSyncedWithCDSRecord.QuotationLineRefRecId]);
        }

        salesQuotationLineNotSyncedWithCDSRecord.delete();
    }

    ttscommit;

    return records;
}

public static container getEntityDataSourceToFieldMapping(container mapping)
{
    mapping += [[dataEntityDataSourceStr(SalesQuotationHeaderCDSEntity, SalesQuotationHeaderV2Entity), tablenum(LogisticsPostalAddress), fieldstr(SalesQuotationHeaderCDSEntity, DeliveryPostalAddressRecId)],
                [dataEntityDataSourceStr(SalesQuotationHeaderCDSEntity, SalesQuotationHeaderV2Entity), tablenum(SalesQuotationCalculatedTotals), fieldstr(SalesQuotationHeaderCDSEntity, SalesQuotationCalculatedTotalsRecId)]];

    return mapping;
}

public static void jumpRefMethod(str _menuItemName, Common _stagingBuffer, Object _callerObj, DataAreaId _dataAreaId)
{
    changeCompany(_dataAreaId)
    {
        SalesQuotationHeaderCDSStaging stagingBuffer = _stagingBuffer;

        Args args = new Args();
        args.lookupValue(stagingBuffer.SalesQuotationNumber);

        MenuFunction::runCalled(_menuItemName, MenuItemType::Display, false, args);
    }
}

// This method implements the interface defined by <c>TradeEntitySpecificDeliveryAddress</c>
public RecId deliveryAddressPartyRecId()
{
    if (this.RequestingCustomerAccountNumber)
    {
        return CustTable::find(this.RequestingCustomerAccountNumber).Party;
    }
    else
    {
        return smmBusRelTable::find(this.ProspectId).Party;
    }
}

public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationHeaderCDSEntity, SalesQuotationHeaderV2Entity):
            if (this.SalesQuotationNumber)
            {
                _dataSourceCtx.getBuffer().skipNumberSequenceCheck(true);
            }
            break;
    }
    super(_entityCtx, _dataSourceCtx);
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationHeaderCDSEntity, SalesQuotationHeaderV2Entity):
            // Enable insert of records with blank values for mandatory field RequestedShippingDate
            if (_entityCtx.isDatabaseOperationInsert())
            {
                this.setBlankFieldStateToNotSet(fieldNum(SalesQuotationHeaderCDSEntity, RequestedShippingDate));
            }

            // Find existing logistic address location to avoid duplicate.
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                if (this.IsDeliveryAddressOrderSpecific)
                {
                    this.TradeEntitySpecificDeliveryAddress::resolveQuotationSpecificLocationId(this.SalesQuotationNumber);
                }
                else
                {
                    this.TradeEntitySpecificDeliveryAddress::resolvePartyAddressLocationId();
                }
            }

            break;
    }

    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationHeaderCDSEntity, SalesQuotationHeaderV2Entity):
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                SalesQuotationHeaderV2Entity entityBuf = _dataSourceCtx.getBuffer();

                entityBuf.IsCDSIntegration = NoYes::Yes;

                if (_entityCtx.isDatabaseOperationInsert())
                {
                    // Enable insert of logistics address when location id is blank but has a delivery address description
                    if (!entityBuf.DeliveryAddressLocationId && entityBuf.DeliveryAddressDescription)
                    {
                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesQuotationHeaderV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }
                }

                if (_entityCtx.isDatabaseOperationUpdate())
                {
                    // Enable insert of logistics address when location id is blank.
                    if (!entityBuf.DeliveryAddressLocationId
                        && (!SalesSkipAddressCreationIfAddressDescriptionBlankFlight::instance().isEnabled() || entityBuf.DeliveryAddressDescription))
                    {
                        // New location Id will be generated in TradeEntitySpecificDeliveryAddress.mapLogisticsPostalAddressBaseEntityDataSourceToEntity().
                        entityBuf.DeliveryAddressLocationId = entityBuf.orig().DeliveryAddressLocationId;

                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesQuotationHeaderV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }                        
                }

                entityBuf.fieldState(fieldNum(SalesQuotationHeaderV2Entity, IsDeliveryAddressOrderSpecific), FieldState::Set); // let new logistics location is associated with party.
            }
            break;
    }
}

private void allowAddressCreation(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesQuotationHeaderV2Entity entityBuf = _dataSourceCtx.getBuffer();

    // Update address fields state to Set. Otherwise, those fields value cannot be assigned to the new address.
    _dataSourceCtx.setFieldStateForSubDataSourceFields(dataEntityDataSourceStr(SalesQuotationHeaderV2Entity, DeliveryLogisticsPostalAddressBaseEntity), FieldState::Set);
    entityBuf.fieldState(fieldNum(SalesQuotationHeaderV2Entity, DeliveryAddressLocationId), FieldState::NotSet); // Create new record.
    entityBuf.fieldState(fieldNum(SalesQuotationHeaderV2Entity, FormattedDeliveryAddress), FieldState::NotSet); // Not allow for write.
}
