public static Query defaultCTQuery()
{
    boolean useReferencedData = !SalesParameters::skipSalesQuotationReferencedDataDuringChangeTracking();
    Query quotationLineCDSQuery = new Query();

    var salesQuotationLineDS = quotationLineCDSQuery.addDataSource(tableNum(SalesQuotationLine));
    salesQuotationLineDS.addRange(fieldNum(SalesQuotationLine, QuotationType)).value(queryValue(QuotationType::Sales));

    SalesQuotationLineV2Entity::addChangeTrackingDataSourcesForSalesQuotationLineChildren(salesQuotationLineDS);

    if (useReferencedData)
    {
        var inventTableDS = salesQuotationLineDS.addDataSource(tableNum(InventTable));
        inventTableDS.addLink(fieldNum(SalesQuotationLine, ItemId), fieldNum(InventTable, ItemId));
    }

    var lineTotalsDS = salesQuotationLineDS.addDataSource(tableNum(SalesQuotationLineCalculatedTotals));
    lineTotalsDS.joinMode(JoinMode::OuterJoin);
    lineTotalsDS.addLink(fieldNum(SalesQuotationLine, InventTransId), fieldNum(SalesQuotationLineCalculatedTotals, InventTransId));

    if (useReferencedData)
    {
        var QuotationTableDS = salesQuotationLineDS.addDataSource(tableNum(SalesQuotationTable));
        QuotationTableDS.addLink(fieldNum(SalesQuotationLine, QuotationId), fieldNum(SalesQuotationTable, QuotationId));
    }

    return quotationLineCDSQuery;
}

