        SysComputedColumn::returnField(tableStr(SalesQuotationLineCDSEntity), dataEntityDataSourceStr(SalesQuotationLineCDSEntity, EcoResProductTranslationToSystemLanguage), fieldStr(EcoResProductTranslation, Name)),
        SysComputedColumn::returnField(tableStr(SalesQuotationLineCDSEntity), dataEntityDataSourceStr(SalesQuotationLineCDSEntity, EcoResProductMasterTranslationToSystemLanguage), fieldStr(EcoResProductTranslation, Name)));
}

/// <summary>
/// Calculate a boolean that indicates whether the totals have been calculated before or not.
/// </summary>
/// <returns>The value of <c>AreTotalsCalculated</c> field.</returns>
public static str areTotalsComputedColumnDefinition()
{
    return SysComputedColumn::if(SysComputedColumn::isNullExpression(SysComputedColumn::comparisonField(tableStr(SalesQuotationLineCDSEntity), dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineCalculatedTotals), fieldStr(SalesQuotationLineCalculatedTotals, RecId))),
        SysComputedColumn::returnLiteral(NoYes::No),
        SysComputedColumn::returnLiteral(NoYes::Yes));
}

/// <summary>
/// Calculate the Total discount amount by subtracting the fixed price charge from the total discount.
/// </summary>
/// <returns>The value of <c>TotalDiscountAmount</c> field.</returns>
/// <remarks>
/// On entity import the TotalDiscountAmount for a full line is divided by line quantity.
/// Due to rounding of the result there may be an amount difference once the result is again multiplied by line quantity.
/// This difference is stored in FixedPriceCharges on import.
/// </remarks>
public static str totalDiscountAmountComputedColumnDefinition()
{
    return SysComputedColumn::subtract(
        SysComputedColumn::returnField(tableStr(SalesQuotationLineCDSEntity), dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineCalculatedTotals), fieldStr(SalesQuotationLineCalculatedTotals, TotalDiscountAmount)),
        SysComputedColumn::returnField(tableStr(SalesQuotationLineCDSEntity), dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationlineEntity), fieldStr(SalesQuotationlineV2Entity, FixedPriceCharges)));
}

public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineEntity):
            this.defaultItemIdAndDimensions();
            break;
