// This method implements the interface defined by <c>TradeEntitySpecificDeliveryAddress</c>
public RecId deliveryAddressPartyRecId()
{
    SalesQuotationTable salesQuotationTable = SalesQuotationTable::find(this.SalesQuotationNumber);

    if (salesQuotationTable.CustAccount)
    {
        return CustTable::find(salesQuotationTable.CustAccount).Party;
    }
    else
    {
        return smmBusRelTable::find(salesQuotationTable.BusRelAccount).Party;
    }
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret, isLineDiscountSet;        
    SalesPrice salesPrice;
    LineAmount lineAmount;
    real lineDiscountPercentage;
    LineAmount lineDiscountAmount;
    SalesQuotationLineV2Entity quotationLineEntity;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineEntity):
            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                quotationLineEntity = _dataSourceCtx.getBuffer();
                salesPrice = quotationLineEntity.SalesPrice;
                lineAmount = quotationLineEntity.LineAmount;
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                quotationLineEntity = _dataSourceCtx.getBuffer();
                lineDiscountPercentage = quotationLineEntity.LineDiscountPercentage;
                lineDiscountAmount = quotationLineEntity.LineDiscountAmount;
            }

            isLineDiscountSet = this.insertLineDiscount(_dataSourceCtx);
            break;
    }

    ret = super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineEntity):
            if (isLineDiscountSet)
            {
                this.updateSalesMarkupWithRoundingDifference(_dataSourceCtx);
            }

            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesPriceOrLineAmountOutOfSync(quotationLineEntity, salesPrice, lineAmount);
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesLineDiscountPercentageOrLineDiscountAmountOutOfSync(quotationLineEntity, lineDiscountPercentage, lineDiscountAmount);
            }

            break;
    }
    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret, isLineDiscountSet;
    SalesPrice salesPrice;
    LineAmount lineAmount;
    real lineDiscountPercentage;
    LineAmount lineDiscountAmount;
    SalesQuotationLineV2Entity quotationLineEntity;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineEntity):
            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                quotationLineEntity = _dataSourceCtx.getBuffer();
                salesPrice = quotationLineEntity.SalesPrice;
                lineAmount = quotationLineEntity.LineAmount;
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                quotationLineEntity = _dataSourceCtx.getBuffer();
                lineDiscountPercentage = quotationLineEntity.LineDiscountPercentage;
                lineDiscountAmount = quotationLineEntity.LineDiscountAmount;
            }

            isLineDiscountSet = this.updateLineDiscount(_dataSourceCtx);
            break;
    }

    ret = super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineEntity):
            if (isLineDiscountSet)
            {
                this.updateSalesMarkupWithRoundingDifference(_dataSourceCtx);
            }

            if (DualWriteTrackSalesPriceOrLineAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesPriceOrLineAmountOutOfSync(quotationLineEntity, salesPrice, lineAmount);
            }

            if (DualWriteLineDiscountPercentageOrLineDiscountAmountOutOfSyncFlight::instance().isEnabled())
            {
                this.checkSalesLineDiscountPercentageOrLineDiscountAmountOutOfSync(quotationLineEntity, lineDiscountPercentage, lineDiscountAmount);
            }

            break;
    }
    return ret;
}

private void checkSalesPriceOrLineAmountOutOfSync(SalesQuotationLineV2Entity _quotationLineEntity, SalesPrice _originalSalesPrice, LineAmount _originalLineAmount)
{
    if (_quotationLineEntity.SalesPrice != _originalSalesPrice || _quotationLineEntity.LineAmount != _originalLineAmount)
    {
        SalesQuotationLineNotSyncedWithCDSRecord::insertRecord(_quotationLineEntity.SalesQuotationNumber, _quotationLineEntity.RecId, SalesNotSyncedWithCDSFieldsType::SalesPriceOrLineAmount);
    }
}

private void checkSalesLineDiscountPercentageOrLineDiscountAmountOutOfSync(SalesQuotationLineV2Entity _quotationLineEntity, real _originalLineDiscountPercentage, LineAmount _originalLineDiscountAmount)
{
    if (_quotationLineEntity.LineDiscountPercentage != _originalLineDiscountPercentage || _quotationLineEntity.LineDiscountAmount != _originalLineDiscountAmount)
    {
        SalesQuotationLineNotSyncedWithCDSRecord::insertRecord(_quotationLineEntity.SalesQuotationNumber, _quotationLineEntity.RecId, SalesNotSyncedWithCDSFieldsType::SalesLineDiscountPercentageOrLineDiscountAmount);
    }
}

private boolean insertLineDiscount(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (this.TotalDiscountAmount != 0 && this.RequestedSalesQuantity != 0)
    {
        this.setLineDiscount(_dataSourceCtx, this.RequestedSalesQuantity);
        return true;
    }
    return false;
}

private boolean updateLineDiscount(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (SalesCDSUpdateLineDiscountOnlyWhenTotalDiscountAmountModifiedFlight::instance().isEnabled()
        && !this.isFieldModified(fieldNum(SalesQuotationLineCDSEntity, TotalDiscountAmount)))
    {
        return false;
    }

    boolean isQuantitySet = this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, RequestedSalesQuantity));
    if (this.TotalDiscountAmount != 0)
    {
        if (isQuantitySet && this.RequestedSalesQuantity != 0)
        {
            this.setLineDiscount(_dataSourceCtx, this.RequestedSalesQuantity);
            return true;
        }
        else
        {
            SalesQuotationLine line = SalesQuotationLine::findLineCreationSequence(this.SalesQuotationNumber, this.LineCreationSequenceNumber);
            if (line.SalesQty != 0)
            {
                this.setLineDiscount(_dataSourceCtx, line.SalesQty);
                return true;
            }
        }
    }
    return false;
}

private void updateSalesMarkupWithRoundingDifference(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesQuotationLineV2Entity quotationLineEntity = _dataSourceCtx.getBuffer();
    SalesQuotationLine line = SalesQuotationLine::findInventTransId(quotationLineEntity.InventoryLotId, true);
    var calculatedDiscount = CurrencyExchange::round(line.SalesQty * line.LineDisc, line.CurrencyCode);
    if (this.TotalDiscountAmount != calculatedDiscount)
    {
        line.SalesMarkup = calculatedDiscount - this.TotalDiscountAmount;
        line.LineAmount = line.calcLineAmountForced();
        line.update();
    }
}

private void setLineDiscount(DataEntityDataSourceRuntimeContext _dataSourceCtx, SalesOrderedQty _qty)
{
    if (_qty != 0)
    {
        SalesQuotationLineV2Entity quotationLineEntity = _dataSourceCtx.getBuffer();
        quotationLineEntity.LineDiscountAmount = CurrencyExchange::round((this.TotalDiscountAmount / _qty), Ledger::accountingCurrency());
        quotationLineEntity.FixedPriceCharges = 0;
        _dataSourceCtx.setBuffer(quotationLineEntity);
    }
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineEntity):
            // Enable insert of records with blank values for mandatory field RequestedShippingDate
            if (_entityCtx.isDatabaseOperationInsert())
            {
                this.setBlankFieldStateToNotSet(fieldNum(SalesQuotationLineCDSEntity, RequestedShippingDate));
            }

            // Find existing logistic address location to avoid duplicate.
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                if (this.IsDeliveryAddressOrderSpecific)
                {
                    this.TradeEntitySpecificDeliveryAddress::resolveQuotationSpecificLocationId(this.SalesQuotationNumber);
                }
                else
                {
                    this.TradeEntitySpecificDeliveryAddress::resolvePartyAddressLocationId();
                }
            }

            if (DualWriteCalculateLineAmountFlight::instance().isEnabled()
                && !SalesParameters::find().SalesEntityCalculateLineAmount
                && _entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update
                && !this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, LineAmount))
                && !this.hasLineAmountDependentFieldsSet())
            {
                SalesQuotationLineV2Entity entityBuf = _dataSourceCtx.getBuffer() as SalesQuotationLineV2Entity;
                entityBuf.fieldState(fieldNum(SalesQuotationLineV2Entity, LineAmount), FieldState::Set);
            }

            break;
    }

    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(SalesQuotationLineCDSEntity, SalesQuotationLineEntity):
            if (_entityCtx.isDatabaseOperationInsertOrUpdate())
            {
                SalesQuotationLineV2Entity entityBuf = _dataSourceCtx.getBuffer();

                entityBuf.IsCDSIntegration = NoYes::Yes;

                if (this.SalesPrice == 0 && this.shouldDefaultToAgreementPrice())
                {
                    entityBuf.fieldState(fieldNum(SalesQuotationLineV2Entity, SalesPrice), FieldState::NotSet);
                }
                if (_entityCtx.isDatabaseOperationInsert())
                {
                    // Enable insert of logistics address when location id is blank but has a delivery address description
                    if (!entityBuf.DeliveryAddressLocationId && entityBuf.DeliveryAddressDescription)
                    {
                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesQuotationLineV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }
                }
                else if (_entityCtx.isDatabaseOperationUpdate())
                {
                    // Enable insert of logistics address when location id is blank.
                    if (!entityBuf.DeliveryAddressLocationId
                        && (!SalesSkipAddressCreationIfAddressDescriptionBlankFlight::instance().isEnabled() || entityBuf.DeliveryAddressDescription))
                    {
                        // New location Id will be generated in TradeEntitySpecificDeliveryAddress.mapLogisticsPostalAddressBaseEntityDataSourceToEntity().
                        entityBuf.DeliveryAddressLocationId = entityBuf.orig().DeliveryAddressLocationId;

                        this.allowAddressCreation(_dataSourceCtx);
                    }
                    else
                    {
                        entityBuf.fieldState(fieldNum(SalesQuotationLineV2Entity, DeliveryAddressLocationId), FieldState::Set); // prevent creating new record.
                    }
                }

                entityBuf.fieldState(fieldNum(SalesQuotationLineV2Entity, IsDeliveryAddressOrderSpecific), FieldState::Set); // let new logistics location is associated with party.
            }
            break;
    }
}

private void allowAddressCreation(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesQuotationLineV2Entity entityBuf = _dataSourceCtx.getBuffer();

    // Update address fields state to Set. Otherwise, those fields value cannot be assigned to the new address.
    _dataSourceCtx.setFieldStateForSubDataSourceFields(dataEntityDataSourceStr(SalesQuotationLineV2Entity, DeliveryLogisticsPostalAddressBaseEntity), FieldState::Set);
    entityBuf.fieldState(fieldNum(SalesQuotationLineV2Entity, DeliveryAddressLocationId), FieldState::NotSet); // Create new record.
    entityBuf.fieldState(fieldNum(SalesQuotationLineV2Entity, FormattedDeliveryAddress), FieldState::NotSet); // Not allow for write.
}

protected boolean shouldDefaultToAgreementPrice()
{
    // Indicates if default pricing (i.e. Trade Agreement) should be used or not if sales price is set to zero from the entity.
    return FeatureStateProvider::isFeatureEnabled(SalesZeroPriceAsABlankOnSalesQuoteLineToggle::instance());
}

private boolean hasLineAmountDependentFieldsSet()
{
    return this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, SalesPrice))
        || this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, RequestedSalesQuantity))
        || this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, LineDiscountAmount))
        || this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, LineDiscountPercentage))
        || this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, MultilineDiscountAmount))
        || this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, MultilineDiscountPercentage))
        || this.isFieldSet(fieldNum(SalesQuotationLineCDSEntity, FixedPriceCharges));
}

