public static TaxCodeEntity find(TaxCode _taxCode, boolean _update = false, ConcurrencyModel _sync = ConcurrencyModel::Auto)
{
    TaxCodeEntity entity;

    entity.selectForUpdate(_update);

    if (_update && ConcurrencyModel::Auto != _sync)
    {
        entity.concurrencyModel(_sync);
    }

    select firstonly entity
        where entity.TaxCode == _taxCode;

    return entity;
}

public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    if (_dataSourceCtx.name() == dataEntityDataSourceStr(TaxCodeEntity, TaxationCodeTable_BR))
    {
        TaxationCodeTable_BR taxationCode;

        select firstonly taxationCode
            where   taxationCode.TaxType        == this.BrazilTaxType
                &&  taxationCode.TaxationCode   == this.TaxationCode
                &&  (this.FiscalValue == TaxFiscalValue_BR::Blank || taxationCode.FiscalValue == this.FiscalValue);

        ret = taxationCode;
    }
    else
    {
        ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

