    TaxCodeEntityStaging::resolveImportSequence(_dmfDefinitionGroupExecution);
}

/// <summary>
/// Validate field for <c>TaxCodeEntity</c>
/// </summary>
/// <param name = "_fieldIdToCheck">Field id to check</param>
/// <returns>Returns true if the validation pass, otherwise, returns false.</returns>
public boolean validateField(FieldId _fieldIdToCheck)
{         
    this.initInternalUnmappedFieldsBeforeFieldValidation();
    return super(_fieldIdToCheck);        
}

/// <summary>
/// Ensures unmapped fields are initialized before fields validation when importing data or publishing changes from excel file, validation logic of some fields depend on unmapped fields.
/// </summary>
/// <remarks>
/// TaxCodeEntity is a data entity to export/import sales tax codes, TaxTable is the table to store sales tax codes and has not ReportLayout field, 
/// but TaxCodeEntity has ReportLayout field and no mapping defined for this field. When exporting sales tax codes to an excel file, 
/// ReportLayout field is empty and not stored in the excel file. When importing sales tax codes from an excel file, validateField() method of 
/// TaxCodeEntity is called for each field, ReportLayout is not stored in the excel file, so value of this field is default to Default layout, 
/// validation for foreign keys defined for ReportingCode* fields of TaxCodeEntity will fail if ReportingCode* is a reporting code with non-default report layout,
/// since the foreign keys contains ReportLayout and ReportingCode* fields. Data import/export management framework has not a suitable callback method to handle this, 
/// so we added this method and call it in validateField() method of TaxCodeEntity to initialize ReportLayout to avoid foreign key validation failure.
/// </remarks>
private void initInternalUnmappedFieldsBeforeFieldValidation()
{
    if (!this.ReportLayout && this.TaxPeriodId)
    {
        this.ReportLayout = TaxAuthorityEntity::find(TaxPeriodEntity::find(this.TaxPeriodId).TaxAuthorityId).ReportLayout;
    }
}
