Method.Source:
/// <summary>
/// Find <c>TransTaxInformationEntity</c> entity by data source.
/// </summary>
/// <param name = "_entityCtx">The data entity context.</param>
/// <param name = "_dataSourceCtx">The data source context.</param>
/// <returns></returns>
public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    ret = super(_entityCtx, _dataSourceCtx);
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(TransTaxInformationEntity, TaxInformation):
            ret = TaxInformation_IN::findByLocationAndName(
                LogisticsLocation::findByLocationId(this.CompanyLocation).RecId, 
                this.CompanyTaxInformation);
            break;
        case dataEntityDataSourceStr(TransTaxInformationEntity, CustomerTaxInformation):
            ret = TaxInformation_IN::findByLocationAndName(
                LogisticsLocation::findByLocationId(this.CustomerLocation).RecId,
                this.CustomerTaxInformation);
            break;
        case dataEntityDataSourceStr(TransTaxInformationEntity, VendorTaxInformation):
            ret = TaxInformation_IN::findByLocationAndName(
                LogisticsLocation::findByLocationId(this.VendorLocation).RecId,
                this.VendorTaxInformation);
            break;
        case dataEntityDataSourceStr(TransTaxInformationEntity, BankTaxInformation):
            ret = TaxInformation_IN::findByLocationAndName(
                LogisticsLocation::findByLocationId(this.BankLocation).RecId,
                this.BankTaxInformation);
            break;
    }

    return ret;
}

/// <summary>
/// Map the data entity to the data source.
/// </summary>
/// <param name = "_entityCtx">The data entity context.</param>
/// <param name = "_dataSourceCtx">The data source context.</param>
public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    if (_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
            || _entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update)
    {
        switch (_dataSourceCtx.name())
        {
            case dataEntityDataSourceStr(TransTaxInformationEntity, TransTaxInformation):
                TransTaxInformation transTaxInformation = _dataSourceCtx.getBuffer();
                if (this.CompanyTaxInformation)
                {
                    TaxInformation_IN taxinformation = TaxInformation_IN::findByLocationAndName(
                    LogisticsLocation::findByLocationId(this.CompanyLocation).RecId,
                    this.CompanyTaxInformation);

                    if (taxinformation.RecId)
                    {
                        transTaxInformation.TaxInformation = taxinformation.RecId;
                        TransTaxInformationHelper::initFromTaxInformation(transTaxInformation);
                    }
                }
                break;

            case dataEntityDataSourceStr(TransTaxInformationEntity, TransitDocumentTransTaxInfoRelation):
                TransitDocumentTransTaxInfoRelation_IN transitDocumentTransTaxInfoRelation = _dataSourceCtx.getBuffer();

                transitDocumentTransTaxInfoRelation.TransitDocumentType = TransitDocumentType_IN::findType(
                    tableNum(TransTaxInformation),
                    fieldNum(TransTaxInformation, Type),
                    this.Type).RecId;
                this.markTaxDocumentTaxStatus(transitDocumentTransTaxInfoRelation);
                    break;
        }
    }

}

private void markTaxDocumentTaxStatus(TransitDocumentTransTaxInfoRelation_IN _transitDocumentTransTaxInfoRelation)
{
    TaxableDocumentDescriptor bundler;

    switch (_transitDocumentTransTaxInfoRelation.TransactionTableId)
    {
        case tableNum(LedgerJournalTrans):
            bundler = TaxableDocumentDescriptor::getTaxDocumentdescriptor(classNum(TaxableDocumentDescriptorGeneralJournal), LedgerJournalTrans::findRecId(_transitDocumentTransTaxInfoRelation.TransactionRecId, false));
            break;
    }
    if (bundler && !bundler.skipTaxDocument())
    {
        ITaxableDocument taxableDocument = TaxableDocumentObject::construct(bundler);
        TaxBusinessService::markTaxDocumentTaxStatus(taxableDocument, TaxDocumentCalculationLevel::Dirty);
    }
}

/// <summary>
/// Update the data entity data source.
/// </summary>
/// <param name = "_entityCtx">The data entity context.</param>
/// <param name = "_dataSourceCtx">The data source context.</param>
/// <returns>return boolean</returns>
public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;
    this.initFromTaxInformation(_entityCtx, _dataSourceCtx);
    ret = super(_entityCtx, _dataSourceCtx);

    return ret;
}

/// <summary>
/// Insert the data entity data source.
/// </summary>
/// <param name = "_entityCtx">The data entity context.</param>
/// <param name = "_dataSourceCtx">The data source context.</param>
/// <returns>return boolean</returns>
public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    boolean ret;
    this.initFromTaxInformation(_entityCtx, _dataSourceCtx);
    ret = super(_entityCtx, _dataSourceCtx);

    return ret;
}

private void initFromTaxInformation(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    #ISOCountryRegionCodes

    if (_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
            || _entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update)
    {
        if (_dataSourceCtx.name() == dataEntityDataSourceStr(TransTaxInformationEntity, TransTaxInformation))
        {
            TransTaxInformation transTaxInformation = _dataSourceCtx.getBuffer();
            if (this.CompanyTaxInformation)
            {
                TaxInformation_IN taxinformation = TaxInformation_IN::findByLocationAndName(
                                                        LogisticsLocation::findByLocationId(this.CompanyLocation).RecId,
                                                        this.CompanyTaxInformation);

                if (taxinformation.RecId)
                {
                    transTaxInformation.TaxInformation = taxinformation.RecId;
                    TransTaxInformationHelper::initFromTaxInformation(transTaxInformation);
                }
            }

            if (this.TransactionRecId && this.TransactionTableId && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
            {
                TaxModelTaxable taxModelTaxable = TaxModelDocLineFactory::getTaxModelDocLineInstance(DataExpUtil::find(this.TransactionTableId, this.TransactionRecId), false);
                if (taxModelTaxable)
                {
                    CustVendTable custVendTable   = taxModelTaxable.getPartyTable();
                    if (custVendTable.TableId == tableNum(VendTable))
                    {
                        TaxInformationVendTable_IN taxInformationVendTable_IN = TaxInformationVendTable_IN::findByVendTable(custVendTable.AccountNum);
                        transTaxInformation.TaxWithholdNatureOfAssessee = taxInformationVendTable_IN.NatureOfAssessee;
                    }
                    else if (custVendTable.TableId == tableNum(CustTable))
                    {
                        TaxInformationCustTable_IN taxInformationCustTable_IN = TaxInformationCustTable_IN::findByCustTable(custVendTable.AccountNum);
                        transTaxInformation.TaxWithholdNatureOfAssessee = taxInformationCustTable_IN.NatureOfAssessee;
                    }
                }
            }
        }
    }
}
