private void initLedgerJournalTrans(LedgerJournalTrans _ledgerJournalTrans)
{
    // When editing through the entity, always delay tax calculation to avoid
    // a O(n^2) tax calculation while importing multiple lines. The journal will
    // automatically calculate taxes later at posting time or when necessary.
    _ledgerJournalTrans.DelayTaxCalculation = NoYes::Yes;

    _ledgerJournalTrans.markAsApprovedByCurrentUser();
    _ledgerJournalTrans.TransactionType = LedgerTransType::Payment;

    if (!_ledgerJournalTrans.SettleVoucher)
    {
        _ledgerJournalTrans.SettleVoucher = VendParameters::find().AutoSettle ? SettlementType::OpenTransact : SettlementType::None;
    }

    _ledgerJournalTrans.AccountType = LedgerJournalACType::Vend;

    this.removeDimensionDelimiter();

    if (!_ledgerJournalTrans.CustVendBankAccountId && CustVendPaymImportInheritDefaultCustVendBankAccountFlight::instance().isEnabled())
    {
        _ledgerJournalTrans.CustVendBankAccountId = VendTable::find(this.AccountDisplayValue).BankAccount;
    }
}

private void updateJounalBatchNumber(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update
        && this.NewJournalBatchNumber != ""
        && this.NewJournalBatchNumber != this.JournalBatchNumber)
    {
        LedgerJournalTable newLedgerJournalTable = LedgerJournalTable::find(this.NewJournalBatchNumber);
        LedgerJournalTable currentLedgerJournalTable = LedgerJournalTable::find(this.JournalBatchNumber);

        if (newLedgerJournalTable.JournalType != currentLedgerJournalTable.JournalType)
        {
            throw error(strFmt("@CashManagement:WrongJournalType", newLedgerJournalTable.JournalName, currentLedgerJournalTable.JournalType));
        }

        if (newLedgerJournalTable.NumberSequenceTable != currentLedgerJournalTable.NumberSequenceTable)
        {
            NumberSeq numberSeq = NumberSeq::newGetVoucherFromId(newLedgerJournalTable.NumberSequenceTable);
            this.Voucher = numberSeq.voucher();
        }

        this.JournalBatchNumber = this.NewJournalBatchNumber;
        this.LineNumber = LedgerJournalTrans::lastLineNum(this.NewJournalBatchNumber) + 1;

    }
}

private void saveRemittanceAddress(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    DataEntityDataSourceRuntimeContext addressDataSourceContext = _entityCtx.getRuntimeContextByName(dataEntityDataSourceStr(VendorPaymentJournalLineEntity, LogisticsPostalAddressBaseEntity));

    if (this.AccountType == LedgerJournalACType::Vend
        && this.isRequiredAddressFieldSet())
    {
        LogisticsPostalAddressBaseEntity postalAddressEntity = addressDataSourceContext.getBuffer();
        DirPartyRecId partyRecId = this.findPartyRecId();

        if (this.RemittanceLocationId != '')
        {
            // Use existing address
            LogisticsPostalAddressBaseEntity postalAddressEntityLookup;
            DirPartyLocation dirPartyLocation;

            select firstonly postalAddressEntityLookup
                where postalAddressEntityLookup.LocationId == this.RemittanceLocationId
                exists join dirPartyLocation
                    where dirPartyLocation.Location == postalAddressEntityLookup.LocationRecId
                        && dirPartyLocation.Party == partyRecId;

            if (!postalAddressEntityLookup)
            {
                throw error(strFmt("@CashManagement:RemittanceAddressNotFound", this.RemittanceLocationId, this.AccountDisplayValue));
            }

            addressDataSourceContext.setBuffer(postalAddressEntityLookup);
        }
        else
        {
            // Create new address
            postalAddressEntity.RecId = 0;
            postalAddressEntity.write();
            DirPartyLocation partyLocation = DirPartyLocation::create(partyRecId, postalAddressEntity.LocationRecId);
            if (partyLocation.RecId != 0)
            {
                container roles = [LogisticsLocationRole::findBytype(LogisticsLocationRoleType::RemitTo).RecId];
                DirPartyLocationRole::createPartyLocationRoles(partyLocation.RecId, roles, false);
            }
        }

        // Update journal line
        LedgerJournalTrans ledgerJournalTrans =  _dataSourceCtx.getBuffer();
        postalAddressEntity = addressDataSourceContext.getBuffer();

        ledgerJournalTrans.RemittanceLocation = postalAddressEntity.LocationRecId;
        ledgerJournalTrans.RemittanceAddress = LogisticsPostalAddress::addressRecIdFromLocationRecId(postalAddressEntity.LocationRecId);
    }

    // Set the datasource as saved.
    addressDataSourceContext.setDataSaved(true);
    addressDataSourceContext.setDatabaseOperation(DataEntityDatabaseOperation::None);
}

private boolean isRequiredAddressFieldSet()
{
    // When imported fields are present, but blank, the values are defaulted and isFieldSet returns true.
    // Verify that if the field is set, it is not empty.
    return (this.isFieldSet(fieldNum(VendorPaymentJournalLineEntity, RemittanceLocationId)) && this.RemittanceLocationId != '')
        || (this.isFieldSet(fieldNum(VendorPaymentJournalLineEntity, RemittanceAddressDescription)) && this.RemittanceAddressDescription != '')
        || (this.isFieldSet(fieldNum(VendorPaymentJournalLineEntity, RemittanceAddressCountry)) && this.RemittanceAddressCountry != '');
}

public static str vendorName()
{
    str vendTableName = tableStr(VendTable);
    str vendTableAlias = 'VT';
    str vendAccountNumberColumn = fieldStr(VendTable, AccountNum);
    str vendPartyColumn = fieldStr(VendTable, Party);
    str partitionColumn = fieldStr(VendTable, Partition);
    str vendDataAreaIdColumn = fieldStr(VendTable, DATAAREAID);
    str dirPartyTableName = tableStr(DirPartyTable);
    str dirPartyTableAlias = 'DPT';
    str dirPartyRecIdColumn = fieldStr(DirPartyTable, RecId);
    str nameColumn = fieldStr(DirPartyTable, Name);
    str accountDAVCViewAlias = 'T4';
    str accountDAVCAccountValueColumn = fieldStr(DimensionCombinationEntity, AccountValue);
    str ledgerJournalTransTableAlias = 'T1';
    str accountCompanyColumn = fieldStr(LedgerJournalTrans, Company);

    str vendorNameSelect = strFmt('SELECT %1.%2 FROM %3 %4 CROSS JOIN %5 %1 WHERE %6.%7 = %4.%8 AND %9.%10 = %4.%11 AND %9.%12 = %4.%12 AND %4.%13 = %1.%14 AND %4.%12 = %1.%12',
        dirPartyTableAlias,
        nameColumn,
        vendTableName,
        vendTableAlias,
        dirPartyTableName,
        accountDAVCViewAlias,
        accountDAVCAccountValueColumn,
        vendAccountNumberColumn,
        ledgerJournalTransTableAlias,
        accountCompanyColumn,
        vendDataAreaIdColumn,
        partitionColumn,
        vendPartyColumn,
        dirPartyRecIdColumn);

    return vendorNameSelect;
}

/// <summary>
/// To remove the escape character delimeter used in display values.
/// </summary>
private void removeDimensionDelimiter()
{
    List displayValue;
    ListIterator iterator;

    displayValue = DimensionResolver::splitByDimensionIntegrationDelimiter(this.AccountDisplayValue);
    iterator = new ListIterator(displayValue);
    this.AccountDisplayValue = iterator.value();
}

public static Query defaultCTQuery()
{
    Query query = new Query();

    QueryBuildDataSource qbdsLedgerJournalTrans = query.addDataSource(tableNum(LedgerJournalTrans));

    LogisticsPostalAddressBaseEntity::addChangeTrackingDataSourcesForAddressBase(qbdsLedgerJournalTrans, fieldNum(LedgerJournalTrans, RemittanceLocation), false);

    QueryBuildDataSource qbdsNACHA = qbdsLedgerJournalTrans.addDataSource(tableNum(CustVendNACHAIATInfoTable_US));
    qbdsNACHA.joinMode(JoinMode::OuterJoin);
    qbdsNACHA.addLink(fieldNum(LedgerJournalTrans, RecId), fieldNum(CustVendNACHAIATInfoTable_US, LedgerJournalTransRecId));

    QueryBuildDataSource qbdsCustVendPDCRegister = qbdsLedgerJournalTrans.addDataSource(tableNum(CustVendPDCRegister));
    qbdsCustVendPDCRegister.joinMode(JoinMode::OuterJoin);
    qbdsCustVendPDCRegister.addLink(fieldNum(LedgerJournalTrans, RecId), fieldNum(CustVendPDCRegister, LedgerJournalTrans));

    QueryBuildDataSource qbdsLedgerJournalTrans_W = qbdsLedgerJournalTrans.addDataSource(tableNum(LedgerJournalTrans_W));
    qbdsLedgerJournalTrans_W.joinMode(JoinMode::OuterJoin);
    qbdsLedgerJournalTrans_W.addLink(fieldNum(LedgerJournalTrans, RecId), fieldNum(LedgerJournalTrans_W, LedgerJournalTrans));

    QueryBuildDataSource qbdsPaymentMessageTable_DEDTAZV = qbdsLedgerJournalTrans.addDataSource(tableNum(PaymentMessageTable_DEDTAZV));
    qbdsPaymentMessageTable_DEDTAZV.joinMode(JoinMode::OuterJoin);
    qbdsPaymentMessageTable_DEDTAZV.addLink(fieldNum(LedgerJournalTrans, RecId), fieldNum(PaymentMessageTable_DEDTAZV, LedgerJournalTrans));

    return query;
}

private void resolveFintagsWithContext(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    VendorPaymentJournalLineEntity entity = _entityCtx.getEntityRecord();
    LedgerJournalTrans ledgerJournalTrans = _dataSourceCtx.getBuffer();

    this.resolveFinTags(entity, ledgerJournalTrans);
}
