private static str computeValidateColumn(VendPaymentValidate _vendPaymentValidate)
{
    str vendPaymentValidateSqlValue = queryValue(enum2int(_vendPaymentValidate));

    str sql = 'SELECT COUNT(*) FROM ' + tableStr(VendPaymMethodVal) + ' VPMV ';
    sql += 'WHERE VPMV.' + fieldStr(VendPaymMethodVal, VendPaymentMode) + '='
        + SysComputedColumn::returnField(tablestr(VendorPaymentMethodEntity), dataEntityDataSourceStr(VendorPaymentMethodEntity, VendPaymModeTable), fieldstr(VendPaymModeTable, PaymMode));
    sql += ' AND VPMV.' + fieldStr(VendPaymMethodVal, VendPaymValidate) + '=' +  vendPaymentValidateSqlValue;

    // Add ranges for Partition and DataAreaId
    sql += ' AND VPMV.'+ fieldStr(VendPaymMethodVal, Partition) + '='
        + SysComputedColumn::returnField(tablestr(VendorPaymentMethodEntity), dataEntityDataSourceStr(VendorPaymentMethodEntity, VendPaymModeTable), fieldstr(VendPaymModeTable, Partition));
    sql += ' AND VPMV.' + fieldStr(VendPaymMethodVal, DATAAREAID) + '='
        + SysComputedColumn::returnField(tablestr(VendorPaymentMethodEntity), dataEntityDataSourceStr(VendorPaymentMethodEntity, VendPaymModeTable), fieldstr(VendPaymModeTable, dataAreaId));

    return sql;
}

