public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    if ((_entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Insert
        || _entityCtx.getDatabaseOperation() == DataEntityDatabaseOperation::Update)
        && _dataSourceCtx.name() == dataentitydatasourcestr(VendorPaymentMethodEntity, VendPaymModeTable))
    {
        this.updatePaymentControl();
        this.updatePaymentAttributes();
        this.resolvePaymentFileClassIds(_dataSourceCtx.getBuffer());

        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]))
        {
            VendPaymModeTable vendPaymModeTable = _dataSourceCtx.getBuffer();
            vendPaymModeTable.DimensionAttributeSet = DimensionEnumerationResolver::newResolver(this.DimensionAttributeDisplayValue, enumStr(NoYes)).resolve();
        }
    }
    else if (_dataSourceCtx.name() == dataEntityDataSourceStr(VendorPaymentMethodEntity, PaymentFormatCodeSets_W))
    {
        PaymentFormatCodeSets_W paymentFormatCodeSets;
        PaymentFormatCodeSets_W paymentFormatCodeSetsSource = _dataSourceCtx.getBuffer();

        select firstonly RecId from paymentFormatCodeSets
            where paymentFormatCodeSets.Code == paymentFormatCodeSetsSource.Code
                && paymentFormatCodeSets.Type == PaymentFormatCodeType_W::ChargeBearer;

        this.ChargeBearer = paymentFormatCodeSets.RecId;
    }
}

private void resolvePaymentFileClassIds(VendPaymModeTable _paymentMethod)
{
    if (this.ExportFormatClassName != this.orig().ExportFormatClassName)
    {
        _paymentMethod.ClassId = className2Id(this.ExportFormatClassName);

        if (_paymentMethod.ClassId
            && SysDictClass::isSuperclass(_paymentMethod.classId, classnum(VendOutPaym)))
        {
            VendPaymFormat::addFormats(BankFormat::Export, [_paymentMethod.ClassId]);
        }
    }

    if (this.RemittanceFormatClassName != this.orig().RemittanceFormatClassName)
    {
        _paymentMethod.ClassIdRemittance = className2Id(this.RemittanceFormatClassName);

        if (_paymentMethod.ClassIdRemittance
            && SysDictClass::isSuperclass(_paymentMethod.ClassIdRemittance, classnum(VendOutPaymRemittance)))
        {
            VendPaymFormat::addFormats(BankFormat::Remittance, [_paymentMethod.ClassIdRemittance]);
        }
    }

    if (this.ReturnFormatClassName != this.orig().ReturnFormatClassName)
    {
        _paymentMethod.ClassIdReturn = className2Id(this.ReturnFormatClassName);

        if (_paymentMethod.ClassIdReturn
            && SysDictClass::isSuperclass(_paymentMethod.ClassIdReturn, classnum(VendPaymReconciliationImport)))
        {
            VendPaymFormat::addFormats(BankFormat::ReturnFormat, [_paymentMethod.ClassIdReturn]);
        }
    }
}

public void postLoad()
{
    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]))
    {
        DimensionEnumerationEntity dimensionEnumerationEntity;
        select firstonly dimensionEnumerationEntity
            where dimensionEnumerationEntity.RecordId == this.DimensionAttributeSet;

        this.DimensionAttributeDisplayValue = dimensionEnumerationEntity.DisplayValue;
    }
}

