[Form]
public class AccountingSourceExplorer extends FormRun
{
    AccountingSourceExplorerBalanceParameters accountingSourceExplorerBalanceParameters;
    DimensionHierarchy dimensionHierarchy;
    AmountMSTDebCred total, reportingTotal;
    LedgerRecId ledger;
    SelectableDataArea targetCompany;
    SelectableDataArea startupCompany;

    private const str GeneralLedgerBalanceFeatureWhatsNewGuidStr = '{d9cf521c-54ee-4c27-acbc-0cb8c6b6c55f}';

}

public void constructBalances()
{
    delete_from AccountingSourceExplorerTmp;

    accountingSourceExplorerBalanceParameters.loadLastValue(formStr(AccountingSourceExplorerParameters));

    AccountingSourceExplorerProcessor::constructBalances(AccountingSourceExplorerTmp, accountingSourceExplorerBalanceParameters);

    this.displayDimensionAttributeValueColumns();
}

public void activate(boolean _active)
{
    super(_active);

    // Change the default company to show the correct data in the form
    if (targetCompany != curext())
    {
        appl.setDefaultCompany(targetCompany, false);
        warning(strFmt("@SYS53441", targetCompany));
    }
}

public void close()
{
    super();

    if (startupCompany != curext())
    {
        appl.setDefaultCompany(startupCompany, false);
    }
}

public void init()
{
    super();

    if (GeneralLedgerBalanceFeatureRecommendationFlight::instance().isEnabled()
        && !FeatureStateProvider::IsFeatureEnabled(GeneralLedgerBalanceFeature::instance()))
    {
        SystemNotificationsWhatsNewManager::AddWhatsNewWithActionLink(
            GeneralLedgerBalanceFeatureWhatsNewGuidStr,
            "@GeneralLedger:GeneralLedgerBalanceFeature",
            "@GeneralLedger:GeneralLedgerBalanceFeatureSummary",
            DimensionSetName.name(),
            "@GeneralLedger:GeneralLedgerBalanceFeatureLink");
    }

    if (this.args().parmObject() == null)
    {
        accountingSourceExplorerBalanceParameters = AccountingSourceExplorerBalanceParameters::construct();
    }
    else
    {
        accountingSourceExplorerBalanceParameters = this.args().parmObject() as AccountingSourceExplorerBalanceParameters;
    }

    startupCompany = curext();
    ledger = accountingSourceExplorerBalanceParameters.parmLedger();
    if (ledger != 0)
    {
        targetCompany = CompanyInfo::getDataArea(Ledger::primaryForLegalEntity(ledger));
    }
    else
    {
        targetCompany = startupCompany;
        ledger = Ledger::current();
    }
    accountingSourceExplorerBalanceParameters.parmProcessPendingReferenceIdentities(true);
    this.setDimensionHierarchy();

    if(this.args().caller() is FinancialInsightsBIReportsHandler)
    {
        accountingSourceExplorerBalanceParameters.parmGeneralJournalAccountEntry(0);
        accountingSourceExplorerBalanceParameters.saveLastValue(formStr(AccountingSourceExplorerParameters));
        element.constructBalances();
    }
    else
    {
        switch (this.args().dataset())
        {
            case tableNum(LedgerTrialBalanceTmp) :

                // When using view with record from the Trial balance inquiry form, filter records appropriately
                LedgerTrialBalanceTmp ledgerTrialBalanceTmp = this.args().record();

                if (Dynamics.AX.Application.FeatureStateProvider::IsFeatureEnabled(GeneralLedgerBalanceFeature::instance()))
                {
                    accountingSourceExplorerBalanceParameters.parmReportingDimensions(
                        GeneralLedgerBalanceReportingDimension::find(ledgerTrialBalanceTmp.ReportingDimension));
                }
                else
                {
                    accountingSourceExplorerBalanceParameters.parmDimensionFocus(
                        DimensionAttributeValueCombination::find(ledgerTrialBalanceTmp.LedgerDimension));
                }

                var ledgerTrialBalanceListPageBalanceParms = LedgerTrialBalanceListPageBalanceParms::construct();
                ledgerTrialBalanceListPageBalanceParms.loadLastValue(formStr(LedgerTrialBalanceListPageBalanceParms));

                accountingSourceExplorerBalanceParameters.parmStartDate(ledgerTrialBalanceListPageBalanceParms.parmStartDate());
                accountingSourceExplorerBalanceParameters.parmEndDate(ledgerTrialBalanceListPageBalanceParms.parmEndDate());
                accountingSourceExplorerBalanceParameters.parmGeneralJournalAccountEntry(0);
                accountingSourceExplorerBalanceParameters.parmLedger(ledger);
                accountingSourceExplorerBalanceParameters.parmfilterUsingSysQuery(false);
                accountingSourceExplorerBalanceParameters.saveLastValue(formStr(AccountingSourceExplorerParameters));

                element.constructBalances();

                break;

            case tableNum(GeneralJournalAccountEntry) :

                // When using view with record from the Voucher transactions form, filter records to a single general journal account entry
                GeneralJournalAccountEntry generalJournalAccountEntry = this.args().record();
                GeneralJournalEntry generalJournalEntry = GeneralJournalEntry::findByGeneralJournalAccountEntry(generalJournalAccountEntry.RecId);

                accountingSourceExplorerBalanceParameters.parmGeneralJournalAccountEntry(generalJournalAccountEntry.RecId);
                accountingSourceExplorerBalanceParameters.parmStartDate(generalJournalEntry.AccountingDate);
                accountingSourceExplorerBalanceParameters.parmEndDate(generalJournalEntry.AccountingDate);
                accountingSourceExplorerBalanceParameters.parmLedger(generalJournalEntry.Ledger);
                accountingSourceExplorerBalanceParameters.parmfilterUsingSysQuery(false);
                accountingSourceExplorerBalanceParameters.saveLastValue(formStr(AccountingSourceExplorerParameters));

                element.constructBalances();

                break;

            case tableNum(MainAccount) :

                // When using view with record from the Main account form, filter records appropriately
                MainAccount mainAccount = this.args().record();

                accountingSourceExplorerBalanceParameters.parmMainAccountNum(mainAccount.MainAccountId);
                accountingSourceExplorerBalanceParameters.parmMainAccountNumTo(mainAccount.MainAccountId);
                accountingSourceExplorerBalanceParameters.parmGeneralJournalAccountEntry(0);
                accountingSourceExplorerBalanceParameters.parmLedger(ledger);
                accountingSourceExplorerBalanceParameters.parmfilterUsingSysQuery(false);
                accountingSourceExplorerBalanceParameters.saveLastValue(formStr(AccountingSourceExplorerParameters));

                element.constructBalances();

                break;

            default :

                accountingSourceExplorerBalanceParameters.parmMainAccountNum(null);
                accountingSourceExplorerBalanceParameters.parmLedger(ledger);
                accountingSourceExplorerBalanceParameters.parmGeneralJournalAccountEntry(0);
                accountingSourceExplorerBalanceParameters.saveLastValue(formStr(AccountingSourceExplorerParameters));
                this.displayDimensionAttributeValueColumns();

                break;
        }
    }

    // Run unprocessed documents only once during init
    accountingSourceExplorerBalanceParameters.parmProcessPendingReferenceIdentities(false);

    // Lose the dimension focus filter after initial run
    accountingSourceExplorerBalanceParameters.parmDimensionFocus(null);

    Ledger ledgerTable = Ledger::find(ledger);
    if (ledgerTable.ReportingCurrency && ledgerTable.ReportingCurrency != ledgerTable.AccountingCurrency)
    {
        AccountingSourceExplorerTmp_ReportingCurrencyAmount.visible(true);
        AmountReportingTotal.visible(true);
    }

    // toggle advanced filtering, update button visibility
    if (FeatureStateProvider::isFeatureEnabled(AccountingSourceExplorerSysQueryFilterFeature::instance()))
    {
        ParametersBalances.visible(false);
        AdvancedSysQueryFilter.visible(true);
    }
}

private Void setDimensionHierarchy()
{
    dimensionHierarchy = DimensionHierarchy::getMainAccountFocus();

    DimensionSetName.text(dimensionHierarchy.Name);
    accountingSourceExplorerBalanceParameters.parmDimensionHierarchy(dimensionHierarchy);
}

private FormStringControl getDimensionAttributeControl(int _dimensionHierarchyLevel)
{
    str leadingZero;

    if (_dimensionHierarchyLevel <= 9)
    {
        leadingZero = '0';
    }
    else
    {
        leadingZero = '';
    }

    return element.design().controlName(identifierstr(DimensionAttributeValue) + leadingZero + int2str(_dimensionHierarchyLevel));
}

public void displayDimensionAttributeValueColumns()
{
    int                     i, dimensionHierarchylevelCount;
    FormStringControl       dimensionFormControl;
    DimensionSetSegmentName dimensionSetSegmentNames;
    #Dimensions

    DimensionAttributeValueCombination_DisplayValue.visible(false);

    dimensionSetSegmentNames = DimensionHierarchyLevel::getDimensionHierarchyLevelNames(dimensionHierarchy.RecId);

    dimensionHierarchylevelCount = DimensionHierarchy::getLevelCount(dimensionHierarchy.RecId);

    for (i = 1; i <= #MaxDimensionHierarchyLevelsInStructure; i++)
    {
        dimensionFormControl = this.getDimensionAttributeControl(i);

        if (i <= dimensionHierarchylevelCount)
        {
            dimensionFormControl.label(dimensionSetSegmentNames[i]);
            dimensionFormControl.visible(true);
        }
        else
        {
            dimensionFormControl.visible(false);
        }
    }

    AccountingSourceExplorerTmp_ds.executeQuery();

    this.updateQuickFilterControlList();
}

private void updateQuickFilterControlList()
{
    List quickFilterList = new List(Types::String);
    List quickFilterLabels = new List(Types::String);
    int i;

    for (i = 1; i <= grid.controlCount(); i++)
    {
        FormControl listPageGridColumn = grid.controlNum(i);

        if (listPageGridColumn.visible())
        {
            quickFilterList.addEnd(listPageGridColumn.name());
            quickFilterLabels.addEnd(listPageGridColumn.labelText());
        }
    }

    quickFilter.parmFieldList(quickFilterList);
    quickFilter.parmFieldLabels(quickFilterLabels);
}

private void refresh()
{
    // Trigger execute query to pick up the new balance parameters for filtering
    element.constructBalances();
    AccountingSourceExplorerTmp_ds.executeQuery();
    AccountingSourceExplorerTmp_ds.refresh();
}

private void calculateTotals()
{
    total = AccountingSourceExplorerProcessor::calculateQueryTotals(accountingSourceExplorerTmp_Ds.queryRun().query(), accountingSourceExplorerTmp_Ds.cursor());
    reportingTotal = AccountingSourceExplorerProcessor::calculateQueryReportingTotals(accountingSourceExplorerTmp_Ds.queryRun().query(), accountingSourceExplorerTmp_Ds.cursor());
}

display AmountMSTDebCred getTotal()
