    return this.total;
}

display AmountMSTDebCred getReportingTotal()
{
    return this.reportingTotal;
}

public int active()
{
    int active;
    active = super();

    if (AccountingSourceExplorerTmp.SourceDocumentRecId)
    {
        ViewSourceDocument.enabled(SourceDocumentViewer::canViewSourceDocument(AccountingSourceExplorerTmp.SourceDocumentRecId));
    }
    else
    {
        ViewSourceDocument.enabled(false);
    }

    return active;
}

public void executeQuery()
{
    GeneralJournalAccountEntryRecId generalJournalAccountEntryRecId;
    QueryBuildDataSource qbds;
    TransDate startDate, endDate;
    ProjId projId;

    accountingSourceExplorerBalanceParameters.loadLastValue(formStr(AccountingSourceExplorerParameters));

    startDate = accountingSourceExplorerBalanceParameters.parmStartDate();
    endDate = accountingSourceExplorerBalanceParameters.parmEndDate();

    generalJournalAccountEntryRecId = accountingSourceExplorerBalanceParameters.parmGeneralJournalAccountEntry();
    projId = accountingSourceExplorerBalanceParameters.parmProjId();

    qbds = this.query().dataSourceTable(tableNum(AccountingSourceExplorerTmp));
    qbds.clearRanges();

    if (startDate && endDate)
    {
        qbds.addRange(fieldNum(AccountingSourceExplorerTmp, AccountingDate)).value(SysQuery::range(startDate, endDate));
    }

    if (generalJournalAccountEntryRecId)
    {
        qbds.addRange(fieldNum(AccountingSourceExplorerTmp, GeneralJournalAccountEntry)).value(SysQuery::value(generalJournalAccountEntryRecId));
    }

    if(projId)
    {
        qbds.addRange(fieldNum(AccountingSourceExplorerTmp, ProjectId)).value(SysQuery::value(projId));
    }

    super();

    element.calculateTotals();
}

void clicked()
{
    FormRun                     formRun;
    Args                        args = new Args();
    GeneralJournalEntry         generalJournalEntry;
    GeneralJournalAccountEntry  generalJournalAccountEntry;
    AccountingSourceExplorerTmp accountingSourceExplorerTmpCursor = Grid.dataSourceObject().cursor() as AccountingSourceExplorerTmp;

    changecompany(CompanyInfo::getDataArea(Ledger::primaryForLegalEntity(ledger)))
    {
        select firstOnly generalJournalEntry
        exists join generalJournalAccountEntry
            where GeneralJournalAccountEntry.RecId == accountingSourceExplorerTmpCursor.GeneralJournalAccountEntry
               && GeneralJournalAccountEntry.GeneralJournalEntry == generalJournalEntry.recid;

        args.name(formStr(LedgerTransVoucher));
        args.record(generalJournalEntry);

        formRun = ClassFactory.formRunClass(args);
        formRun.init();
        formRun.run();
    }
}

void clicked()
{
    changecompany(CompanyInfo::getDataArea(Ledger::primaryForLegalEntity(ledger)))
    {
        accountingSourceExplorerTmp = Grid.dataSourceObject().cursor() as AccountingSourceExplorerTmp;
        SourceDocumentViewer::viewSourceDocument(accountingSourceExplorerTmp.SourceDocumentRecId);
    }
}

void clicked()
{
    // Apart from the following balance parameters, the rest are ethier set in the subsequent code
    // or donot impact filtering using advanced filter.
    accountingSourceExplorerBalanceParameters.parmDimensionFocus(null);
    accountingSourceExplorerBalanceParameters.parmGeneralJournalAccountEntry(0);
    accountingSourceExplorerBalanceParameters.parmProcessPendingReferenceIdentities(true);

    AccountingSourceExplorerSysQueryFilterHelper sysQueryFilterHelper =
        AccountingSourceExplorerSysQueryFilterHelper::construct(accountingSourceExplorerBalanceParameters);

    SysQueryRun queryRun = new SysQueryRun(sysQueryFilterHelper.parmQuery());
    queryRun.promptShowSorting(false);
    queryRun.promptShowQuerySelect(true);
    queryRun.promptAllowSave(true);

    if (queryRun.prompt())
    {   
        List accountingDateRange = sysQueryFilterHelper.getAccountingDateRange(queryRun);
        boolean dateRangeWithinSameFiscalYear = sysQueryFilterHelper.validateFromAndToDateInSameFiscalYear(accountingDateRange);
        boolean dateRangeNotNull = sysQueryFilterHelper.validateFromAndToDateNotNull(accountingDateRange);
        boolean isNumberOfRowsUnderProcessingLimit = sysQueryFilterHelper.isNumberOfRowsUnderProcessingLimit(queryRun);

        if (dateRangeWithinSameFiscalYear && dateRangeNotNull && isNumberOfRowsUnderProcessingLimit)
        {
            AccountingSourceExplorerGeneralJournalAccountEntryTmp aseGeneralJournalAccountEntryTmp;
            aseGeneralJournalAccountEntryTmp = sysQueryFilterHelper.loadQueryRunResults(queryRun);

            accountingSourceExplorerBalanceParameters.parmfilterUsingSysQuery(true);
            sysQueryFilterHelper.setStartAndEndDateInBalanceParameters(accountingDateRange, accountingSourceExplorerBalanceParameters);
            accountingSourceExplorerBalanceParameters.parmSysQueryGeneralJournalAccountEntryTmp(aseGeneralJournalAccountEntryTmp);
            accountingSourceExplorerBalanceParameters.saveLastValue(formStr(AccountingSourceExplorerParameters));

            element.constructBalances();
        }
        else
        {
            AccountingSourceExplorerTmp.clear();
            AccountingSourceExplorerTmp.reread();
        }
    }
}

public void lookup()
{
    DimensionHierarchyHelper::lookupDimensionSet(dimensionSetName, true, DimensionFocusBalanceInitializationState::Initialized);
}

public boolean modified()
{
    boolean isModified = super();

    if (strlen(dimensionSetName.text()) == 0)
    {
        dimensionHierarchy = null;
    }
    else if (dimensionHierarchy.Name != dimensionSetName.text())
    {
        dimensionHierarchy = DimensionHierarchy::findByTypeAndName(DimensionHierarchyType::Focus, dimensionSetName.text());

        if (dimensionHierarchy == null)
        {
            throw error(strFmt("@SYS333565", dimensionSetName.text()));
        }
    }

    accountingSourceExplorerBalanceParameters.parmDimensionHierarchy(dimensionHierarchy);

    element.constructBalances();

    return isModified;
}

public boolean validate()
{
    boolean isValid;

    isValid = super();

    if (strlen(dimensionSetName.text()) == 0)
    {
        dimensionHierarchy = null;
    }
    else
    {
        dimensionHierarchy = DimensionHierarchy::findByTypeAndName(DimensionHierarchyType::Focus, dimensionSetName.text());
        if (dimensionHierarchy == null)
        {
            isValid = checkFailed(strFmt("@SYS333565", dimensionSetName.text()));
        }
    }

    return isValid;
}
