public void init()
{
    super();

    ledgerId.value(Ledger::current());
    element.updateLedger();
}

public void updateLedger()
{
    AdvanceAdjustmentParameters_W::findLedger(ledgerId.value());
    findOrCreateRange_W(advanceAdjustmentParameters_W_ds.query().dataSourceNo(1), fieldNum(AdvanceAdjustmentParameters_W, Ledger), SysQuery::value(ledgerId.value()));
    advanceAdjustmentParameters_W_ds.executeQuery();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.updateLedger();

    return ret;
}

