/// <summary>
///     Checks budget for the advanced ledger entry.
/// </summary>
public void CheckBudgetLine()
{
    if (advancedLedgerEntryLine)
    {
        advancedLedgerEntryLine.checkBudget(false);
        advancedLedgerEntryLine_ds.research(true);
        advancedLedgerEntryLine_ds.updateBudgetCheckResultDisplay();
    }
}

public void close()
{
    if (!advancedLedgerEntryHeader.RecId && advancedLedgerEntryHeader.TransactionNumber)
    {
        // Form is being closed without saving the advanced ledger entry header.
        // Release the transaction number that was allocated to the advanced ledger entry header.
        NumberSeq::releaseNumber(LedgerParameters::numRefAdvancedLedgerEntryId().NumberSequenceId, advancedLedgerEntryHeader.TransactionNumber);
    }

    super();
}

public void init()
{
    boolean doBudgetCheckOnEntry = false;

    super();

    advancedLedgerEntryManager = AdvancedLedgerEntryManager::construct();
    advancedLedgerEntryManager.formMethodInit(element);

    LedgerDimensionCtrl.parmValidateBlockedForManualEntry(true);
    LedgerDimensionCtrl.parmPostingType(LedgerPostingType::LedgerJournal);

    boolean doBudgetCheck = BudgetControlConfiguration::isSourceIntegratorEnabled(Ledger::current(),
        BudgetControlSourceIntegrator::AdvancedLedgerEntry);

    checkBudget.visible(doBudgetCheck);
    checkBudgetLine.visible(doBudgetCheck);

    areFinTagsEnabled = this.areFinTagsEnabled();
    this.initFinancialTags();

    if (element
        && element.args()
        && element.args().caller() is FormRun
        && element.args().caller().name() == formStr(AdvancedLedgerEntryCreate))
    {
        tabPageHeader.fastTabExpanded(FastTabExpanded::No);
        tabPageLines.fastTabExpanded(FastTabExpanded::Yes);
        tabPageLineDetails.fastTabExpanded(FastTabExpanded::No);
        Transaction_FiscalCalendarPeriod.viewEditMode(ViewEditMode::Edit);
    }
}

private void initFinancialTags()
{
    if (areFinTagsEnabled)
    {
        FinTagReferenceGroupControllerContract finTagContract = FinTagReferenceGroupControllerContract::construct(
            AdvancedLedgerEntry_FinTag);

        FinTagReferenceGroupController::registerReferenceGroup(finTagContract);
    }
    else
    {
        AdvancedLedgerEntry_FinTag.visible(false);
        FinTagFastTab.visible(false);
    }
}

private boolean areFinTagsEnabled()
{
    return isFinTagConfigurationSetForCurrent
        && AdvancedLedgerEntryFinTagFlight::instance().isEnabled();
}

/// <summary>
///     Sets the field accessability of the cost and sales price based on the project category type selected on the form.
/// </summary>
/// <param name="_projCategoryType">
///     The project category type which is selected on the form.
/// </param>
public void setFieldAccess(ProjCategoryType _projCategoryType)
{
    boolean isEnabled = false;

    isEnabled = (_projCategoryType != ProjCategoryType::Revenue && advancedLedgerEntryLine.ProjId);
    advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, CostPrice)).allowEdit(isEnabled);
    advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, SalesPrice)).allowEdit(isEnabled);
}

public void setHeaderAccountingDateEditability()
{
    boolean hasProjectLines = AdvancedLedgerEntryHeader::hasProjectLines(AdvancedLedgerEntryHeader.RecId);

    // if there is a project on any line, make the accounting date disabled
    advancedLedgerEntryHeader_ds.object(fieldNum(AdvancedLedgerEntryHeader, AccountingDate)).enabled(!hasProjectLines);
}

/// <summary>
/// Allows or disallows editing of the ledger dimension on a selected advanced ledger entry line on the <c>AdvancedLedgerEntry</c> form.
/// </summary>
public void setProjectFieldsEditable()
{
    boolean isAllowEditLedgerAccountOnProject = LedgerParameters::find().AllowALELedgerDimensionEdit_PSN;

    AdvancedLedgerEntryLine_DS.object(fieldNum(AdvancedLedgerEntryLine, LedgerDimension)).allowEdit(isAllowEditLedgerAccountOnProject || !AdvancedLedgerEntryLine.ProjId);
}

/// <summary>
///     Sets the project fields mandatory based on the project category type selected on the form.
/// </summary>
/// <param name="_projCategoryId">
///     The project category which is selected on the form.
/// </param>
public void setProjFieldsMandatory(ProjCategoryId _projCategoryId)
{
    ProjCategory    projCategory;
    boolean         isProjCategorySelected = _projCategoryId ? true : false;

    select CategoryType from projCategory
        where projCategory.CategoryId == _projCategoryId;

    this.setFieldAccess(projCategory.CategoryType);

    switch (projCategory.CategoryType)
    {
        case ProjCategoryType::Cost:
            advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, CostPrice)).mandatory(isProjCategorySelected);
            advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjLinePropertyId)).mandatory(isProjCategorySelected);
            break;

        case ProjCategoryType::Revenue:
            advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, CostPrice)).mandatory(false);
            advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjLinePropertyId)).mandatory(isProjCategorySelected);
            break;

        case ProjCategoryType::Hour:
            advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjLinePropertyId)).mandatory(isProjCategorySelected);
            break;

        default:
            advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, CostPrice)).mandatory(isProjCategorySelected);
            advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjLinePropertyId)).mandatory(isProjCategorySelected);
    }
}

/// <summary>
///     Enables or disables the enablility of budget controls.
/// </summary>
public void updateBudgetControls()
{
    boolean enableLine = advancedLedgerEntryLine.RecId != 0;

    //enable or disable budget control buttons on header action strip
    budgetCheckResult.enabled(enableLine);
    checkBudget.enabled(enableLine);

    //enable or disable budget control buttons on lines tab action strip
    budgetCheckResultLine.enabled(enableLine);
    checkBudgetLine.enabled(enableLine);
}

private boolean isRecordEditable()
{
    return advancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::Draft
                            || advancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::Rejected
                            || advancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::Approved;
}

/// <summary>
/// The workflow icon on header should be visible only if you can submit it to the workflow with an active workflow configuration
//  or if there are workitems on the advanced ledger entry header for the current user
//  or if there is a workflow history available for the advanced ledger entry.
/// </summary>
private void updateWorkflowIconForHeader()
{
    if ((this.canSubmitToWorkflow() && Workflow::findWorkflowConfigurationToActivate(AdvancedLedgerEntryHeader, workflowTypeStr(AdvancedLedgerEntryTemplate))
        || Workflow::findWorkItemsForCurrentUser(AdvancedLedgerEntryHeader).elements()
        || Workflow::findTrackingStatusForDocument(AdvancedLedgerEntryHeader)))
    {
        AdvancedLedgerEntryWFDropDialog.visible(true);
    }
    else
    {
        AdvancedLedgerEntryWFDropDialog.visible(false);
    }
}

public void updateDesign()
{
    boolean isRecordEditable = true;
    boolean isRecordExist = AdvancedLedgerEntryHeader.RecId ? true : false;
    boolean isRecordDraft = AdvancedLedgerEntryHeader.TransactionStatus == AdvancedLedgerEntryTransactionStatus::Draft;
    boolean isUserTaskOwner;
    boolean isUserApprovalOwner;

    if (AdvancedLedgerEntryHeader.TransactionStatus == AdvancedLedgerEntryTransactionStatus::Posted)
    {
        element.design().viewEditMode(ViewEditMode::View);
    }
    else
    {
        element.design().viewEditMode(ViewEditMode::Auto);
    }

    if (AdvancedLedgerEntryManager::activeWorkflowForCurrentCompanyExists())
    {
        if (AdvancedLedgerEntryHeader.TransactionStatus != AdvancedLedgerEntryTransactionStatus::Posted
            && advancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::Approved)
        {
            // Added the line to enable the Edit button, when ViewEditMode becomes Auto.
            element.design().viewEditMode(ViewEditMode::View);
            element.design().viewEditMode(ViewEditMode::Auto);
        }
    }

    if (AdvancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::PendingApproval ||
        AdvancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::PendingCompletion ||
        AdvancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::ChangeRequested)
    {
        isUserTaskOwner = WorkflowWorkItem::isUserTaskOwner(AdvancedLedgerEntryHeader);
        isUserApprovalOwner = WorkflowWorkItem::isUserApprovalOwner(AdvancedLedgerEntryHeader);
    }

    else
    {
        isUserTaskOwner = false;
        isUserApprovalOwner = false;
    }

    if (AdvancedLedgerEntryHeader.WorkFlowStatus)
    {
        isRecordEditable = element.isRecordEditable();
    }

    //can not edit the posted transactions
    assignedSettings_JournalizingDefinition.allowEdit(isRecordDraft && isRecordEditable);
    reversingEntry_ReverseEntry.allowEdit(isRecordDraft && isRecordEditable);

    AdvancedLedgerEntryHeader_ds.allowEdit(isRecordDraft && (isRecordEditable || isUserTaskOwner || isUserApprovalOwner));
    AdvancedLedgerEntryHeader_ds.allowDelete(isRecordDraft && isRecordEditable);
    AdvancedLedgerEntryLine_ds.allowEdit(isRecordDraft && (isRecordEditable || isUserTaskOwner || isUserApprovalOwner));
    AdvancedLedgerEntryLine_ds.allowCreate(isRecordDraft && isRecordEditable);
    AdvancedLedgerEntryLine_ds.allowDelete(isRecordDraft && isRecordEditable);

    ledgerEntryCopy.enabled(isRecordExist);

    FormCommandButtonControl editButton= this.control(this.controlId(#SystemDefinedViewEditButton)) as FormCommandButtonControl;
    editButton.enabled(isRecordDraft && (isRecordEditable || isUserTaskOwner || isUserApprovalOwner));

    // Updates the Enable property of buttons related to budget
    this.updateBudgetControls();

    this.updateWorkflowIconForHeader();

    advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjSalesCurrency)).mandatory(advancedLedgerEntryLine.ProjId != '');
    reversingEntry_ReverseDate.allowEdit(AdvancedLedgerEntryHeader.ReverseEntry && isRecordDraft && isRecordEditable);

    // Enables the period name if multiple periods exist for the accounting date
    Transaction_FiscalCalendarPeriod.allowEdit(AdvancedLedgerEntryManager::doMultipleOpenPeriodsExist(AdvancedLedgerEntryHeader.AccountingDate));
}

public int task(int _taskId)
{
    #Task
    int taskExecutionResult;

    switch (_taskId)
    {
        case #taskEditRecord :

            if (advancedLedgerEntryHeader.TransactionStatus == AdvancedLedgerEntryTransactionStatus::Draft && element.isRecordEditable())
            {
                element.design().viewEditMode(ViewEditMode::Edit);
            }
            else
            {
                element.design().viewEditMode(ViewEditMode::Auto);
            }

            if (advancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::Approved
                && AdvancedLedgerEntryHeader.TransactionStatus != AdvancedLedgerEntryTransactionStatus::Posted)
            {
                AdvancedLedgerEntryHeader::setWorkflowState(advancedLedgerEntryHeader.RecId, AdvancedLedgerEntryWFApprovalState::NotSubmitted);
                advancedLedgerEntryHeader_ds.research(true);
            }
            break;

        case #taskDeleteRecord :

            if (advancedLedgerEntryHeader.WorkFlowStatus == AdvancedLedgerEntryWFApprovalStatus::Approved)
            {
                advancedLedgerEntryHeader.WorkflowApprovalState = AdvancedLedgerEntryWFApprovalState::NotSubmitted;
                advancedLedgerEntryHeader.WorkFlowStatus = 0;
            }
            break;

        case #taskSwitchToGridView:

            AdvancedLedgerEntryHeader_DS.query().dataSourceTable(tableNum(AdvancedLedgerEntryHeader)).clearRange(fieldNum(AdvancedLedgerEntryHeader, TransactionNumber));
            AdvancedLedgerEntryHeader_DS.executeQuery();
            break;
    }

    taskExecutionResult = super(_taskId);

    return taskExecutionResult;
}

public void executeQuery()
{
    super();

    if (advancedLedgerEntryLine.RecId)
    {
        accountingDistributionsLineButton.enabled(true);
        accountingDistributionsDocumentView.enabled(true);
    }

    if (advancedLedgerEntryLine.ProjId)
    {
        advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjFundingSource)).allowEdit
                                                    (advancedLedgerEntryLine.setProjFundingSource() != 0);
    }
}

public void delete()
{
    ProjId originalProjectID = advancedLedgerEntryLine.orig().ProjId;

    super();

    // Disable Distribute amounts and View distributions button on the form when the ALE line is deleted.
    accountingDistributionsLineButton.enabled(false);
    accountingDistributionsDocumentView.enabled(false);

    if (!advancedLedgerEntryLine.RecId)
    {
        advancedLedgerEntryHeader.WorkFlowStatus = 0;
        advancedLedgerEntryHeader_ds.refresh();
    }

    if (originalProjectID != '')
    {
        element.setHeaderAccountingDateEditability();
    }
}

public int active()
{
    int ret;

    ret = super();

    advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjCategoryId)).mandatory(advancedLedgerEntryLine.ProjId != '');

    element.setProjFieldsMandatory(advancedLedgerEntryLine.ProjCategoryId);
    element.setProjectFieldsEditable();
    LedgerDimensionCtrl.parmLockMainAccountSegment(advancedLedgerEntryLine.ProjId != '');

    if (advancedLedgerEntryLine.RecId)
    {
        accountingDistributionsLineButton.enabled(true);
        accountingDistributionsDocumentView.enabled(true);

        currentLedgerDimensionAccount = advancedLedgerEntryLine.LedgerDimension;
    }

    if (advancedLedgerEntryLine.ProjId)
    {
        advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjFundingSource)).allowEdit
                                                    (advancedLedgerEntryLine.setProjFundingSource() != 0);
    }

    element.updateDesign();

    if (ret)
    {
        currentMainAccountId = MainAccount::getMainAccountRecIdFromLedgerDimension(advancedLedgerEntryLine.LedgerDimension);
    }

    return ret;
}

