public void write()
{
    WorkflowTable workflowTableFieldList;
    ProjId originalProjectID = advancedLedgerEntryLine.orig().ProjId;

    super();

    if ((originalProjectID == '') && (advancedLedgerEntryLine.ProjId != ''))
    {
        // The user added a projectID so make the accounting date read-only
        advancedLedgerEntryHeader_ds.object(fieldNum(AdvancedLedgerEntryHeader, AccountingDate)).enabled(false);
    }
    else if ((originalProjectID != '') && (advancedLedgerEntryLine.ProjId == ''))
    {
        element.setHeaderAccountingDateEditability();
    }

    advancedLedgerEntryHeader_ds.research(true);
    element.updateDesign();

    // Update the enable property of budget buttons
    element.updateBudgetControls();

    advancedLedgerEntryManager.formMethodDataSourceALELineWrite(element, AdvancedLedgerEntryLine_ds);
}

