public void modified()
{
    super();

    if (advancedLedgerEntryLine.setProjFundingSource())
    {
        advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjFundingSource)).allowEdit(true);
        advancedLedgerEntryLine_ds.rereadReferenceDataSources();
    }
    else
    {
        advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjFundingSource)).allowEdit(false);
    }

    advancedLedgerEntryLine.ProjLinePropertyId = ProjLinePropertySetup::findLinePropertyId(
                                                        advancedLedgerEntryLine.ProjId,
                                                        advancedLedgerEntryLine.ProjCategoryId);

    // Make the project category field mandatory when project is selected
    advancedLedgerEntryLine_ds.object(fieldNum(AdvancedLedgerEntryLine, ProjCategoryId)).mandatory(advancedLedgerEntryLine.ProjId != '');

    if (advancedLedgerEntryLine.ProjId == '')
    {
        AdvancedLedgerEntryLine.ProjCategoryId = '';
        LedgerDimensionCtrl.parmLockMainAccountSegment(false);
        element.setProjFieldsMandatory('');
        advancedLedgerEntryLine.ProjLinePropertyId = '';

        this.updateLedgerDimesionNoProjectId();
    }
    else
    {
        advancedLedgerEntryLine.ProjLinePropertyId = ProjLinePropertySetup::findLinePropertyId(
                                                            advancedLedgerEntryLine.ProjId,
                                                            advancedLedgerEntryLine.ProjCategoryId);
    }

    element.updateDesign();
}

/// <summary>
///     Finds and updates Ledger Dimension when no Project ID.
/// </summary>
public void updateLedgerDimesionNoProjectId()
{
    DimensionStorage        dimensionStorage;
    DimensionStorageSegment segment;
    int                     segmentCount, segmentIndex;
    int                     hierarchyCount, hierarchyIndex;
    RefTableId              backingEntityType;

    dimensionStorage = dimensionStorage::findById(advancedLedgerEntryLine.LedgerDimension);

    hierarchyCount = dimensionStorage.hierarchyCount();
    for (hierarchyIndex = 1; hierarchyIndex <= hierarchyCount; hierarchyIndex++)
    {
        segmentCount = dimensionStorage.segmentCountForHierarchy(hierarchyIndex);

        for (segmentIndex = segmentCount; segmentIndex >= 1; segmentIndex--)
        {
            // Get segment
            segment = dimensionStorage.getSegmentForHierarchy(hierarchyIndex, segmentIndex);

            if (segment.parmDimensionAttributeValueId() != 0)
            {
                backingEntityType = DimensionAttribute::find(DimensionAttributeValue::find(segment.parmDimensionAttributeValueId()).DimensionAttribute).BackingEntityType;

                if (backingEntityType == tableNum(DimAttributeProjTable))
                {
                    // Clear project dimension segment when project ID is cleared
                    segment.parmDisplayValue('');
                    advancedLedgerEntryLine.LedgerDimension = dimensionStorage.save();

                    break;
                }
            }
        }
    }
}

