/// <summary>
///     Manages the number sequence during the creation of a advanced ledger entry header.
/// </summary>
/// <param name="_append">
///     A boolean flag that indicates whether to insert the record after or before the current
///     cursor position. If true, the new record is inserted after the current record.
/// </param>
public void create(boolean _append = false)
{
    boolean isSequenceValid;
    boolean enabled = advancedLedgerEntryHeader.RecId != 0;

    // Validate the number sequence for Advanced ledger entry Number.
    isSequenceValid = AdvancedLedgerEntryManager::checkAdvancedLedgerEntryNumberSequence();

    if (isSequenceValid)
    {
        advancedLedgerEntryManager.formMethodDataSourceCreatePre(element, advancedLedgerEntryHeader_ds);

        super(_append);

        advancedLedgerEntryManager.formMethodDataSourceCreate(element, advancedLedgerEntryHeader_ds);
    }

    budgetCheckResult.enabled(enabled);
    checkBudget.enabled(enabled);
}

/// <summary>
///     Manages the number sequence during a write and the ordering of advanced ledger entry transactions.
/// </summary>
public void write()
{
    if (!advancedLedgerEntryHeader.RecId)
    {
        advancedLedgerEntryManager.formMethodDataSourceWrite(element, advancedLedgerEntryHeader_ds);
    }

    super();
}

/// <summary>
///     Validates the number sequence during a write.
/// </summary>
/// <returns>
///     The result of the super call plus the result of calling the formMethodDataSourceValidateWrite method
///     on the AdvancedLedgerEntryManager class.
/// </returns>
public boolean validateWrite()
{
    boolean isSequenceValid;

    isSequenceValid = super();

    isSequenceValid = isSequenceValid && advancedLedgerEntryManager.formMethodDataSourceValidateWrite(element, advancedLedgerEntryHeader_ds);

    return isSequenceValid;
}

public void modified()
{
    super();

    // Load the accounting date into the ledger account segmented entry control.
    LedgerDimensionCtrl.parmControlDate(advancedLedgerEntryHeader.AccountingDate);
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return AdvancedLedgerEntryManager::constructFiscalPeriodLookup(_formReferenceControl, AdvancedLedgerEntryHeader.AccountingDate);
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common                  moduleId;
    SysReferenceTableLookup sysReferenceTableLookup;
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildDataSource    queryBuildDataSourceJDV;

    sysReferenceTableLookup = SysReferenceTableLookup::newParameters(tableNum(JournalizingDefinition), _formReferenceControl, true);

    queryBuildDataSource = query.addDataSource(tableNum(JournalizingDefinition));
    queryBuildDataSource.addRange(fieldNum(JournalizingDefinition, JournalizingDefinitionModuleValue)).value(int2str(enum2int(JournalizingDefinitionModuleId::GeneralLedger)));
    queryBuildDataSourceJDV = queryBuildDataSource.addDataSource(tableNum(JournalizingDefinitionVersion));
    queryBuildDataSourceJDV.addLink(fieldNum(JournalizingDefinition, RecId), fieldNum(JournalizingDefinitionVersion, JournalizingDefinition));

    sysReferenceTableLookup.addLookupfield(fieldNum(JournalizingDefinition, JournalizingDefinitionCode));
    sysReferenceTableLookup.addLookupfield(fieldNum(JournalizingDefinition, JournalizingDefinitionDesc));
    sysReferenceTableLookup.parmQuery(query);

    moduleId = sysReferenceTableLookup.performFormLookup();

    return moduleId;
}

void clicked()
{
    advancedLedgerEntryHeader_ds.reread();

    super();
}

void clicked()
{
    if (advancedLedgerEntryHeader)
    {
        advancedLedgerEntryHeader.checkBudget(false, true);
        advancedLedgerEntryLine_ds.research(true);
    }

    element.updateDesign();
}

