        this,
        LedgerJournalACType::Ledger,
        LedgerJournalACType::Ledger,
        true);
}

public boolean modified()
{
    boolean ok;
    boolean isChecked;

    isChecked = reversingEntry_ReverseEntry.checked();
    reversingEntry_ReverseDate.allowEdit(isChecked);

    if (isChecked)
    {
        advancedLedgerEntryHeader.ReverseDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    }
    else
    {
        advancedLedgerEntryHeader.ReverseDate = dateNull();
    }

    ok = super();

    return ok;
}

void clicked()
{
    Args args;
    SourceDocumentLineList  sourceDocumentLineList;

    if (advancedLedgerEntryLine.RecId == 0)
    {
        throw error("@SYS136802");
    }

    sourceDocumentLineList = SourceDocumentLineList::construct();
    sourceDocumentLineList.addStart((SourceDocumentLine::find(advancedLedgerEntryLine.SourceDocumentLine)));

    args = new Args();
    args.caller(element);
    args.record(advancedLedgerEntryHeader);
    args.parmObject(sourceDocumentLineList);

    new MenuFunction(
        menuitemDisplayStr(SubledgerJournalAccountEntries),
        MenuItemType::Display).run(args);
}

void clicked()
{
     element.CheckBudgetLine();
     element.updateDesign();
}

