void calcCreditLimit()
{
    container           displayFields;
    AmountMST           balanceEstimate;
    boolean             useEstimated;
    CustParameters      custParameters = CustParameters::find();

    if  (custParameters.AgreementCreditLine_RU && custParameters.CreditMaxCheck != TypeOfCreditmaxCheck::None)
    {
        [displayFields, balanceEstimate, useEstimated] = TmpCustVendTrans::custCalcSalesTotals(custAccount,
                                                                                               CompanyInfoHelper::standardCurrency(),
                                                                                               salesAgreementHeaderExt.RecId);

        creditRemain -= this.amountToAdjustCreditRemain(displayFields, balanceEstimate, useEstimated);

        creditLimit = salesAgreementHeaderExt.CreditMax;
        if (creditLimit)
        {
            creditRemain += conpeek(displayFields,TradeTotals::posFreeValue());
        }
        else
        {
            creditRemain = 0;
        }
    }
}

/// <summary>
/// Determines the amount to adjust the remaining credit amount based on the <c>TypeOfCreditmaxCheck</c>.
/// </summary>
/// <param name = "_displayFields">A <c>container</c> of fields that hold totals information.</param>
/// <param name = "_balanceEstimate">The estimated balance for the customer.</param>
/// <param name = "useEstimated">Indicates whether or not to use the estimated value.</param>
/// <returns>The amount by which the credit remain value should be adjusted.</returns>
protected AmountMST amountToAdjustCreditRemain(container _displayFields, AmountMST _balanceEstimate, boolean useEstimated)
{
    AmountMST amountToAdjust = 0;

    switch (CustParameters::find().CreditMaxCheck)
    {
        case TypeOfCreditmaxCheck::BalanceDelivered:
        case TypeOfCreditmaxCheck::BalanceAll:
            if (useEstimated)
            {
                amountToAdjust = _balanceEstimate;
            }
            break;
    }

    return amountToAdjust;
}

