void calcCreditLimit()
{
    container      displayFields;
    AmountMST      balanceEstimate;
    VendParameters vendParameters = VendParameters::find();

    if  (vendParameters.AgreementCreditLine_RU && vendParameters.CreditMaxCheck != TypeOfCreditmaxCheck::None)
    {
        [displayFields] = TmpCustVendTrans::vendCalcSalesTotals(vendAccount, purchAgreementHeaderExt.RecId);

        switch (VendParameters::find().CreditMaxCheck)
        {
            case TypeOfCreditmaxCheck::BalanceDelivered:
                balanceEstimate = PurchTable::balanceAmountDelivered(vendAccount, '', true, purchAgreementHeaderExt.RecId);
                creditRemain -= balanceEstimate;
                break;

            case TypeOfCreditmaxCheck::BalanceAll:
                balanceEstimate = PurchTable::balanceAmountNotInvoiced(vendAccount, '', true, purchAgreementHeaderExt.RecId);
                creditRemain -= balanceEstimate;
                break;
        }

        creditLimit = purchAgreementHeaderExt.CreditMax;
        if (creditLimit)
        {
            creditRemain += conpeek(displayFields,PurchTotals::posFreeValue());
        }
        else
        {
            creditRemain = 0;
        }
    }
}

