/// <summary>
/// Display end balance.
/// </summary>
/// <returns>Return end balance.</returns>
display AmountMST endBalance()
{
    return startBalance + turnoverDebit + turnoverCredit;
}

public void init()
{
    purchAgreementHeaderExt = element.args().record() as PurchAgreementHeaderExt_RU;
    vendAccount = PurchAgreementHeader::find(purchAgreementHeaderExt.AgreementHeader).VendAccount;

    dimAttrRecId = AgreementHeaderExt_RU::getAgreementDimensionAttribute();

    super();

    endDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
}

