void updateBalances()
{
    QueryRun             queryRun;
    Query                query;
    QueryBuildDataSource qbd;
    VendTrans            vendTrans;
    query = new Query(vendTransDebit_Q);
    qbd   = query.dataSourceNo(1);
    qbd.findRange(fieldnum(VendTrans,AmountMST)).value(SysQuery::valueUnlimited());
    qbd.findRange(fieldnum(VendTrans,TransDate)).value(SysQuery::range(dateNull(),endDate.dateValue()));
    queryRun = new QueryRun(query);
    startBalance     = 0;
    turnoverCredit   = 0;
    turnoverDebit    = 0;
    settleEndBalance = 0;
    creditRemain     = 0;
    creditLimit      = 0;
    while (queryRun.next())
    {
        vendTrans = queryRun.get(tablenum(VendTrans));
        settleEndBalance += vendTrans.remainAmountMST();
        if (vendTrans.TransDate < startDate.dateValue())
        {
            startBalance += vendTrans.AmountMST;
        }
        else
        {
            if (vendTrans.AmountMST > 0)
            {
                turnoverDebit += vendTrans.AmountMST;
            }
            else
            {
                turnoverCredit += vendTrans.AmountMST;
            }
        }
    }
    this.calcCreditLimit();
}

public void executeQuery()
{
    criteriaDatesDebit.value(SysQuery::range(startDate.dateValue(),endDate.dateValue()));

    if (dimAttrRecId)
    {
        SysQuery::clearDimensionRangesFromQuery(this.query());

        SysQuery::addDimensionAttributeRange(this.query(),
            this.query().dataSourceNo(1).name(),
            fieldStr(VendTrans, DefaultDimension),
            DimensionComponent::DimensionAttribute,
            PurchAgreementHeaderExt.AgreementId,
            DimensionAttribute::find(dimAttrRecId).Name);
    }

    super();

    element.updateBalances();
}

public void executeQuery()
{
    criteriaDatesCredit.value(SysQuery::range(startDate.dateValue(),endDate.dateValue()));

    if (dimAttrRecId)
    {
        SysQuery::clearDimensionRangesFromQuery(this.query());

        SysQuery::addDimensionAttributeRange(this.query(),
            this.query().dataSourceNo(1).name(),
            fieldStr(VendTrans, DefaultDimension),
            DimensionComponent::DimensionAttribute,
            PurchAgreementHeaderExt.AgreementId,
            DimensionAttribute::find(dimAttrRecId).Name);
    }

    super();
}

