/// <summary>
/// Check asset status.
/// If the calculation method is the first calculation method and there is asset not acquired, throw a warning message.
/// </summary>
public void checkAssetStatus()
{
    AssetDocumentEntry_JP   assetDocumentEntryLocal;
    AssetBook               assetBook;

    if (AssetDocumentAcceleratedDepTable_JP.CalculationMethod == AssetAcceleratedDepCalcMethod_JP::FirstCalculationMethod)
    {
        select firstonly RecId from assetDocumentEntryLocal
            exists join assetBook
            where assetBook.AssetId == assetDocumentEntryLocal.AssetId
                && assetBook.BookId == assetDocumentEntryLocal.BookId
                && assetBook.Status == AssetStatus::NoAcquisition
                && assetDocumentEntryLocal.AssetDocumentTable_JP == AssetDocumentAcceleratedDepTable_JP.AssetDocumentTable_JP;
        if (assetDocumentEntryLocal.RecId)
        {
            infolog.add(Exception::Warning, "@FixedAssetsIncentiveDepreciation:AcceleratedDepAssetsNotAcquired");
        }
    }
}

