display AssetName assetName(AssetDocumentEntry_JP _docEntry)
{
    AssetTable assetTable;

    select firstonly Name from assetTable where assetTable.AssetId == _docEntry.AssetId;

    return assetTable.Name;
}

display AssetStatus assetStatus(AssetDocumentEntry_JP _docEntry)
{
    AssetBook assetBook;

    select firstonly Status from assetBook
        where assetBook.AssetId == _docEntry.AssetId
            && assetBook.BookId == _docEntry.BookId;

    return assetBook.Status;
}

