Method.Source:
[Form]
public class AssetBook extends FormRun implements OfficeIMenuCustomizer
{
    #isoCountryRegionCodes
    AssetDepreciationProfile assetDepreciationProfile_old;
    AssetDepreciationProfile assetDepreciationAltProfile_old;
    AssetDepreciationProfile assetDepreciationExtProfile_old;
    AssetDepreciationProfile assetDepreciationAcceleratedProfile_JP_old;
    AssetParameters assetParameters;

    AssetReasonCode assetReasonCode;
    boolean reasonPrompted;

    boolean isJPN;
    boolean isIsoRU;

    RAssetPurchTrans assetPurchTrans;
    Integer lastSelection;
    QueryBuildRange criteriaDeferralsAccount;

    private boolean unreversedDepreciationExists;

}

public boolean canClose()

{
    boolean ret;
    AssetBook          mainAssetBookTable;
    AssetBook          localAssetBookTable;
    #ISOCountryRegionCodes

    ret = super();

    // Verify that French Derogatory Depreciation value models have been properly set up.
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR]))
    {
        // Check to see if any of the tax and accounting records have proper settings for
        // derogatory depreciation.
        //  Check the accounting value models
        while select AssetDerogatoryModel_FR, AssetId, Depreciation, BookId from mainAssetBookTable
                where mainAssetBookTable.AssetDerogatoryModel_FR
                && mainAssetBookTable.AssetId == assetBook.AssetId
        {
            // Verify that depreciation checkbox is selected.
            if (!mainAssetBookTable.Depreciation)
            {
                // "The Depreciation option needs to be selected for value model '%1'."
                warning(strFmt("@FixedAssets:FA_MessageValidation_MustSelectDepreciationOptionForDerogatoryBook", mainAssetBookTable.BookId));
                return false;
            }

            select firstonly localAssetBookTable
                where localAssetBookTable.BookId == mainAssetBookTable.AssetDerogatoryModel_FR
                    && mainAssetBookTable.AssetId == localAssetBookTable.AssetId;

            // If the this value model was setup for Derogation calculations
            if (!localAssetBookTable.RecId)
            {
                // The derogatory depreciation tax book '%1' does not exist for for the accounting value model '%2' of the Asset '%3'.
                warning(strFmt("@FixedAssets:FA_MessageValidation_DerogatoryTaxModelSpecifiedOnBookIsNotSpecifiedOnAsset", mainAssetBookTable.AssetDerogatoryModel_FR, mainAssetBookTable.BookId, mainAssetBookTable.AssetId));
                return false;
            }
        }

        //  Check the tax value models
        while select IsDerogatoryBook_FR, AssetId, Depreciation, BookId from mainAssetBookTable
            where mainAssetBookTable.IsDerogatoryBook_FR
            && mainAssetBookTable.AssetId == assetBook.AssetId
        {
            // Verify that depreciation checkbox is selected.
            if (!mainAssetBookTable.Depreciation)
            {
                // The Depreciation option needs to be selected for value model '%1'.
                warning(strFmt("@FixedAssets:FA_MessageValidation_MustSelectDepreciationOptionForDerogatoryBook", mainAssetBookTable.BookId));
                return false;
            }

            select firstonly RecId from localAssetBookTable
                where localAssetBookTable.AssetDerogatoryModel_FR == mainAssetBookTable.BookId
                    && mainAssetBookTable.AssetId == localAssetBookTable.AssetId;

            // If the this value model was setup for Derogation calculations
            if (!localAssetBookTable.RecId)
            {
                // The derogatory tax model '%1' is not assigned to any value models of '%2' asset.
                warning(strFmt("@FixedAssets:FA_MessageValidation_DerogatoryTaxModelIsNotAssignedToAnyBook", mainAssetBookTable.BookId, mainAssetBookTable.AssetId));
                return false;
            }
        }
    }
    return ret;
}

public void init()
{
    if (!element.args()
        || (element.args().dataset() != tableNum(AssetTable)
        && element.args().dataset() != tableNum(AssetBook)))
    {
        throw error(strFmt("@SYS25516",element.name()));
    }

    //<GJP>
    isJPN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]);
    //</GJP>
    isIsoRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);

    if (isIsoRU)
    {
        Object formCaller = element.args().caller();
        if (formCaller
            && SysDictClass::isSuperclass(classIdGet(formCaller),classNum(FormRun))
            && formCaller.name() == formStr(AssetTable))
        {
            assetPurchTrans = formCaller.getPurchSource_RU();
        }
    }

    super();

    navigationList.visible(true);

    // <GIN>
    if (AssetParameters::isDepreciationCompaniesActEnable_IN())
    {
        shiftDepreciation.visible(true);
    }

    if (AssetParameters::isDepreciationIncomeTaxActEnable_IN())
    {
        AssetBook_ds.object(fieldNum(AssetBook, AssetGroupDepreciation_IN)).visible(true);
        AssetBook_ds.refresh();
        assetGroupBalance.visible(true);
        assetGroupProfile.visible(true);
        AssetSpecialDepProfile_IN.visible(true);
    }
    // </GIN>

    // <GJP>
    if (AssetParameters::isManuallyChangeDepProfileEnable_JP())
    {
        AssetDepProfileChange_JP.visible(true);
        AssetBookTable_CurrentDepreciationProfile_JP.visible(true);
        AssetBook_CurrentServiceLife_JP.visible(true);
        AssetBook_CurrentLifeTime_JP.visible(true);
        AssetBook_ds.object(fieldnum(AssetBook, DepreciationProfile)).visible(false);
        AssetBook_ds.object(fieldNum(AssetBook, ServiceLife)).visible(false);
        AssetBook_ds.object(fieldNum(AssetBook, LifeTime)).visible(false);
    }
    // </GJP>

    assetParameters = AssetParameters::find();
    assetReasonCode = AssetReasonCode::construct();

    // <GEEU>
    acquisition_CategorizationDate_CZ.visible(AssetParameters::find().AllowPreAcquisition_CZ != AssetAllowPreAcquisition_CZ::No);
    // </GEEU>

    // <GEEPL>
    this.modifyFields_PL();
    // </GEEPL>

    if (assetParameters.AssetBookInSingleUse == NoYes::Yes)
    {
        AssetBookInSingleUseMode.visible(true);
    }

    this.enableApplyDepreciationAdjSameDate();
    this.hidePositiveDepreciationForLocalizationScenarios();
    this.enableNetBookValueSettings(!assetBook.IsPositiveDepreciation);
}

/// <summary>
/// Customizes the options used to populate the Office Menu with the latest version of the entity.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        dataEntityOptionsIterator.delete();
    }

    _menuOptions.dataEntityOptions().addEnd(OfficeMenuDataEntityOptions::construct(tableStr(AssetBookV2Entity)));
}

public void modifyFields_PL()
{
    boolean fieldstat;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        fieldstat = assetBook.Status == AssetStatus::NoAcquisition ? true : false;

        acquisition_DeprPrevPeriodsNonCost_PL.allowEdit(fieldstat);
        acquisition_DeprPrevPriorCost_PL.allowEdit(fieldstat);
        acquisition_DeprPrevPriorNonCost_PL.allowEdit(fieldstat);

        // Depreciation group is only allowed for Polish deprecation groups.
        depreciationGroup_DepreciationGroupId_W.allowEdit(assetBook.isPrimaryProfilePolishDepreciationMethod());

        if (assetBook.DepreciationGroupId_W)
        {
            acquisition_DeprPrevPeriodsNonCost_PL.visible(true);
            acquisition_DeprPrevPriorNonCost_PL.visible(true);
        }
        else
        {
            acquisition_DeprPrevPeriodsNonCost_PL.visible(false);
            acquisition_DeprPrevPriorNonCost_PL.visible(false);
        }
    }
}

display AssetDepreciation nextDepreciationAmount_PL()
{
    AssetTableMethod            assetTableMethod;
    AssetTableMethod            assetTableMethodAlt;
    AssetTableInterval          assetTableInterval;
    TransDate                   deprDate;
    AssetAmount                 deprPrim;
    AssetAmount                 deprSec;
    AssetAmount                 ret;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        if (assetBook.Status == AssetStatus::NoAcquisition
            && assetBook.AcquisitionPrice <= assetBook.DeprPrevPeriods_PL)
        {
            return 0;
        }

        if (assetBook.Status == AssetStatus::Scrapped ||
            assetBook.Status == AssetStatus::Sold)
        {
            return 0;
        }

        if (!assetBook.LastDepreciationDate && !assetBook.DepreciationStartDate)
        {
            return 0;
        }

        assetTableInterval = AssetTableInterval::construct(assetBook.depreciationProfile());
        deprDate = (assetBook.LastDepreciationDate ?
                    assetTableInterval.endPeriod(nextMth(assetBook.LastDepreciationDate)) :
                    assetTableInterval.endPeriod(assetBook.DepreciationStartDate));

        if (assetBook.depreciationAltProfile())
        {
            assetTableMethod    = AssetTableMethod::construct(assetBook,deprDate,'',true);
            assetTableMethodAlt = AssetTableMethod::construct(assetBook,deprDate,'',true,true);
            assetTableMethodAlt.parmUseFactor_PL(true);
            deprPrim            = assetTableMethod.calc();
            deprSec             = assetTableMethodAlt.calc();
            ret                 = max(deprPrim, deprSec);
        }
        else
        {
            assetTableMethod = AssetTableMethod::construct(assetBook,deprDate);
            ret              =  assetTableMethod.calc();
        }

        if (assetBook.Status != AssetStatus::NoAcquisition)
        {
            deprPrim = AssetSumCalc_Trans::newAssetPeriod(assetBook.AssetId, assetBook.BookId, dateNull(), maxDate()).netBookValue();
            if (ret > deprPrim)
            {
                ret = deprPrim;
            }
        }
    }

    return ret;
}

void refreshAssetTable()
{
    AssetITable formAssetTable;
    FormRun callerForm = element.args().caller();

    if (callerForm)
    {
        if (callerForm.name() == formStr(AssetTable))
        {
            formAssetTable = callerForm as AssetITable;
            formAssetTable.refreshAssetTable();
        }
    }
}

/// <summary>
/// Sets the edit state of the <c>AssetBook</c> table's <c>AcquisitionDate</c> field dependent controls.
/// </summary>
/// <remarks>
/// If the value model is used as a derogatory book the field is synchronized from the accounting value model. This
/// synchronization requires the control to be read only.
/// </remarks>
public void setAcquisitionDateCtrlEditState()
{
    boolean isEnabled;

    if (assetBook.IsDerogatoryBook_FR)
    {
        // This is a derogatory book, so force the control's enabled state to false.
        isEnabled = false;
    }
    else
    {
        // This is not a derogatory book, so force the control's enabled state back to true.
        isEnabled = true;
    }

    // Set the edit state of the AcquisitionDate control.
    acquisition_AcquisitionDate.allowEdit(isEnabled);
    acquisition_AcquisitionDate.enabled(isEnabled);
    acquisition_AcquisitionDate.skip(!isEnabled);
}

/// <summary>
/// Sets the edit state of the <c>AssetBook</c> table's <c>AcquisitionPrice</c> field dependent controls.
/// </summary>
/// <remarks>
/// If the value model is used as a derogatory book the field is synchronized from the accounting value model. This
/// synchronization requires the control to be read only.
/// </remarks>
public void setAcquisitionPriceCtrlEditState()
{
    boolean isEnabled;

    if (assetBook.IsDerogatoryBook_FR)
    {
        // This is a derogatory book, so force the control's enabled state to false.
        isEnabled = false;
    }
    else
    {
        // This is not a derogatory book, so force the control's enabled state back to true.
        isEnabled = true;
    }
    boolean isEnabledReportingCurrency = element.enableReportingCurrency(isEnabled);

    // Set the edit state of the AcquisitionPrice control.
    acquisition_AcquisitionPrice.allowEdit(isEnabled);
    acquisition_AcquisitionPrice.enabled(isEnabled);
    acquisition_AcquisitionPrice.skip(!isEnabled);
    acquisition_AcquisitionPriceReportingCurrency.allowEdit(isEnabledReportingCurrency);
    acquisition_AcquisitionPriceReportingCurrency.enabled(isEnabledReportingCurrency);
    acquisition_AcquisitionPriceReportingCurrency.skip(!isEnabledReportingCurrency);
}

void setAltPeriodFrequency()
{
    switch (assetDepreciationAltProfile.DepreciationYear)
    {
        case AssetDepreciationYear::Calendar:
            depreciationAltCalendar.visible(true);
            depreciationAltFiscal.visible(false);
            break;
        case AssetDepreciationYear::Fiscal:
            depreciationAltCalendar.visible(false);
            depreciationAltFiscal.visible(true);
            break;
    }
}

/// <summary>
/// Sets the enabled state of the AssetSpecialDepreciationProfile controls
/// </summary>
public void setAssetSpecialDepreProfileEnabled_IN()
{
    boolean enabled = false;

    if (AssetParameters::isDepreciationIncomeTaxActEnable_IN()  &&
        (AssetBookTable.CurrentOperationsTax == CurrentOperationsTax::Tax || AssetBookTable.CurrentOperationsTax == CurrentOperationsTax::None) &&
        AssetBook.Status == AssetStatus::Open                   &&
        AssetBook.AssetGroupDepreciation_IN == NoYes::Yes       &&
        AssetBook.AssetDepBookMap_IN::bonusCount()              &&
        AssetBook.AssetDepBookMap_IN::hasBonusRecords())
    {
        enabled = true;
    }

    AssetSpecialDepProfile_IN.enabled(enabled);
}

/// <summary>
/// Initializes the <c>DepreciationConvention</c> if necessary.
/// </summary>
void setDepreciationConvention()
{
    switch (assetDepreciationProfile.Accrual)
    {
        case AssetAccrual::Daily:
            assetBook.DepreciationConvention = AssetDepreciationConvention::None;
            break;
    }
}

/// <summary>
/// Sets the edit state of the <c>AssetBook</c> table's <c>DepreciationConvention</c> field dependent controls.
/// </summary>
/// <remarks>
/// If the value model is used as a derogatory book the field is synchronized from the accounting value model. This
/// synchronization requires the control to be read only.
/// </remarks>
public void setDepreciationConventionCtrlEditState()
{
    boolean isEnabled;

    if (assetBook.IsDerogatoryBook_FR || assetDepreciationProfile.Accrual == AssetAccrual::Daily)
    {
        // This is a derogatory book or a daily accrual, so force the control's enabled state to false.
        isEnabled = false;
    }
    else
    {
        // This is not a derogatory book, so decide what edit state the control should be in.
        if (assetBook.checkIfConventionCanBeSet() &&
           !AssetTrans::existDepreciationTrans(assetBook.AssetId, assetBook.BookId) &&
           !LedgerJournalTrans_Asset::existDepreciationJournals(assetBook.AssetId, assetBook.BookId))
        {
            isEnabled = true;
        }
        else
        {
            isEnabled = false;
        }
    }

    // Set the edit state of the DepreciationConvention control.
    depreciationGroup_depreciationConvention.allowEdit(isEnabled);
    depreciationGroup_depreciationConvention.enabled(isEnabled);
    depreciationGroup_depreciationConvention.skip(!isEnabled);
}

/// <summary>
/// Sets the edit state of the <c>AssetBook</c> table's <c>DepreciationStartDate</c> field dependent controls.
/// </summary>
/// <remarks>
/// If the value model is used as a derogatory book the field is synchronized from the accounting value model. This
/// synchronization requires the control to be read only.
/// </remarks>
public void setDepStartDateCtrlEditState()
{
    boolean isEnabled;

    if (assetBook.IsDerogatoryBook_FR)
    {
        // This is a derogatory book, so force the control's enabled state to false.
        isEnabled = false;
    }
    else
    {
        // This is not a derogatory book, so decide what edit state the control should be in.
        if (unreversedDepreciationExists || assetBook.DepreciationConvention != AssetDepreciationConvention::None)
        {
            isEnabled = false;
        }
        else
        {
            isEnabled = true;
        }
    }

    // Set the enabled state of the depreciation start date control on the grid.
    assetBook_DepreciationStartDate.allowEdit(isEnabled);
    assetBook_DepreciationStartDate.enabled(isEnabled);
    assetBook_DepreciationStartDate.skip(!isEnabled);

    // Set the enabled state of the depreciation start date control on the depreciation tab.
    depreciationStartDate.allowEdit(isEnabled);
    depreciationStartDate.enabled(isEnabled);
    depreciationStartDate.skip(!isEnabled);
}

void setExtPeriodFrequency()
{
    switch (assetDepreciationExtProfile.DepreciationYear)
    {
        case AssetDepreciationYear::Calendar:
            depreciationExtCalendar.visible(true);
            depreciationExtFiscal.visible(false);
            break;
        case AssetDepreciationYear::Fiscal:
            depreciationExtCalendar.visible(false);
            depreciationExtFiscal.visible(true);
            break;
    }
}

/// <summary>
/// Sets the state of the financial dimension control.
/// </summary>
/// <remarks>
/// If the value model does not allow ledger posting then we can hide this tab.
/// </remarks>
private void setTabFinancialDimensionsControlEditState()
{
    boolean isEnabled = assetBookTable.postToGeneralLedger();

    TabFinancialDimensions.allowEdit(isEnabled);
    TabFinancialDimensions.enabled(isEnabled);
    TabFinancialDimensions.skip(!isEnabled);
}

private void setPostingProfileState()
{
    FormDataObject postingProfileObject = AssetBook_ds.object(fieldNum(AssetBook, PostingProfile));
    boolean postToGL = (assetBookTable.postToGeneralLedger() == NoYes::Yes);

    postingProfileObject.mandatory(postToGL);
    postingProfileObject.allowEdit(postToGL);
}

void setFieldsAssetDepreciationAltProfile()
{
    boolean     switchPercentage = false;
    str         percLabel        = "";

    switch (assetDepreciationAltProfile.Method)
    {
        case AssetDepreciationMethod::StraightLine:
        case AssetDepreciationMethod::StraightLineLifeRemaining:
        case AssetDepreciationMethod::Manual:
            // <GCN>
        case AssetDepreciationMethod::DoubleReduceBalance_CN:
        case AssetDepreciationMethod::SumYearDigits_CN:
            // </GCN>
            // <GEEPL>
        case AssetDepreciationMethod::StraightLine_PL:
            // </GEEPL>
            // <GEEHU>
        case AssetDepreciationMethod::StraightLine_HU:
            // </GEEHU>
            depreciationAltInterval.visible(false);
            depreciationAltYear.visible(true);
            element.setAltPeriodFrequency();
            break;

        case AssetDepreciationMethod::ReducingBalance:
            // <GIN>
        case AssetDepreciationMethod::StraightLinePercentage_IN:
            // </GIN>
            // <GEEPL>
        case AssetDepreciationMethod::ReducingBalance_PL:
        case AssetDepreciationMethod::StraightLinePercentage_PL:
            // </GEEPL>
            // <GEEHU>
        case AssetDepreciationMethod::ReducingBalance_HU :
            // </GEEHU>
            switchPercentage = true;
            percLabel = "@SYS67344";
            depreciationAltInterval.visible(false);
            depreciationAltYear.visible(true);
            element.setAltPeriodFrequency();
            break;

        case AssetDepreciationMethod::ReducingBalance125:
        case AssetDepreciationMethod::ReducingBalance150:
        case AssetDepreciationMethod::ReducingBalance175:
        case AssetDepreciationMethod::ReducingBalance200:
            // <GJP>
        case AssetDepreciationMethod::OldStraightLine_JP:
        case AssetDepreciationMethod::OldDecliningBalance_JP:
        case AssetDepreciationMethod::NewDecliningBalance200_JP:
        case AssetDepreciationMethod::NewDecliningBalance250_JP:
        case AssetDepreciationMethod::NewStraightLine_JP:
        case AssetDepreciationMethod::EquallyDivided_JP:
            // </GJP>
            depreciationAltInterval.visible(false);
            depreciationAltYear.visible(true);
            element.setAltPeriodFrequency();
            break;

        case AssetDepreciationMethod::Factor:
            // <GEEHU>
        case AssetDepreciationMethod::Factor_HU:
            // </GEEHU>
            switchPercentage = true;
            percLabel = "@SYS1187";
            depreciationAltInterval.visible(true);
            depreciationAltYear.visible(false);
            depreciationAltCalendar.visible(false);
            depreciationAltFiscal.visible(false);
            break;

        case AssetDepreciationMethod::Consumption:
            depreciationAltInterval.visible(false);
            depreciationAltYear.visible(false);
            depreciationAltCalendar.visible(false);
            depreciationAltFiscal.visible(false);
            break;
    }

    depreciationAltPercentage.visible(switchPercentage);
    depreciationAltPercentage.label(percLabel);
}

