            depreciationInterval.visible(false);
            depreciationYear.visible(false);
            depreciationCalendar.visible(false);
            depreciationFiscal.visible(false);
            break;

            // <GEECZ>
        case AssetDepreciationMethod::Regular_CZ:
        case AssetDepreciationMethod::Accelerated_CZ:
            //assetBook_DS.object(fieldnum(AssetBook, DepreciationGroupId_W)).mandatory(true);
            depreciationInterval.visible(false);
            depreciationYear.visible(true);
            element.setPeriodFrequency();
            break;
            // </GEECZ>
            // <GEEHU>
        case AssetDepreciationMethod::MultiplicationFactor_HU:
            depreciationInterval.visible(false);
            depreciationYear.visible(true);
            element.setPeriodFrequency();
            break;
            // </GEEHU>
    }

    depreciationPercentage.visible(switchPercentage);
    depreciationPercentage.label(percLabel);
}

/// <summary>
/// Sets state of special depreciation fields.
/// </summary>
/// <param name="_visible">
/// Whether special depreciation fields are visible or not.
/// </param>
private void setFieldsExtSpecialDepreciation_JP(boolean _visible)
{
    boolean     showAllocFields = (_visible && assetDepreciationExtProfile.SpecialDepAccountingMethod_JP == AssetSpecialDepAccountingMethod_JP::Reserve);

    specialDepAccountingMethod_JP.visible(_visible);
    specialDepAllocationPeriods_JP.visible(showAllocFields);
    specialDepAllocationUnit_JP.visible(showAllocFields);
    SpecialDepAllocationConvention_JP.visible(showAllocFields);

    if (showAllocFields && AssetBook.SpecialDepAllocationUnit_JP == AssetAccrualFiscal::Yearly)
    {
        SpecialDepAllocationConvention_JP.allowEdit(false);
    }
    else
    {
        SpecialDepAllocationConvention_JP.allowEdit(true);
    }
}

private void setLifeTimeFieldsState(boolean _enableField, boolean _visableField = true)
{
    // Fields in the Deprecation tab
    assetBook_LifeTime.enabled(_enableField);
    assetBook_LifeTime.allowEdit(_enableField);
    assetBook_LifeTime.skip(!_enableField);

    // Fields in the Overview grid
    lifeTime.enabled(_enableField);
    lifeTime.allowEdit(_enableField);
    lifeTime.skip(!_enableField);

    lifeTime.visible(_visableField);
}

private void setLifeTimeRestFieldsState(boolean _enableField, boolean _visableField = true)
{
    // Fields in the Deprecation tab
    assetBook_LifeTimeRest.enabled(_enableField);
    assetBook_LifeTimeRest.allowEdit(_enableField);
    assetBook_LifeTimeRest.skip(!_enableField);

    // Fields in the Overview grid
    lifeTimeRest.enabled(_enableField);
    lifeTimeRest.allowEdit(_enableField);
    lifeTimeRest.skip(!_enableField);

    lifeTimeRest.visible(_visableField);
}

void setPeriodFrequency()
{
    switch (assetDepreciationProfile.DepreciationYear)
    {
        case AssetDepreciationYear::Calendar:
            depreciationCalendar.visible(true);
            depreciationFiscal.visible(false);
            break;
        case AssetDepreciationYear::Fiscal:
            depreciationCalendar.visible(false);
            depreciationFiscal.visible(true);
            break;
    }
}

/// <summary>
/// Sets the edit state of the <c>AssetBook</c> table's <c>ScrapValue</c> field dependent controls.
/// </summary>
/// <remarks>
/// ScrapValue must be disabled for derogatory books.
/// </remarks>
public void setScrapValueCtrlEditState()
{
    boolean isEnabled;        

    if (assetBook.IsDerogatoryBook_FR)
    {
        // This is a derogatory book, so force the control's enabled state to false.
        isEnabled = false;
    }
    else
    {
        // This is not a derogatory book, so force the control's enabled state back to true.
        isEnabled = true;
    }

    boolean isEnabledReportingCurrency = this.enableReportingCurrency(isEnabled);
    // Set the edit state of the ScrapValue control.
    scrapValue.allowEdit(isEnabled);
    scrapValue.enabled(isEnabled);
    scrapValue.skip(!isEnabled);
    ScrapValueReportingCurrency.allowEdit(isEnabledReportingCurrency);
    ScrapValueReportingCurrency.enabled(isEnabledReportingCurrency);
    ScrapValueReportingCurrency.skip(!isEnabledReportingCurrency);
}

private void setServiceLifeFieldsState(boolean _enableField, boolean _visableField = true)
{
    // Fields in the Deprecation tab
    assetBook_ServiceLife.enabled(_enableField);
    assetBook_ServiceLife.allowEdit(_enableField);
    assetBook_ServiceLife.skip(!_enableField);

    // Fields in the Overview grid
    serviceLife.enabled(_enableField);
    serviceLife.allowEdit(_enableField);
    serviceLife.skip(!_enableField);

    serviceLife.visible(_visableField);
}

/// <summary>
/// Sets the enabled state of the ShiftDepreciation controls
/// </summary>
public void setShiftDepreciationEnabled_IN()
{
    if (AssetParameters::isDepreciationCompaniesActEnable_IN() && assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Current)
    {
        shiftDepreciation.enabled(assetBook.depreciationProfile().ShiftDepreciation_IN);
    }
    else
    {
        shiftDepreciation.enabled(false);
    }
}

/// <summary>
/// Sets the edit state of the <c>AssetBook</c> table's <c>UsedFromDate</c> field dependent controls.
/// </summary>
/// <remarks>
/// If the value model is used as a derogatory book the field is synchronized from the accounting value model. This
/// synchronization requires the control to be read only.
/// </remarks>
public void setUsedFromDateCtrlEditState()
{
    boolean isEnabled;

    if (assetBook.IsDerogatoryBook_FR)
    {
        // This is a derogatory book, so force the control's enabled state to false.
        isEnabled = false;
    }
    else
    {
        // This is not a derogatory book, so decide what edit state the control should be in.
        if (unreversedDepreciationExists)
        {
            isEnabled = false;
        }
        else
        {
            isEnabled = true;
        }
    }

    // Set the edit state of the UsedFromDate control.
    usedFromDate.allowEdit(isEnabled);
    usedFromDate.enabled(isEnabled);
    usedFromDate.skip(!isEnabled);
}

private void setPositiveDepreciationCtrlEditState()
{
    AssetBook_ds.object(fieldNum(AssetBook, IsPositiveDepreciation)).allowEdit(!unreversedDepreciationExists);
    AssetBook_ds.object(fieldNum(AssetBook, IsPositiveDepreciation)).enabled(!unreversedDepreciationExists);
    AssetBook_ds.object(fieldNum(AssetBook, IsPositiveDepreciation)).skip(unreversedDepreciationExists);
}

/// <summary>
/// Updates the UI's state.
/// </summary>
public void updateDesign()
{
    AssetStatusType assetStatusType = AssetStatusType::construct(assetBook.Status);
    boolean isMethodDeReducingBalance;

    // Enable/disable buttons.
    scrapValue.allowEdit(!assetStatusType.isDisposed());
    statusBox.allowEdit(assetStatusType.canChangeStatus());
    assetBook_status.allowEdit(assetStatusType.canChangeStatus());

    // Update design.
    if (assetDepreciationProfile.Method    == AssetDepreciationMethod::Consumption ||
        assetDepreciationAltProfile.Method == AssetDepreciationMethod::Consumption)
    {
        boolean showReportingCurrencyControl;
        if (Ledger::reportingCurrency() && Ledger::accountingCurrency() != Ledger::reportingCurrency())
        {
            showReportingCurrencyControl = true;
        }

        consumptionDepreciation.visible(true);
        consumptionFactorId.skip(false);
        consumptionUnitId.skip(false);
        depreciationAmountUnit.skip(false);
        depreciationAmountUnitReporting.visible(showReportingCurrencyControl);
        depreciationAmountUnitReporting.skip(!showReportingCurrencyControl);
        consumptionQtyEstimated.skip(false);
        consumptionQtyPosted.skip(false);
    }
    else
    {
        consumptionDepreciation.visible(false);
        consumptionFactorId.skip(true);
        consumptionUnitId.skip(true);
        depreciationAmountUnit.skip(true);
        depreciationAmountUnitReporting.skip(true);
        consumptionQtyEstimated.skip(true);
        consumptionQtyPosted.skip(true);
    }

    // Show calcuation base/ Full depreciation field only for manual method
    if (assetDepreciationProfile.Method == AssetDepreciationMethod::Manual)
    {
        calculationBase.visible(true);
        endDependOnServiceLife.visible(true);
    }
    else
    {
        calculationBase.visible(false);
        endDependOnServiceLife.visible(false);
    }

    isMethodDeReducingBalance = (assetParameters.CheckMaxPercentRB
        && assetDepreciationProfile.Method == AssetDepreciationMethod::ReducingBalance);

    depreciationGroup_CheckMaxPercentRB.visible(isMethodDeReducingBalance);
    depreciationGroup_MaxPercentRB.visible(isMethodDeReducingBalance);
    assetBook_CheckMaxPercentRB.allowEdit(isMethodDeReducingBalance);

    element.setFieldsAssetDepreciationProfile();
    if (assetBook.DepreciationAltProfile)
    {
        depreciationAltProfileDependent.visible(true);
        element.setFieldsAssetDepreciationAltProfile();
    }
    else
    {
        depreciationAltProfileDependent.visible(false);
    }

    // <GJP>
    if (isJPN)
    {
        ExtraordinaryDepreciation_SpecialDepStartDate_JP.visible(false);
        ExtraordinaryDepreciation_SpecialDepLastAllocationDate_JP.visible(false);

        if (assetBook.DepreciationExtProfile)
        {
            element.setFieldsAssetDepreciationExtProfile();
        }
        else
        {
            depreciationExtMethod.visible(false);
            depreciationExtYear.visible(false);
            depreciationExtInterval.visible(false);
            depreciationExtPercentage.visible(false);
            depreciationExtCalendar.visible(false);
            depreciationExtFiscal.visible(false);
            element.setFieldsExtSpecialDepreciation_JP(false);
        }
    }
    else
    {
        // </GJP>
        if (assetBook.DepreciationExtProfile)
        {
            depreciationExtProfileDependent.visible(true);
            element.setFieldsAssetDepreciationExtProfile();
        }
        else
        {
            depreciationExtProfileDependent.visible(false);
        }
        // <GJP>
    }
    // </GJP>

    // <GJP>
    if (assetBook.DepreciationAcceleratedProfile_JP)
    {
        DepreciationAcceleratedProfileDependent_JP.visible(true);
    }
    else
    {
        DepreciationAcceleratedProfileDependent_JP.visible(false);
    }
    // </GJP>

    // Default in the depreciation convention
    this.setDepreciationConvention();

    // Update control states
    unreversedDepreciationExists = assetBook.unreversedDepreciationExists();
    this.setDepStartDateCtrlEditState();
    this.setUsedFromDateCtrlEditState();
    this.setPositiveDepreciationCtrlEditState();

    this.setDepreciationConventionCtrlEditState();
    this.setAcquisitionDateCtrlEditState();
    this.setAcquisitionPriceCtrlEditState();
    this.setScrapValueCtrlEditState();
    this.setTabFinancialDimensionsControlEditState();
    this.setPostingProfileState();
    this.setAssetBudgetButtonEnabled();
    this.setMigratedBook();

    //enable or disable fields for low value pools
    switch (assetDepreciationProfile.Method)
    {
        case AssetDepreciationMethod::LowValuePool:

            element.setLifeTimeRestFieldsState(false);
            element.setServiceLifeFieldsState(false);
            element.setLifeTimeFieldsState(false);
            checkNetBookValue_NegativeNetBookValue.allowEdit(false);
            checkNetBookValue_NegativeNetBookValue.enabled(false);
            checkNetBookValue_NegativeNetBookValue.skip(true);
            scrapValue.allowEdit(false);
            scrapValue.enabled(false);
            scrapValue.skip(true);
            break;

        case AssetDepreciationMethod::Consumption:
            element.setLifeTimeRestFieldsState(false, false);
            element.setServiceLifeFieldsState(false, false);
            element.setLifeTimeFieldsState(false, false);
            break;

        case AssetDepreciationMethod::RBSL:
            // If the RBSL depreciation profile is selected, ServiceLife and LifeTime fields need to be disabled.
            depreciationRBSL.visible(true);
            element.setLifeTimeRestFieldsState(true);
            element.setServiceLifeFieldsState(false);
            element.setLifeTimeFieldsState(false);
            break;

        default:

            element.setLifeTimeRestFieldsState(true);
            element.setServiceLifeFieldsState(true);
            element.setLifeTimeFieldsState(true);
            checkNetBookValue_NegativeNetBookValue.allowEdit(true);
            checkNetBookValue_NegativeNetBookValue.enabled(true);
            checkNetBookValue_NegativeNetBookValue.skip(false);
            scrapValue.allowEdit(true);
            scrapValue.enabled(true);
            scrapValue.skip(false);
            depreciationRBSL.visible(false);
            break;
    }

    // <GCN>
    if (AssetParameters::isDepreciationPolicyEnabled()
        && assetBookTable.ExpectedScrapRate_CN > 0
        && scrapValue.enabled())
    {
        scrapValue.enabled(false);
    }
    // </GCN>

    // <GEEU>
    acquisition_CategorizationDate_CZ.allowEdit(! assetStatusType.isAcquired());
    // </GEEU>

    if (AssetDepreciationProfile::isAutoDeprAllowedForProfile(assetBook.DepreciationProfile))
    {
        depreciationGroup_IsTrueUpDepreciation.visible(true);
        depreciationGroup_IsDisposalSameYear.visible(true);
        depreciationGroup_IsPreventDepreciationAdj.visible(true);

        if (FeatureStateProvider::isFeatureEnabled(FixedAssetDisposalSameYearParametersFeature::instance()))
        {
            this.addWhatsNewNotificationForIsDisposalSameYear();
        }
    }
    else
    {
        depreciationGroup_IsTrueUpDepreciation.visible(false);
        depreciationGroup_IsDisposalSameYear.visible(false);
        depreciationGroup_IsPreventDepreciationAdj.visible(false);
    }

    // <GEEPL>
    this.updateDesign_Pl();
    // </GEEPL>

    // <GJP>
    if (AssetParameters::isManuallyChangeDepProfileEnable_JP())
    {
        AssetDepProfileChange_JP.enabled(
            AssetDepProfileChangeHelper_JP::isDepProfileChangeEnabled(assetBook.assetTable().AssetType, assetBook.DepreciationProfile()));
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]))
    {
        if (assetDepreciationProfile.Method == AssetDepreciationMethod::EquallyDivided_JP)
        {
            AssetBook_ds.object(fieldNum(AssetBook, AllowableLimitForAccumulatedDepType_JP)).allowEdit(false);
        }
        else
        {
            AssetBook_ds.object(fieldNum(AssetBook, AllowableLimitForAccumulatedDepType_JP)).allowEdit(true);
        }

        // Update design for pre-tax deduction feature.
        AssetDepPreTaxDeductionDetails_JP.enabled(AssetBook.taxLayerAssetBookForPreTaxDed_JP().RecId != 0);
    }
    // </GJP>
}

private void addWhatsNewNotificationForIsDisposalSameYear()
{
    const str isDisposalSameYearWhatsNewRuleId = '{D1BE8183-31E2-4B1F-AF36-A5224CBC0B2D}';

    SystemNotificationsWhatsNewManager::AddWhatsNewWithActionLink(
            isDisposalSameYearWhatsNewRuleId,
            '',
            "@FixedAssets:AssetBookIsDisposalSameYearWhatsNewSummary",
            depreciationGroup_IsDisposalSameYear.name(),
            'https://go.microsoft.com/fwlink/?linkid=2272665');
}

private void setValuesForPositiveDepreciation()
{
    if (assetBook.IsPositiveDepreciation)
    {
        AssetBook_ds.object(fieldNum(AssetBook, NegativeNetBookValue)).setValue(NoYes::Yes);
        AssetBook_ds.object(fieldNum(AssetBook, ExceedingNetBookValue)).setValue(NoYes::Yes);
    }

    this.enableNetBookValueSettings(!assetBook.IsPositiveDepreciation);
}

private void enableNetBookValueSettings(boolean _isEnabled = true)
{
    AssetBook_ds.object(fieldNum(AssetBook, NegativeNetBookValue)).enabled(_isEnabled);
    AssetBook_ds.object(fieldNum(AssetBook, ExceedingNetBookValue)).enabled(_isEnabled);
}

private void hidePositiveDepreciationForLocalizationScenarios()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP])
        || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL])
        || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR]))
    {
        AssetBook_ds.object(fieldNum(AssetBook, IsPositiveDepreciation)).setValue(NoYes::No);
        AssetBook_ds.object(fieldNum(AssetBook, IsPositiveDepreciation)).visible(false);
    }
}

private void enableApplyDepreciationAdjSameDate()
{
    if (assetBook.IsTrueUpDepreciation)
    {
        DepreciationGroup_IsPreventDepreciationAdj.allowEdit(true);
    }
    else
    {
        DepreciationGroup_IsPreventDepreciationAdj.allowEdit(false);
    }
}

private void setValueApplyDepreciationAdjSameDate()
{
    if (assetBook.IsTrueUpDepreciation)
    {
        DepreciationGroup_IsPreventDepreciationAdj.allowEdit(true);
    }
    else
    {
        AssetBook_ds.object(fieldNum(AssetBook, IsAllowDepreciationAdjSameDate)).setValue(NoYes::No);
        DepreciationGroup_IsPreventDepreciationAdj.allowEdit(false);
    }
}

void updateDesign_PL()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        depreciationPercentage.visible(false);
        depreciationAltPercentage.visible(false);
        depreciationExtPercentage.visible(false);
        depreciationYear.allowEdit(false);
        depreciationCalendar.allowEdit(false);
        depreciationFiscal.allowEdit(false);
        depreciationAltYear.allowEdit(false);
        depreciationAltCalendar.allowEdit(false);
        depreciationAltFiscal.allowEdit(false);
        depreciationExtYear.allowEdit(false);
        depreciationExtCalendar.allowEdit(false);
        depreciationExtFiscal.allowEdit(false);
    }
}

/// <summary>
/// Updates the UI's state based on value model.
/// </summary>
void updateDesignBasedOnValueModel_JP()
{
    AssetDepreciationProfile    assetDepreciationProfileCur;

    // Update for the field "EquallyDividedStartDate_JP"
    if (assetBook.BookId)
    {
        assetDepreciationProfileCur = assetBook.AssetDepBookMap_JP::depreciationProfileOnDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
        if ((assetDepreciationProfileCur.Method         == AssetDepreciationMethod::OldDecliningBalance_JP
                || assetDepreciationProfileCur.Method   == AssetDepreciationMethod::OldStraightLine_JP)
            && assetDepreciationProfileCur.NumOfYearsEquallyDivided_JP > 0)
        {
            // Start date of the %1 year equally divided method
            DepreciationGroup_EquallyDividedStartDate_JP.label(strFmt("@SYP4860150", AssetDepreciationProfile.NumOfYearsEquallyDivided_JP));
            // The start date from which the %1 year equally divided method is active
            DepreciationGroup_EquallyDividedStartDate_JP.helpText(strFmt("@SYP4860142", AssetDepreciationProfile.NumOfYearsEquallyDivided_JP));
            AssetBook_ds.object(fieldNum(AssetBook, EquallyDividedStartDate_JP)).visible(true);
        }
        else
        {
            AssetBook_ds.object(fieldNum(AssetBook, EquallyDividedStartDate_JP)).visible(false);
        }
    }
}

private void setAssetBudgetButtonEnabled()
{
    boolean buttonEnabled = (AssetBookTable.postToGeneralLedger() == NoYes::Yes);

    assetBudget.enabled(buttonEnabled);
}

private void setMigratedBook()
{
    MigratedAsset.allowEdit(!unreversedDepreciationExists);
}

private boolean enableReportingCurrency(boolean _isEnabled)
{
    boolean isEnabledReportingCurrency;

    if (_isEnabled && Ledger::reportingCurrency() && Ledger::reportingCurrency() != Ledger::accountingCurrency())
    {
        isEnabledReportingCurrency = true;
    }
    else
    {
        isEnabledReportingCurrency = false;
    }

    return isEnabledReportingCurrency;
}

public void selectionChanged()
{
    super();

    assetDepSuspension_CZ.enabled(assetBook.isDepreciationSuspendable());
}

public void initValue()
{
    AssetTable assetTable;
    #ISoCountryRegionCodes
    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#IsoHU, #IsoCZ]))
    {
        assetTable = AssetTable::find(assetBook.AssetId);

        assetBook.AcquisitionPrice      = assetTable.AcquisitionPrice_W;
        assetBook.AcquisitionDate       = assetTable.AcquisitionDate_W;
        assetBook.CategorizationDate_CZ = assetTable.AcquisitionDate_W;
    }
}

public void active_Pl()
{
    boolean fieldStat;
    AssetDepreciationMethod assetDepreciationMethod;
    AssetDepreciationMethod assetDepreciationAltMethod;
    AssetDepreciationMethod assetDepreciationExtMethod;

    fieldStat =  assetBook.Status == AssetStatus::NoAcquisition ? true : false;

    depreciationPercentage_PL.allowEdit(false);
    depreciationAltPercentage_PL.allowEdit(false);
    depreciationPercentageExt_PL.allowEdit(false);

    lifeTimeRest.visible(true);
    lifeTime.visible(true);

    assetDepreciationMethod = assetBook.depreciationMethod();
    if (assetDepreciationMethod == AssetDepreciationMethod::StraightLinePercentage_PL
        || assetDepreciationMethod == AssetDepreciationMethod::ReducingBalance_PL)
    {
        if (assetBook.Status == AssetStatus::Open
            || assetBook.Status == AssetStatus::NoAcquisition )
        {
            depreciationPercentage_PL.allowEdit(true);
        }
    }

    if (assetDepreciationMethod == AssetDepreciationMethod::StraightLinePercentage_PL)
    {
        lifeTimeRest.visible(false);
        lifeTime.visible(false);
    }

    assetDepreciationAltMethod = assetBook.depreciationAltMethod();
    if (assetDepreciationAltMethod == AssetDepreciationMethod::StraightLinePercentage_PL
     || assetDepreciationAltMethod == AssetDepreciationMethod::ReducingBalance_PL)
    {
        depreciationAltPercentage_PL.allowEdit(true);
    }

    assetDepreciationExtMethod = assetBook.depreciationExtProfile().Method;
    if (assetDepreciationExtMethod == AssetDepreciationMethod::StraightLinePercentage_PL
     || assetDepreciationExtMethod == AssetDepreciationMethod::ReducingBalance_PL)
    {
        depreciationPercentageExt_PL.allowEdit(true);
    }

    acquisition_DeprPrevPeriods_PL.allowEdit(fieldStat);
    acquisition_AcquisitionPrice.allowEdit(fieldStat);
    assetBook_depreciationStartDate.allowEdit(fieldStat);

    if (AssetTrans::amountMSTPerDateTranstype(assetBook.AssetId, maxDate(), assetBook.BookId, AssetTransType::Depreciation))
    {
        depreciationStartDate.allowEdit(false);
    }
    else
    {
        depreciationStartDate.allowEdit(true);
    }

    if (AssetBookLastDepreciationDatePLFlight::instance().isEnabled())
    {
        lastdepreciationDate.allowEdit(true);            
    }
    else
    {
        lastdepreciationDate.allowEdit(fieldStat);
    }

    acquisition_AssetPurchaseDate_PL.allowEdit(fieldStat);
    acquisition_AcquisitionDate.allowEdit(fieldStat);

    element.modifyFields_PL();
}

/// <summary>
/// Determines whether the current record is valid and ready to be written to the database.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
/// <remarks>
/// This method displays the Asset reason code dialog, if the 'Asset changes' attribute in
/// Asset parameters is checked.
/// </remarks>
public boolean validateWrite()
{
    boolean isValid;
    boolean needReason;             //True when a reason code is required

    isValid = super();

    //Determine if we need a reason code
    needReason = isValid && assetParameters.AssetChanges==NoYes::Yes && assetBook.RecId != 0 && assetReasonCode.checkFields(assetBook);

    //Need to make sure we prompt before the tts if we haven't already
    if (needReason && !reasonPrompted)
    {
        //Set prompted flag
        reasonPrompted = true;

        isValid = assetReasonCode.prompt();

        if (!isValid)
        {
            //If prompt is cancelled reset flag
            reasonPrompted = false;
        }
    }

    return isValid;
}

public void write()
{
    boolean doRefresh;
    // <GEEU>
    boolean isCountryRegionCZPL = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ,#isoPL]);
    AssetBook assetBookLocal;

    if (isCountryRegionCZPL)
    {
        assetBookLocal.data(assetBook);
    }
    // </GEEU>

    //AssetChangesHistory should be written in the same transaction as the AssetDepBook record
    ttsbegin;

    doRefresh = assetBook.RecId ? false : true;

    //Create reason code and assetChangesHistory records
    if (assetParameters.AssetChanges==NoYes::Yes && assetBook.RecId != 0 && assetReasonCode.checkFields(assetBook))
    {
        assetReasonCode.runOperation();
    }

    super();

    ttscommit;

    if (doRefresh)
    {
        element.refreshAssetTable();
    }

    // <GEEU>
    if (isCountryRegionCZPL &&
       (assetBook.orig().DepreciationGroupId_W != assetBook.DepreciationGroupId_W ||
        assetBook.orig().DeprPrevPeriods_PL    != assetBook.DeprPrevPeriods_PL))
    {
        element.args().lookupRecord(assetBookLocal);
        AssetBook_ds.research(false);
    }
    // </GEEU>

    if (isIsoRU && tabPageDeferrals_RU.visible())
    {
        RAssetDeferralsAccounts::initFromRassetDeferralsAccount(assetBook.BookId, assetBook.DepreciationGroupId_W, assetBook.AssetId);
        rAssetDeferralsAccounts_ds.research();
    }

    //Clear reasonPrompted flag after all ttscommits
    reasonPrompted = false;

    if (Acquisition_AcquisitionPriceReportingCurrency.enabled()
        && Acquisition_AcquisitionPriceReportingCurrency.realValue() == 0
        && Acquisition_AcquisitionPrice.realValue() != 0)
    {
        warning("@FixedAssets:FA_Form_AcquisitionPriceReportingCurrencyValidation");
    }

    if (ScrapValueReportingCurrency.enabled()
        && ScrapValueReportingCurrency.realValue() == 0
        && ScrapValue.realValue() != 0)
    {
        warning("@FixedAssets:FA_Form_ExpectedScrapValueReportingCurrencyValidation");
    }
}

public void delete()
{
    #ISOCountryRegionCodes

    super();
    element.refreshAssetTable();

    // An asset used for the French derogatory depreciation requires to have another value model used as
    // a derogatory book.  If a value model has a derogatory book, both records need to be deleted.
    // AssetBook.delete() method deletes the derogatory record, but the current data source needs to
    // be update to reflect the change in the current form.
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR]))
    {
        this.research();
    }
}

    public int active()
    {
        int ret;
        // <GJP>
        boolean            allowEditLoc = false;
        AssetDocument_JP   document;
        // </GJP>

        ret = super();

        assetBookTable.data(AssetBookTable::find(assetBook.BookId));
        // <GIN>
        element.setShiftDepreciationEnabled_IN();
        element.setAssetSpecialDepreProfileEnabled_IN();
        // </GIN>
        // The Derogatory Tax Model has information updated by the main derogatory value model, so
        // make sure that tax model has the latest information.
        if (assetBookTable.IsDerogatoryBook_FR)
        {
            AssetBook_ds.reread();
        }

        assetBookTable_ds.setCurrent();

        assetDepreciationProfile_old.data(assetDepreciationProfile);
        assetDepreciationAltProfile_old.data(assetDepreciationAltProfile);
        assetDepreciationExtProfile_old.data(assetDepreciationExtProfile);
        assetDepreciationProfile.data(assetBook.depreciationProfile());
        assetDepreciationProfile_ds.setCurrent();
        assetDepreciationAltProfile.data(assetBook.depreciationAltProfile());
        assetDepreciationAltProfile_ds.setCurrent();
		assetDepreciationExtProfile.data(assetBook.depreciationExtProfile());
        assetDepreciationExtProfile_ds.setCurrent();

        element.updateDesign();
        element.enableApplyDepreciationAdjSameDate();
        element.enableNetBookValueSettings(!assetBook.IsPositiveDepreciation);

        // <GIN>
        if (AssetParameters::checkAssetParameters_IN())
        {
            if (assetBook.AssetGroupDepreciation_IN == NoYes::Yes || AssetGroupTrans_IN::existAssetGroupTrans(assetBook.AssetGroup, assetBook.BookId))
            {
                assetSplit.enabled(false);
            }
            else
            {
                assetSplit.enabled(true);
            }
        }
        // </GIN>

        // <GEEPL>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
        {
            this.active_Pl();
        }
        // </GEEPL>

        // <GJP>
        if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
        {
            document = AssetReductionEntryManager_JP::getAssetReductionEntryDocByAssetBook(
                assetBook.AssetId,
                assetBook.BookId);
            if (document != null)
            {
                allowEditLoc = true;
            }
            ReductionEntryDocumentDate_JP.allowEdit(allowEditLoc);
        }

        if (isJPN)
        {
            element.updateDesignBasedOnValueModel_JP();
        }
        // </GJP>

        return ret;
    }

public void modified_PL()
{
    assetBook.Percentage_PL    = assetDepreciationProfile.Percentage;
    assetBook.PercentageAlt_PL = assetDepreciationAltProfile.Percentage;
    assetBook.PercentageExt_PL = assetDepreciationExtProfile.Percentage;
    assetBook.initLifeTime_PL();
}

public void modified()
{
    // <GCN>
    #isoCountryRegionCodes
    // </GCN>
    super();

    assetBookTable.data(AssetBookTable::find(assetBook.BookId));
    assetBookTable_ds.setCurrent();

    assetDepreciationProfile_ds.executeQuery();
    assetDepreciationAltProfile_ds.executeQuery();
    assetDepreciationExtProfile_ds.executeQuery();

    // <GEEPL>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        this.modified_PL();
        AssetBook_ds.active_Pl();
    }
    // </GEEPL>

    element.updateDesign();

    // <GCN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCN]))
    {
        assetDepreciationProfile_ds.refresh();
        assetDepreciationAltProfile_ds.refresh();
        assetDepreciationExtProfile_ds.refresh();
    }
    // </GCN>

    // <GIN>
    element.setShiftDepreciationEnabled_IN();
    // </GIN>

    // GJP>
    if (isJPN)
    {
        element.updateDesignBasedOnValueModel_JP();
    }
    // </GJP>
}

public void modified()
{
    super();
}

public void modified()
{
    super();

    // <GIN>
    if (AssetParameters::checkAssetParameters_IN())
    {
        if (assetBook.AssetGroupDepreciation_IN == NoYes::Yes)
        {
            assetBook.Depreciation = NoYes::No;
        }
    }
    // </GIN>
}

