public void modified()
{
    super();

    if (AssetBook_ds.validateWrite())
    {
        AssetBook_ds.write();
        assetDepreciationAcceleratedProfile_JP_old.data(assetDepreciationAcceleratedProfile_JP);
        assetDepreciationAcceleratedProfile_JP_ds.research();
        assetDepreciationAcceleratedProfile_JP.data(assetBook.depreciationAcceleratedProfile_JP());
        assetDepreciationAcceleratedProfile_JP_ds.setCurrent();

        element.updateDesign();
    }
}

