public void modified()
{
    super();

    element.updateDesign();
}

public void modified()
{
    super();

    //
    // Write is required since we use a dialog in the table level validateField
    // method for this field. Validate field is executed twice, when we just
    // modify the field (it doens't perform the write), and when you tab off to next field
    // (when it performs the write). We dont want to show the same dialog
    // twice, so we perform a write the first time when modified is called.
    //

    // Because validateWrite can show a reasonCode dialog, we need to make sure that prompt
    // happens here not inside of the tts block in write's super call.
    if (AssetBook_ds.validateWrite())
    {
        AssetBook_ds.write();
    }
}

public void jumpRef()
{
    Args args = new Args();
    args.record(assetBook);
    new MenuFunction(menuitemDisplayStr(AssetDepreciationGroup_W), MenuItemType::Display).run(args);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup       sysTableLookup = SysTableLookup::newParameters(tableNum(RAssetSubGroup), _formControl);
    Query                query = new Query();
    sysTableLookup.addLookupfield(fieldNum(RAssetSubGroup, SubGroupId), true);
    sysTableLookup.addLookupfield(fieldNum(RAssetSubGroup, Name));
    sysTableLookup.addLookupfield(fieldNum(RAssetSubGroup, Factor));
    query.addDataSource(tableNum(RAssetSubGroup)).addRange(fieldNum(RAssetSubGroup, GroupId)).value(queryValue(assetBook.DepreciationGroupId_W));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void init()
{
    QueryBuildDataSource queryBuildProfile;

    super();
    queryBuildProfile = this.queryBuildDataSource();

    if (queryBuildProfile)
    {
        queryBuildProfile.clearDynalinks();
        queryBuildProfile.addDynalink(
            fieldNum(AssetDepreciationProfile, Profile),
            assetBook,
            fieldNum(AssetBook, DepreciationProfile));
    }
}

public void init()
{
    QueryBuildDataSource queryBuildAltProfile;

    super();

    queryBuildAltProfile = this.queryBuildDataSource();

    if (queryBuildAltProfile)
    {
        queryBuildAltProfile.clearDynalinks();
        queryBuildAltProfile.addDynalink(
            fieldNum(AssetDepreciationProfile, Profile),
            assetBook,
            fieldNum(AssetBook, DepreciationAltProfile));
    }
}

        public void init()
        {
