{
    RAssetDeferralsAccounts::lookupBookId(_formControl, assetBook.BookId);
}

public void modified()
{
    super();

    if (AssetBookInUseForPosting::findByAssetIdAndBookId(assetBook.AssetId, assetBook.BookId))
    {
        AssetBookInUseForPosting::deleteAssetBooksInSingleUseForAssetBook(assetBook.AssetId, AssetBook.BookId);
        AssetBookInUseForPosting_ds.reread();
    }

}

void clicked()
{
    Args        args;

    args = new Args();
    args.record(assetBook);

    if (!AssetBook::openAssetGroupTrans_IN(args, assetBook.AssetGroupDepreciation_IN))
    {
        super();
    }
}

public void tabChanged(int _FromTab, int _ToTab)
{
    if (isIsoRU)
    {
        const int tabPageDeferralsNumber = 5;
        if (_toTab == tabPageDeferralsNumber && !assetBook.RecId)
        {
            if  (!assetBook.validateWrite())
            {
                DetailTab.tab(_fromTab);
                return;
            }
        }
    }
    super(_FromTab, _ToTab);
}

[Hookable(false)]
public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        element.setValuesForPositiveDepreciation();
    }

    return ret;
}

