{
    super();
    AssetReductionEntryManager_JP::jumpRefReductionEntry(this, AssetBook.AssetId, AssetBook.BookId);
}

public void lookup()
{
    AssetDocumentTable_JP::lookupDocumentIdByMainType(this, AssetDocumentMainType_JP::ReductionEntry);
}

public boolean modified()
{
    boolean ret;
    boolean                       allowEditLoc = false;
    AssetDocumentId_JP            documentId;

    ret = super();

    if (this.text())
    {
        documentId = this.text();

        if (AssetDocumentTable_JP::findByDocumentId(AssetDocumentMainType_JP::ReductionEntry, documentId))
        {
            allowEditLoc = true;
        }
    }

    ReductionEntryDocumentDate_JP.allowEdit(allowEditLoc);

    AssetBook_ds.refresh();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        element.setValueApplyDepreciationAdjSameDate();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        element.modifyFields_PL();
    }

    return ret;
}

public void enter()
{
    super();
    lastSelection = this.selection();
}

public int selectionChange()
{
    if (!rassetDeferralsAccounts_ds.leaveRecord())
    {
        this.selection(lastSelection);
        return 0;
    }
    int ret = super();
    rassetDeferralsAccounts_ds.executeQuery();
    return ret;
}
