private void addEntityToEntityOptions(List _entityOptions, str _entityName)
{
    OfficeMenuDataEntityOptions entityOption  = OfficeMenuDataEntityOptions::construct(_entityName);
    _entityOptions.addEnd(entityOption);
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List entityOptions = _menuOptions.dataEntityOptions();

    this.addEntityToEntityOptions(entityOptions, tablestr(AssetValueModelSetupDerivedValueModelEntity));
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.dataEntityName())
    {
        case tablestr(AssetValueModelSetupDerivedValueModelEntity):
            context = ExportToExcelDataEntityContext::constructDefault(
                tablestr(AssetValueModelSetupDerivedValueModelEntity));
            break;
    }

    return context;
}

public void activate(boolean _active)
{
    super(_active);

    // <GIN>
    if (isDepreciationIncomeTaxAct)
    {
        element.updateDesignforGroupDepreciation_IN();
    }
    // </GIN>
}

public void enableDisableWorkingDays_IN()
{
    setup_WorkingDays_IN.enabled(assetBookTable.OverRideCalendarDays_IN);
}

// This special lookup form is used for French Derogatory Depreciation.
// It is used to help a user to select a Derogatory Tax Model.
void executeDerogatoryLookup(FormControl _callingControl)
{
    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this' the current Form control.
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(AssetBookTable), _callingControl);

    // Display the Depreciation Book Id in the form.
    sysTableLookup.addLookupfield(fieldNum(AssetBookTable, BookId));
    sysTableLookup.setLabel("@SYS110414");

    // Limit the depreciation books to the given asset id.
    queryBuildDataSource = query.addDataSource(tableNum(AssetBookTable));
    queryBuildRange      = queryBuildDataSource.addRange(fieldNum(AssetBookTable, IsDerogatoryBook_FR));
    queryBuildRange.value('1');

    sysTableLookup.parmQuery(query);

    // Perform lookup
    sysTableLookup.performFormLookup();
}

public void init()
{
    // <GJP>
    isIsoJP = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]);
    // </GJP>

    // <GIN>
    isDepreciationCompaniesAct = AssetParameters::isDepreciationCompaniesActEnable_IN();
    isDepreciationIncomeTaxAct = AssetParameters::isDepreciationIncomeTaxActEnable_IN();
    // </GIN>

    super();

    // <GIN>
    shiftDepreciationButton.visible(isDepreciationCompaniesAct);
    AssetBookTable_ds.object(fieldNum(AssetBookTable, WorkingDays_IN)).visible(isDepreciationCompaniesAct);
    AssetBookTable_ds.object(fieldNum(AssetBookTable, OverRideCalendarDays_IN)).visible(isDepreciationCompaniesAct);
    // </GIN>

    if (Ledger::reportingCurrency() && Ledger::reportingCurrency() != Ledger::accountingCurrency())
    {
        Depreciation_NetBookValueRestReportingCurrency.enabled(true);
    }

    this.enableApplyDepreciationAdjSameDate();
    this.hidePositiveDepreciationForLocalizationScenarios();
    this.enableNetBookValueSettings(!assetBookTable.IsPositiveDepreciation);
}

/// <summary>
/// This method is only used if French Derogatory fields are used.
/// If one field has a value, the other needs to be read only.
/// </summary>
void makeReadOnlyDerogatoryFields()
{
    if (assetBookTable.AssetDerogatoryModel_FR)
    {
        assetBookTable_IsDerogatoryBook1.allowEdit(false);
        assetBookTable_IsDerogatoryBook1.enabled(false);
        assetBookTable_IsDerogatoryBook1.skip(true);
    }
    else
    {
        assetBookTable_IsDerogatoryBook1.allowEdit(true);
        assetBookTable_IsDerogatoryBook1.enabled(true);
        assetBookTable_IsDerogatoryBook1.skip(false);
    }

    if (assetBookTable.IsDerogatoryBook_FR)
    {
        assetBookTable_AssetDerogatoryModel.allowEdit(false);
        assetBookTable_AssetDerogatoryModel.enabled(false);
        assetBookTable_AssetDerogatoryModel.skip(true);
        assetBookTable_AssetDerogatoryModel1.allowEdit(false);
        assetBookTable_AssetDerogatoryModel1.enabled(false);
        assetBookTable_AssetDerogatoryModel1.skip(true);
    }
    else
    {
        assetBookTable_AssetDerogatoryModel.allowEdit(true);
        assetBookTable_AssetDerogatoryModel.enabled(true);
        assetBookTable_AssetDerogatoryModel.skip(false);
        assetBookTable_AssetDerogatoryModel1.allowEdit(true);
        assetBookTable_AssetDerogatoryModel1.enabled(true);
        assetBookTable_AssetDerogatoryModel1.skip(false);
    }
}

/// <summary>
/// Enables/disables or shows/hides fields as window values are changed.
/// </summary>

public void updateDesign()
{
    AssetDepreciationMethod assetDepreciationMethod;

    if (AssetDepreciationProfile::isLowValuePoolDepreciationMethod(assetBookTable.DepreciationProfile))
    {
        depreciation_DepreciationAltProfile.enabled(false);
        depreciation_DepreciationAltProfile.allowEdit(false);
        depreciation_DepreciationAltProfile.skip(true);

        setup_NegativeNetBookValue.enabled(false);
        setup_NegativeNetBookValue.allowEdit(false);
        setup_NegativeNetBookValue.skip(true);

        assetBookTable_AssetDerogatoryModel1.enabled(false);
        assetBookTable_AssetDerogatoryModel1.allowEdit(false);
        assetBookTable_AssetDerogatoryModel1.skip(true);

        assetBookTable_IsDerogatoryBook1.enabled(false);
        assetBookTable_IsDerogatoryBook1.allowEdit(false);
        assetBookTable_IsDerogatoryBook1.skip(true);

        assetBookTable.DepreciationAltProfile = '';
        assetBookTable.NegativeNetBookValue = false;
        assetBookTable.AssetDerogatoryModel_FR = '';
        assetBookTable.IsDerogatoryBook_FR = false;
    }
    else
    {
        // <GEEPL>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
        {
            assetDepreciationMethod = AssetDepreciationProfile::find(assetBookTable.DepreciationProfile).Method;
        }

        if (assetDepreciationMethod == AssetDepreciationMethod::StraightLine_PL
            || assetDepreciationMethod == AssetDepreciationMethod::StraightLinePercentage_PL)
        {
            depreciation_DepreciationAltProfile.enabled(false);
            depreciation_DepreciationAltProfile.allowEdit(false);
            depreciation_DepreciationAltProfile.skip(true);
        }
        else
        {
        // </GEEPL>
            depreciation_DepreciationAltProfile.enabled(true);
            depreciation_DepreciationAltProfile.allowEdit(true);
            depreciation_DepreciationAltProfile.skip(false);
        // <GEEPL>
        }
        // </GEEPL>

        setup_NegativeNetBookValue.enabled(true);
        setup_NegativeNetBookValue.allowEdit(true);
        setup_NegativeNetBookValue.skip(false);

        // Make sure that derogatory fields have proper enable state.
        element.makeReadOnlyDerogatoryFields();
    }

    if (AssetDepreciationProfile::isAutoDeprAllowedForProfile(assetBookTable.DepreciationProfile))
    {
        depreciation_IsTrueUpDepreciation.visible(true);
    }
    else
    {
        depreciation_IsTrueUpDepreciation.visible(false);
    }

    // <GCN>
    depreciationPolicies_CN.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCN])
        && AssetParameters::isDepreciationPolicyEnabled());
    // </GCN>

    // <GIN>
    if (isDepreciationIncomeTaxAct)
    {
        element.updateDesignforGroupDepreciation_IN();
    }

    if (isDepreciationCompaniesAct)
    {
        element.updateDesignForShiftDepreciation_IN();
    }
    // </GIN>

    // <GJP>
    if (isIsoJP)
    {
        if (AssetDepreciationProfile::isDepMethodAllowChangeCalendar_JP(AssetDepreciationProfile::find(assetBookTable.DepreciationProfile).Method))
        {
            TabPageCalendar_JP.visible(true);
            AssetBookTable_ds.object(fieldNum(AssetBookTable, FiscalCalendar)).visible(false);
        }
        else
        {
            TabPageCalendar_JP.visible(false);
            AssetBookTable_ds.object(fieldNum(AssetBookTable, FiscalCalendar)).visible(true);
        }
        if (AssetParameters::isManuallyChangeDepProfileEnable_JP())
        {
            AssetBookTable_ds.object(fieldNum(AssetBookTable, DepreciationProfile)).allowEdit(
                !AssetTrans::existBookTransactions(assetBookTable.BookId));
        }
    }
    // </GJP>
}

private void enableApplyDepreciationAdjSameDate()
{
    if (assetBookTable.IsTrueUpDepreciation)
    {
        Depreciation_IsPreventDepreciationAdj.allowEdit(true);
    }
    else
    {
        Depreciation_IsPreventDepreciationAdj.allowEdit(false);
    }
}

private void setValueApplyDepreciationAdjSameDate()
{
    if (assetBookTable.IsTrueUpDepreciation)
    {
        Depreciation_IsPreventDepreciationAdj.allowEdit(true);
    }
    else
    {
        AssetBookTable_ds.object(fieldNum(AssetBookTable, IsAllowDepreciationAdjSameDate)).setValue(NoYes::No);
        Depreciation_IsPreventDepreciationAdj.allowEdit(false);
    }
}

private void setValuesForPositiveDepreciation()
{
    if (assetBookTable.IsPositiveDepreciation)
    {
        AssetBookTable_ds.object(fieldNum(AssetBookTable, NegativeNetBookValue)).setValue(NoYes::Yes);
        AssetBookTable_ds.object(fieldNum(AssetBookTable, ExceedingNetBookValue)).setValue(NoYes::Yes);
    }

    this.enableNetBookValueSettings(!assetBookTable.IsPositiveDepreciation);
}

private void enableNetBookValueSettings(boolean _isEnabled = true)
{
    AssetBookTable_ds.object(fieldNum(AssetBookTable, NegativeNetBookValue)).enabled(_isEnabled);
    AssetBookTable_ds.object(fieldNum(AssetBookTable, ExceedingNetBookValue)).enabled(_isEnabled);
}

private void hidePositiveDepreciationForLocalizationScenarios()
{
    if (isIsoJP
        || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL])
        || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR]))
    {
        AssetBookTable_ds.object(fieldNum(AssetBookTable, IsPositiveDepreciation)).setValue(NoYes::No);
        AssetBookTable_ds.object(fieldNum(AssetBookTable, IsPositiveDepreciation)).visible(false);
    }
}

/// <summary>
/// Updates the UI of about calendar schedule.
/// </summary>
public void updateDesignForCalendarSchedule_JP()
{
    boolean     allowEditCalendarSchedule;

    allowEditCalendarSchedule = (AssetBookTable.LinkedCurrentLayerAssetBookTable_JP == '');

    AssetDepBookCalendarSchedule_JP_ds.allowCreate(allowEditCalendarSchedule);
    AssetDepBookCalendarSchedule_JP_ds.allowEdit(allowEditCalendarSchedule);
    AssetDepBookCalendarSchedule_JP_ds.allowDelete(allowEditCalendarSchedule);

    AssetBookTable_ds.object(fieldNum(AssetBookTable, FiscalCalendar)).allowEdit(allowEditCalendarSchedule);
}

public void updateDesignforGroupDepreciation_IN()
{
    AssetGroupBookSetup    assetGroupBookSetupLoc;

    if (assetGroupBookSetupLoc.checkDepreciationValueModel_IN(assetBookTable.BookId))
    {
        assetBookTable.Depreciation = NoYes::No;
        AssetBookTable_ds.object(fieldNum(AssetBookTable,Depreciation)).enabled(false);
    }
    else
    {
        AssetBookTable_ds.object(fieldNum(AssetBookTable,Depreciation)).enabled(true);
    }
}

/// <summary>
/// Updates the UI about referenced value model.
/// </summary>
public void updateDesignForReferencedValueModel_JP()
{
    boolean     isReferencedValueModelEditable;

    isReferencedValueModelEditable = (AssetBookTable.CurrentOperationsTax == CurrentOperationsTax::Tax);

    AssetBookTable_ds.object(fieldNum(AssetBookTable, LinkedCurrentLayerAssetBookTable_JP)).allowEdit(isReferencedValueModelEditable);
}

public void updateDesignForShiftDepreciation_IN()
{
    boolean                     isEnabled;
    FiscalCalendar              fiscalCalendar;
    FiscalCalendar_IN           fiscalCalendar_IN;
    AssetDepreciationProfile    assetDepreciationProfile;

    fiscalCalendar          = FiscalCalendar::find(str2int64(setup_FiscalCalendarID.valueStr()));
    fiscalCalendar_IN       = FiscalCalendar_IN::findByFiscalCalendar(fiscalCalendar.RecId);

    if (assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Current)
    {
        assetDepreciationProfile    = AssetDepreciationProfile::find(assetBookTable.DepreciationProfile);
        isEnabled = (assetDepreciationProfile.isShiftDepreciationMethod_IN()
                        && assetDepreciationProfile.ShiftDepreciation_IN);
    }

    shiftDepreciationButton.enabled(isEnabled);
    setup_OverRideCalendarDays_IN.enabled(isEnabled);
    setup_WorkingDays_IN.enabled(isEnabled
        && assetBookTable.OverRideCalendarDays_IN);
}

private void updateDesignForTaxCategoryTogether_LV()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLV]))
    {
        if (assetBookTable.CurrentOperationsTax == CurrentOperationsTax::Tax)
        {
            depreciation_TaxCategoryTogether_LV.enabled(true);
        }
        else
        {
            assetBookTable.TaxCategoryTogether_LV = NoYes::No;
            depreciation_TaxCategoryTogether_LV.enabled(false);
        }
    }
}

public void selectionChanged()
{
    super();

    element.updateDesignForTaxCategoryTogether_LV();
}

public int active()
{
    int ret;

    ret = super();
    element.updateDesign();
    element.enableApplyDepreciationAdjSameDate();
    element.enableNetBookValueSettings(!assetBookTable.IsPositiveDepreciation);

    // <GJP>
    if (isIsoJP)
    {
        element.updateDesignForCalendarSchedule_JP();
        element.updateDesignForReferencedValueModel_JP();
    }
    // </GJP>

    return ret;
}

public void write()
{
    super();

    if (Depreciation_NetBookValueRestReportingCurrency.enabled()
        && Depreciation_NetBookValueRestReportingCurrency.realValue() == 0
        && Depreciation_NetBookValueRest.realValue() != 0)
    {
        warning("@FixedAssets:FA_Form_NetBookValueReportingCurrencyValidation");
    }
}

