}

public void modified()
{
    super();

    // <GIN>
    if (isDepreciationCompaniesAct || isDepreciationIncomeTaxAct)
    {
        if (assetBookTable.CurrentOperationsTax != CurrentOperationsTax::Tax)
        {
            assetBookTable.updateValue_IN();
        }
        element.updateDesignForShiftDepreciation_IN();

        AssetBookTable_ds.refresh();
    }
    // </GIN>

    // <GJP>
    if (isIsoJP)
    {
        element.updateDesignForReferencedValueModel_JP();
    }
    // </GJP>
}

