// If the asset is used for Derogatory Depreciation, make sure that asset transaction types used for
// synchronizing of the main value model and the derogatory tax model are not deleted.
public void delete()
{
    if (assetBookTableDerived.BookId
     && assetBookTable.AssetDerogatoryModel_FR
     && assetBookTable.AssetDerogatoryModel_FR == assetBookTableDerived.BookIdDerived
     && (assetBookTableDerived.AssetTransType == AssetTransType::Acquisition
     || assetBookTableDerived.AssetTransType == AssetTransType::AcquisitionAdj
     || assetBookTableDerived.AssetTransType == AssetTransType::WriteDownAdj
     || assetBookTableDerived.AssetTransType == AssetTransType::WriteUpAdj
     || assetBookTableDerived.AssetTransType == AssetTransType::DisposalSale
     || assetBookTableDerived.AssetTransType == AssetTransType::DisposalScrap))
    {
        //Derived value model for '%1' cannot be deleted as it is used for derogatory depreciation.
        throw error(strFmt("@FixedAssets:FA_MessageError_CannotDeleteDerivedBookUsedForDerogatoryDepreciation",assetBookTableDerived.AssetTransType));
    }
    super();
}

public void delete()
{
    super();

    AssetBookTable_ds.reread();
    AssetBookTable_ds.refresh();
}

public void write()
{
    super();

    AssetBookTable_ds.reread();
    AssetBookTable_ds.refresh();
}

public void init()
{
    QueryBuildDataSource    qbds;

    if (isIsoJP)
    {
        this.linkType(FormLinkType::Delayed);
    }

    super();

    if (isIsoJP)
    {
        qbds = this.query().dataSourceTable(tableNum(AssetDepBookCalendarSchedule_JP));
        if (qbds)
        {
            qbds.addRange(fieldNum(AssetDepBookCalendarSchedule_JP, SourceTableId)).value(SysQuery::value(tableNum(AssetBookTable)));
            qbds.addDynalink(
                fieldNum(AssetDepBookCalendarSchedule_JP, SourceRecId),
                AssetBookTable,
                fieldNum(AssetBookTable, RecId));
        }
    }
}

