[Form]
public class AssetDepreciationProfile extends FormRun
{
    // <GEEU>
    #isoCountryRegionCodes
    // </GEEU>
    // <GIN>
    boolean     isDepCompanyActEnabled;
        boolean     isDepIncomeTaxActEnabled;  // </GIN>

}

public boolean canClose()
{
    boolean ret;
    AssetDepreciationProfileSpec assetDepreciationProfileSpecLocal;

    ret = super();

    if  (assetDepreciationProfile.Method == AssetDepreciationMethod::MultiplicationFactor_HU
        || assetDepreciationProfile.Method == AssetDepreciationMethod::Manual)
    {
        select sum(Percentage) from assetDepreciationProfileSpecLocal
            where assetDepreciationProfileSpecLocal.Profile == assetDepreciationProfile.Profile;

        // <GEEHU>
        if (assetDepreciationProfile.Method == AssetDepreciationMethod::MultiplicationFactor_HU
            && assetDepreciationProfileSpecLocal.Percentage != 100
            && assetDepreciationProfile.AssetEndDependOnServiceLife == NoYes::No)
        {
            error(strFmt("@SYS67593", assetDepreciationProfileSpecLocal.Percentage));
            ret = false;
        }
        // </GEEHU>
        else if (assetDepreciationProfileSpecLocal.Percentage != 100
            && assetDepreciationProfile.AssetEndDependOnServiceLife == NoYes::No)
        {
            warning(strfmt("@SYS67593",assetDepreciationProfileSpecLocal.Percentage));
        }
    }
    return ret;
}

public void init()
{
    // <GIN>
    isDepCompanyActEnabled = AssetParameters::isDepreciationCompaniesActEnable_IN();
    isDepIncomeTaxActEnabled = AssetParameters::isDepreciationIncomeTaxActEnable_IN();
    // </GIN>

    super();

    // <GIN>
    if (!isDepCompanyActEnabled)
    {
        assetShiftDepreciationProfile.visible(false);
        assetDepreciationProfile_DS.object(fieldNum(AssetDepreciationProfile,ShiftDepreciation_IN)).visible(false);
    }
    // </GIN>
}

void setPeriodFrequency()
{
    if (assetDepreciationProfile.Method == AssetDepreciationMethod::RBSL)
    {
        switch (assetDepreciationProfile.DepreciationYear)
        {
            case AssetDepreciationYear::Calendar:
                calendar.visible(true);
                calendar.enabled(false);
                fiscal.visible(false);
                fiscal.enabled(false);
                assetDepreciationProfile.Accrual = AssetAccrual::Monthly;
                break;
            case AssetDepreciationYear::Fiscal:
                calendar.visible(false);
                calendar.enabled(false);
                fiscal.visible(true);
                fiscal.enabled(false);
                assetDepreciationProfile.Accrual = AssetAccrual::FiscalPeriod;
                break;
        }
    }
    else
    {
        switch (assetDepreciationProfile.DepreciationYear)
        {
            case AssetDepreciationYear::Calendar:
                calendar.visible(true);
                calendar.enabled(true);
                fiscal.visible(false);
                fiscal.enabled(false);
                break;
            case AssetDepreciationYear::Fiscal:
                calendar.visible(false);
                calendar.enabled(false);
                fiscal.visible(true);
                fiscal.enabled(true);
                break;
        }
    }
}

    void updateDesign()
