            AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, AssetDepRateSchedule_JP)).visible(false);
            AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepAccountingMethod_JP)).visible(false);
