            AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SpecialDepRate_JP)).visible(false);
            AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SmoothDepForTheYearOver95_JP)).visible(false);
            AssetDepreciationProfile_ds.object(fieldNum(AssetDepreciationProfile, SmoothDepForTheYearOver95_JP)).enabled(false);
            AssetDepreciationProfile_SpecialDepAccountingMethod_JP.allowEdit(true);
        }
