                    switchShift = (assetDepreciationProfile.DepreciationYear == AssetDepreciationYear::Fiscal);

                    if (switchShift)
                    {
                        assetDepreciationProfile_DS.object(fieldNum(AssetDepreciationProfile,ShiftDepreciation_IN)).allowEdit(true);
                    }
                    else
                    {
                        AssetDepreciationProfile.ShiftDepreciation_IN = NoYes::No;
