            case AssetDepreciationMethod::ReducingBalance125:
            case AssetDepreciationMethod::ReducingBalance150:
            case AssetDepreciationMethod::ReducingBalance175:
            case AssetDepreciationMethod::ReducingBalance200:
                interval.visible(false);
                depreciationYear.visible(true);
                endDependOnServiceLife.visible(true);
                element.setPeriodFrequency();
                calculationBase.visible(false);
                assetDepreciationProfile.Interval = AssetDepreciationInterval::Yearly;
                lvpLowValueCost_AU.visible(false);
                lvpLowValueCost_AU.mandatory(false);
                lvpPercentage_AU.visible(false);
                lvpPercentage_AU.mandatory(false);
                lvpFirstYearPercentage_AU.visible(false);
                lvpFirstYearPercentage_AU.mandatory(false);
                break;

            case AssetDepreciationMethod::Manual:
                switchSchedule = true;
                interval.visible(false);
                depreciationYear.visible(true);
                calendar.visible(false);
                fiscal.visible(false);
                element.setPeriodFrequency();
                endDependOnServiceLife.visible(true);
                calculationBase.visible(true);

                // <Remark> Calculation base can only be selected for yearly interval </Remark>
                if (assetDepreciationProfile.DepreciationYear == AssetDepreciationYear::Calendar && assetDepreciationProfile.Accrual == AssetAccrual::Yearly)
                {
                    calculationBase.enabled(true);
                }
                else
                {
                    assetDepreciationProfile.CalculationBase = AssetDepreciationCalculationBase::Days;
                    calculationBase.selection(AssetDepreciationCalculationBase::Days);
                    calculationBase.enabled(false);
                }

                lvpLowValueCost_AU.visible(false);
                lvpLowValueCost_AU.mandatory(false);
                lvpPercentage_AU.visible(false);
                lvpPercentage_AU.mandatory(false);
                lvpFirstYearPercentage_AU.visible(false);
                lvpFirstYearPercentage_AU.mandatory(false);

                break;

            case AssetDepreciationMethod::Factor:
            // <GEEU>
            case AssetDepreciationMethod::Factor_HU:
            // </GEEU>
                switchPercentage = true;
                percLabel = "@SYS1187";
                interval.visible(true);
                depreciationYear.visible(false);
                calendar.visible(false);
                fiscal.visible(false);
                endDependOnServiceLife.visible(false);
                calculationBase.visible(false);
                lvpLowValueCost_AU.visible(false);
                lvpLowValueCost_AU.mandatory(false);
                lvpPercentage_AU.visible(false);
                lvpPercentage_AU.mandatory(false);
                lvpFirstYearPercentage_AU.visible(false);
                lvpFirstYearPercentage_AU.mandatory(false);
                break;

            case AssetDepreciationMethod::Consumption:
                interval.visible(false);
                depreciationYear.visible(false);
                calendar.visible(false);
                fiscal.visible(false);
                endDependOnServiceLife.visible(false);
                calculationBase.visible(false);
                assetDepreciationProfile.Interval = AssetDepreciationInterval::Yearly;
                lvpLowValueCost_AU.visible(false);
                lvpLowValueCost_AU.mandatory(false);
                lvpPercentage_AU.visible(false);
                lvpPercentage_AU.mandatory(false);
                lvpFirstYearPercentage_AU.visible(false);
                lvpFirstYearPercentage_AU.mandatory(false);
