            case AssetDepreciationMethod::LowValuePool:
                lvpLowValueCost_AU.visible(true);
                lvpLowValueCost_AU.mandatory(true);
                lvpPercentage_AU.visible(true);
                lvpPercentage_AU.mandatory(true);
                lvpFirstYearPercentage_AU.visible(true);
                lvpFirstYearPercentage_AU.mandatory(true);
                interval.visible(false);
                depreciationYear.visible(true);
                endDependOnServiceLife.visible(false);
                element.setPeriodFrequency();
