                interval.visible(false);
                calendar.visible(assetDepreciationProfile.DepreciationYear == AssetDepreciationYear::Calendar);
                fiscal.visible(assetDepreciationProfile.DepreciationYear == AssetDepreciationYear::Fiscal);
                fiscal.enabled(true);
                depreciationYear.visible(true);
                endDependOnServiceLife.visible(false);
                calculationBase.visible(false);
                break;
            // </GEEU>
            // <GIN>
            case AssetDepreciationMethod::StraightLinePercentage_IN:
                if (isDepCompanyActEnabled)
                {
                    if (assetDepreciationProfile.DepreciationYear == AssetDepreciationYear::Fiscal)
                    {
                        switchShift = true;
                    }
                    depreciationYear.enabled(false);
